; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

RDBAT:					; ...
		call	GETBATBYT
		inc	cx
		call	testkanj
		jz	short RDBAT1
		cmp	cx, 127
		jnb	short TOOLONG
		stosb
		call	GETBATBYT
		inc	cx
		jmp	short SAVBATBYT
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

RDBAT1:					; ...
		cmp	cx, 128
		jnb	short TOOLONG
		cmp	al, '%'
		jz	short NEEDPARM

SAVBATBYT:				; ...
		stosb
		cmp	al, 0Dh
		jnz	short RDBAT

FOUND_EOL:				; ...
		sub	di, (offset COMBUF+3)
		mov	ax, di
		mov	es:COMBUF+1, al
		call	GETBATBYT
		call	BATCLOSE
		cmp	ds:Suppress, 0
		jz	short RESET
		test	ds:EchoFlag, 1
		jnz	short TRY_NEXTFLAG

RESET:					; ...
		push	cs
		pop	ds
		assume ds:TRANGROUP
		jz	short READBAT_RETN

TRY_NEXTFLAG:				; ...
		cmp	ds:NullFlag, 1
		jz	short NO_CRLF_PRINT
		call	CRLF2

NO_CRLF_PRINT:				; ...
		call	PRINT_PROMPT
		push	cs
		pop	ds
		mov	dx, (offset COMBUF+2)
		call	CRPRINT
		call	CRLF2
		retn
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

TOOLONG:				; ...
		cmp	al, 0Dh
		jz	short LTLCONT
		call	SKIPTOEOL

LTLCONT:				; ...
		stosb
		jmp	short FOUND_EOL
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

NEEDPARM:				; ...
		call	GETBATBYT
		cmp	al, '%'
		jz	short SAVBATBYT
		cmp	al, 0Dh
		jz	short SAVBATBYT

PAROK:
		sub	al, '0'
		jb	short NEEDENV
		cmp	al, 9
		ja	short NEEDENV
		cbw
		mov	bx, ax
		shl	bx, 1
		push	es
		mov	es, ds:Batch
		mov	si, es:[bx+0Ch]	; [es:bx+BATCHSEGMENT.BatParm]
		pop	es
		cmp	si, 0FFFFh	; -1
		jnz	short YES_THERE_IS
		jmp	RDBAT
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

YES_THERE_IS:				; ...
		push	ds
		mov	ds, ds:Batch
		assume ds:nothing
		dec	cx

COPYPARM:				; ...
		lodsb
		cmp	al, 0Dh
		jz	short ENDPARAM
		inc	cx
		cmp	cx, 128		; COMBUFLEN
		jnb	short LINETOOL
		stosb
		jmp	short COPYPARM
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

LINETOOL:				; ...
		xor	al, al
		pop	ds
		jmp	short TOOLONG
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

ENDPARAM:				; ...
		pop	ds
		jmp	RDBAT
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

NEEDENV:				; ...
		dec	cx
		push	ds
		push	di
		mov	di, offset ID
		add	al, '0'
		stosb

GETENV1:				; ...
		call	GETBATBYT
		stosb
		cmp	al, 0Dh
		jnz	short GETENV15
		mov	byte ptr es:[di-1], 0
		mov	si, offset ID
		pop	di
		push	cs
		pop	ds
		assume ds:TRANGROUP
		call	STRCPY
		jb	short LINETOOL
		pop	ds
		assume ds:nothing
		jmp	SAVBATBYT
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

GETENV15:				; ...
		cmp	al, '%'
		jnz	short GETENV1
		mov	al, '='
		mov	es:[di-1], al
		mov	si, offset ID
		push	cs
		pop	ds
		assume ds:TRANGROUP
		push	cx
		call	find_name_in_environment
		pop	cx
		push	es
		pop	ds
		assume ds:nothing
		push	cs
		pop	es
		assume es:TRANGROUP
		mov	si, di
		pop	di
		jb	short GETENV6
		call	STRCPY

GETENV6:				; ...
		pop	ds
		jmp	RDBAT
READBAT		endp


; ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ S U B	R O U T	I N E ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ


SKIPTOEOL	proc near		; ...
		test	ds:Batch, 0FFFFh
		jnz	short SKIPTOEOL1
		retn
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

SKIPTOEOL1:				; ...
		call	GETBATBYT
		cmp	al, 0Dh
		jnz	short SKIPTOEOL
		retn
SKIPTOEOL	endp


; ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ S U B	R O U T	I N E ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ


FREE_TPA	proc near		; ...
		push	es
		mov	es, ds:RESSEG
		assume es:nothing
		mov	es, es:Res_Tpa
		mov	ah, 49h
		int	21h		; DOS -	2+ - FREE MEMORY
					; ES = segment address of area to be freed
		pop	es
		retn
FREE_TPA	endp


; ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ S U B	R O U T	I N E ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ


ALLOC_TPA	proc near		; ...
		push	es
		mov	es, ds:RESSEG
		mov	bx, 0FFFFh
		mov	ah, 48h
		int	21h		; DOS -	2+ - ALLOCATE MEMORY
					; BX = number of 16-byte paragraphs desired
		push	bx
		mov	ah, 48h
		int	21h		; DOS -	2+ - ALLOCATE MEMORY
					; BX = number of 16-byte paragraphs desired
		pop	bx
		mov	es:Res_Tpa, ax
		mov	ds:TRAN_TPA, ax
		mov	dx, ax
		and	ax, 0FFFh
		jnz	short CALC_TPA
		mov	ax, dx
		and	ax, 0F000h
		jnz	short NOROUND

CALC_TPA:				; ...
		mov	ax, dx
		and	ax, 0F000h
		add	ax, 1000h
		jb	short NOROUND
		mov	dx, es:Res_Tpa
		add	dx, bx
		cmp	dx, ax
		jb	short NOROUND
		mov	bx, cs
		cmp	bx, ax
		jb	short NOROUND
		sub	bx, ax
		cmp	bx, 1000h
		jnb	short ROUNDDONE

NOROUND:				; ...
		mov	ax, es:Res_Tpa

ROUNDDONE:				; ...
		mov	es:LTpa, ax
		mov	ds:TPA,	ax
		mov	bx, ax
		mov	ax, cs
		sub	ax, bx
		push	bx
		mov	bx, 16
		mul	bx
		pop	bx
		or	dx, dx
		jz	short SAVSIZ2
		mov	ax, 0FFFFh	; -1

SAVSIZ2:				; ...
		cmp	ax, 512
		jbe	short GOTSIZE2
		and	ax, 0FE00h	; NOT 511 = NOT	1FFh

GOTSIZE2:				; ...
		mov	ds:BYTCNT, ax
		pop	es
		retn
ALLOC_TPA	endp

; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ
; START	OF FUNCTION CHUNK FOR PROMPTBAT

BATCOM:					; ...
		mov	es, ds:RESSEG
		cmp	es:Call_Batch_Flag, 1
		jz	short skip_ioset
		call	IOSET

skip_ioset:				; ...
		call	FREE_TPA
		cmp	es:Call_Batch_Flag, 1
		jz	short GETECHO
		call	FOROFF

GETECHO:				; ...
		call	PipeOff
		mov	al, es:EchoFlag
		and	al, 1
		push	ax
		xor	ax, ax
		test	es:Batch, 0FFFFh ; -1
		jz	short LEAVEBAT
		mov	ax, es:Batch
		cmp	es:Call_Batch_Flag, 1
		jz	short LEAVEBAT
		push	es
		mov	es, ax
		mov	ax, es:3	; [es:BATCHSEGMENT.BatLast]
		pop	es

LEAVEBAT:				; ...
		push	ax
		cmp	es:Call_Batch_Flag, 1
		jz	short STARTBAT
		call	BATCHOFF

STARTBAT:				; ...
		mov	es:Call_Batch_Flag, 0
		mov	si, offset EXECPATH
		mov	ax, 0B711h
		int	2Fh		; - Multiplex -	DOS 4.0	APPEND - SET RETURN FOUND NAME STATE
		mov	ah, 4Eh
		mov	dx, si
		mov	cx, 13h
		int	21h		; DOS -	2+ - FIND FIRST	ASCIZ (FINDFIRST)
					; CX = search attributes
					; DS:DX	-> ASCIZ filespec
					; (drive, path,	and wildcards allowed)
		call	dstrlen
		mov	bx, cx
		add	bx, 191		; 15+BATCHSEGMENT.SIZE+COMBUFLEN+15
		push	cx
		mov	cl, 4
		shr	bx, cl
		push	bx
		mov	ah, 48h
		int	21h		; DOS -	2+ - ALLOCATE MEMORY
					; BX = number of 16-byte paragraphs desired
		pop	bx
		jb	short MEM_ERROR
		push	ax
		add	ax, bx
		add	ax, 20h
		mov	bx, cs
		mov	dx, 98C5h	; mov dx,offset	TRANGROUP:TranSpaceEnd
		add	dx, 15
		shr	dx, cl
		add	dx, bx
		cmp	ax, bx
		jb	short ENOUGH_MEM
		cmp	ax, dx
		ja	short ENOUGH_MEM
		pop	ax
		push	es
		mov	es, ax
		mov	ax, 4900h
		int	21h		; DOS -	2+ - FREE MEMORY
					; ES = segment address of area to be freed
		pop	es

MEM_ERROR:				; ...
		jmp	NO_MEMORY
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

ENOUGH_MEM:				; ...
		pop	ax
		mov	es:Batch, ax
		call	ALLOC_TPA
		pop	dx
		pop	ax
		inc	es:Nest
		push	es
		mov	es, es:Batch
		mov	byte ptr es:0, 0 ; [es:BATCHSEGMENT.BatType]
		mov	es:3, ax	; [es:BATCHSEGMENT.BatLast]
		push	ds
		mov	ds, ds:RESSEG
		xor	ax, ax
		mov	bl, ds:ForFlag
		mov	es:7, bl	; [es:BATCHSEGMENT.BatForFlag]
		test	bl, 0FFh	; -1
		jz	short FOR_NOT_ON
		mov	ax, ds:ForPtr
		mov	ds:ForFlag, 0

FOR_NOT_ON:				; ...
		mov	es:5, ax	; [es:BATCHSEGMENT.BatForPtr]
		xor	ax, ax
		mov	ds:ForPtr, ax
		mov	bl, ds:EchoFlag
		pop	ds
		mov	es:1, bl	; [es:BATCHSEGMENT.BatEchoFlag]
		mov	byte ptr es:2, 0 ; [es:BATCHSEGMENT.BatchEOF]
		mov	es:8, ax	; [es:BATCHSEGMENT.BatSeek]
		mov	es:0Ah,	ax	; [es:BATCHSEGMENT.BatSeek+2]
		dec	ax
		mov	di, 0Ch		; BATCHSEGMENT.BatParm
		mov	bx, di
		mov	cx, 10
		rep stosw
		mov	cx, dx
		rep movsb
		mov	si, (offset COMBUF+2)
		mov	cx, 10

EACHPARM:				; ...
		call	scanoff
		cmp	al, 0Dh
		jz	short HAVPARM
		jcxz	short MOVPARM
		mov	es:[bx], di
		add	bx, 2

MOVPARM:				; ...
		lodsb
		call	DELIM
		jz	short ENDPARM
		stosb
		cmp	al, 0Dh
		jz	short HAVPARM
		jmp	short MOVPARM
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

ENDPARM:				; ...
		mov	al, 0Dh
		stosb
		jcxz	short EACHPARM
		dec	cx
		jmp	short EACHPARM
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

HAVPARM:				; ...
		xor	al, al
		stosb
		lea	bx, [di+15]
		mov	cl, 4
		shr	bx, cl
		mov	ah, 4Ah
		int	21h		; DOS -	2+ - ADJUST MEMORY BLOCK SIZE (SETBLOCK)
					; ES = segment address of block	to change
					; BX = new size	in paragraphs
		pop	es
		push	es
		pop	ds
		cmp	ds:SingleCom, 0FFFFh
		jnz	short NOBATSING
		mov	ds:SingleCom, 0FFF0h

NOBATSING:				; ...
		pop	ax
		mov	ds:EchoFlag, al
		jmp	TCOMMAND
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

NO_MEMORY:				; ...
		pop	dx
		pop	ax
		pop	ax
		call	ALLOC_TPA
		mov	ds:msg_disp_class, 1
		mov	dx, offset extend_buf_ptr
		mov	ds:extend_buf_ptr, 8 ; ERROR_NOT_ENOUGH_MEMORY
		jmp	cerror
; END OF FUNCTION CHUNK	FOR PROMPTBAT

; ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ S U B	R O U T	I N E ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ


BATCHOFF	proc near		; ...
		push	ax
		push	es
		push	ds
		push	bx
		mov	es, cs:RESSEG
		mov	ds, cs:RESSEG
		mov	ax, ds:Batch
		or	ax, ax
		jz	short NOTFREE
		push	es
		mov	es, ax
		test	ds:EchoFlag, 1
		jnz	short ECHO_LAST_LINE
		mov	ds:Suppress, 0

ECHO_LAST_LINE:				; ...
		mov	bl, es:1
		mov	ds:EchoFlag, bl
		mov	bx, es:5
		mov	ds:ForPtr, bx
		mov	bl, es:7
		mov	ds:ForFlag, bl
		mov	bx, es:3
		mov	ah, 49h
		int	21h		; DOS -	2+ - FREE MEMORY
					; ES = segment address of area to be freed
		pop	es
		mov	ds:Next_Batch, bx
		dec	es:Nest
		xor	ax, ax
		mov	ds:Batch, ax

NOTFREE:				; ...
		pop	bx
		pop	ds
		pop	es
		pop	ax
		retn
BATCHOFF	endp


; ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ S U B	R O U T	I N E ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ


STRCPY		proc near		; ...
		push	ax

ccycle:					; ...
		lodsb
		inc	cx
		cmp	cx, 128		; COMBUFLEN
		jb	short ccopy
		stc
		jmp	short ccend
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

ccopy:					; ...
		stosb
		or	al, al
		jnz	short ccycle

ccend:					; ...
		dec	cx
		dec	di
		pop	ax
		retn
STRCPY		endp


; ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ S U B	R O U T	I N E ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ


GETBATBYT	proc near		; ...
		push	bx
		push	cx
		push	ds
		test	ds:Batch_Abort,	0FFh ; -1
		jz	short getbatbyt1
		jmp	BATEOF
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

getbatbyt1:				; ...
		test	ds:Batch, 0FFFFh
		jnz	short getbatbyt2
		jmp	BATEOF
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

getbatbyt2:				; ...
		push	es
		mov	es, ds:Batch
		cmp	byte ptr es:2, 0 ; [es:BATCHSEGMENT.BatchEOF]
		jz	short not_eof
		jmp	At_EOF
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

not_eof:				; ...
		add	word ptr es:8, 1 ; [es:BATCHSEGMENT.BatSeek]
		adc	word ptr es:10,	0 ; [es:BATCHSEGMENT.BatSeek+2]
		pop	es
		mov	ax, cs
		mov	ds, ax
		assume ds:TRANGROUP
		mov	bx, BATBUFPOS
		cmp	bx, 0FFFFh	; -1
		jnz	short UNBUF
		mov	dx, offset BATBUF
		mov	cx, BATBUFLEN
		mov	bx, BATHAND
		mov	ah, 3Fh
		int	21h		; DOS -	2+ - READ FROM FILE WITH HANDLE
					; BX = file handle, CX = number	of bytes to read
					; DS:DX	-> buffer
		jnb	short bat_read_ok
		call	get_ext_error_number
		push	ds
		mov	ds, RESSEG
		assume ds:nothing
		mov	dx, ax
		call	output_batch_name
		pop	ds
		call	std_eprintf
		mov	ds:COMBUF+2, 0Dh
		mov	ds:COMBUF+3, 0
		mov	ds, ds:RESSEG
		jmp	short BATEOF
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

bat_read_ok:				; ...
		mov	cx, ax
		jcxz	short BATEOFDS
		mov	ds:BATBUFEND, cx
		xor	bx, bx
		mov	ds:BATBUFPOS, bx

UNBUF:					; ...
		mov	al, ds:BATBUF[bx]
		inc	bx
		cmp	bx, ds:BATBUFEND
		jb	short SETBUFPOS
		mov	bx, 0FFFFh	; -1

SETBUFPOS:				; ...
		mov	ds:BATBUFPOS, bx
		cmp	al, 1Ah
		jnz	short GETBYTEDONE

BATEOFDS:				; ...
		push	es
		mov	es, ds:RESSEG
		mov	es, es:Batch
		cmp	byte ptr es:2, 0
		jnz	short crpresent
		inc	byte ptr es:2	; [es:BATCHSEGMENT.BatchEOF]
		mov	bx, ds:BATBUFEND
		cmp	byte ptr ds:(BATBUFPOS+1)[bx], 0Ah ; [bx+BATBUF-1]
		jz	short crpresent
		add	byte ptr es:2, 3

crpresent:				; ...
		mov	ds, ds:RESSEG

At_EOF:					; ...
		shr	byte ptr es:2, 1
		jz	short turn_off
		cmp	byte ptr es:2, 1
		jz	short ret_lf
		mov	al, 0Dh
		pop	es
		jmp	short GETBYTEDONE
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

ret_lf:					; ...
		mov	al, 0Ah
		pop	es
		jmp	short GETBYTEDONE
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

turn_off:				; ...
		pop	es

BATEOF:					; ...
		call	BATCHOFF
		call	BATCLOSE
		mov	al, 0Dh
		test	ds:Batch_Abort,	0FFh
		mov	ds:Batch_Abort,	0
		jz	short CONT_GET_BYT
		mov	di, (offset COMBUF+2)
		xor	cx, cx
		jmp	short GETBYTEDONE
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

CONT_GET_BYT:				; ...
		cmp	ds:SingleCom, 0FFF0h
		jnz	short GETBYTEDONE
		cmp	ds:Nest, 0
		jnz	short GETBYTEDONE
		mov	ds:SingleCom, 0FFFFh

GETBYTEDONE:				; ...
		pop	ds
		pop	cx
		pop	bx
		retn
GETBATBYT	endp

; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

IFERRORP:				; ...
		pop	ax

IFFORERROR:				; ...
		mov	dx, offset SYNTMES_PTR
		jmp	cerror
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

_$IF:					; ...
		push	ds
		mov	ds, ds:RESSEG
		cmp	ds:PipeFiles, 0
		jz	short IFNoPipe
		call	PIPEDEL

IFNoPipe:				; ...
		pop	ds
		mov	ds:IFNOTFLAG, 0
		mov	ds:IF_NOT_COUNT, 0
		mov	si, 81h

IFREENT:				; ...
		call	scanoff
		cmp	al, 0Dh
		jz	short IFFORERROR
		mov	bp, si
		mov	di, offset IFTAB
		mov	ch, 0

IFINDCOM:				; ...
		mov	si, bp
		mov	cl, [di]
		inc	di
		jcxz	short IFSTRING
		jmp	short FIRSTCOMP
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

IFCOMP:					; ...
		jnz	short IF_DIF

FIRSTCOMP:				; ...
		lodsb
		mov	ah, es:[di]
		inc	di
		cmp	al, ah
		jz	short IFLP
		or	ah, 20h
		cmp	al, ah

IFLP:					; ...
		loop	IFCOMP

IF_DIF:					; ...
		lahf
		add	di, cx
		mov	bx, [di]
		inc	di
		inc	di
		sahf
		jnz	short IFINDCOM
		lodsb
		cmp	al, 0Dh

IFERRJ:					; ...
		jz	short IFFORERROR ; IFERROR
		call	DELIM
		jnz	short IFINDCOM
		call	scanoff
		jmp	bx
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

IFNOT:					; ...
		not	ds:IFNOTFLAG
		inc	ds:IF_NOT_COUNT
		jmp	short IFREENT
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

IFSTRING:				; ...
		push	si
		xor	cx, cx

FIRST_STRING:				; ...
		lodsb
		cmp	al, 0Dh
		jz	short IFERRORP
		call	DELIM
		jz	short EQUAL_CHECK
		inc	cx
		jmp	short FIRST_STRING
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

EQUAL_CHECK:				; ...
		cmp	al, '='
		jz	short EQUAL_CHECK2
		cmp	al, 0Dh
		jz	short IFERRPJ
		lodsb
		jmp	short EQUAL_CHECK
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

EQUAL_CHECK2:				; ...
		lodsb
		cmp	al, '='
		jnz	short IFERRPJ
		call	scanoff
		cmp	al, 0Dh
		jz	short IFERRPJ
		pop	di
		repe cmpsb
		jz	short MATCH
		dec	si
		cmp	byte ptr [si], 0Dh
		jz	short IFERRJ

SKIPSTRINGEND:				; ...
		lodsb

NOTMATCH:				; ...
		cmp	al, 0Dh

IFERRORJ2:				; ...
		jz	short IFERRJ
		call	DELIM
		jnz	short SKIPSTRINGEND
		mov	al, 0FFh	; -1
		jmp	short IFRET
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

IFERRPJ:				; ...
		jmp	IFERRORP
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

MATCH:					; ...
		lodsb
		call	DELIM
		jnz	short NOTMATCH
		xor	al, al
		jmp	short IFRET
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

IFEXISTS:				; ...
		lodsb
		call	DELIM
		jnz	short IFEXISTS
		mov	dx, offset BWDBUF ; DIRBUF
		mov	ax, 1A00h
		int	21h		; DOS -	SET DISK TRANSFER AREA ADDRESS
					; DS:DX	-> disk	transfer buffer
		mov	bx, 2
		add	bx, ds:IF_NOT_COUNT
		mov	ax, offset ARG_ARGV ; mov ax,ARG
		call	argv_calc
		mov	dx, [bx]
		mov	cx, 6
		mov	ax, 4E00h
		int	21h		; DOS -	2+ - FIND FIRST	ASCIZ (FINDFIRST)
					; CX = search attributes
					; DS:DX	-> ASCIZ filespec
					; (drive, path,	and wildcards allowed)
		jb	short IF_EX_C
		xor	al, al
		jmp	short IFRET
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

IF_EX_C:				; ...
		mov	al, 0FFh

IFRET:					; ...
		test	ds:IFNOTFLAG, 0FFh
		jz	short REALTEST
		not	al

REALTEST:				; ...
		or	al, al
		jz	short IFTRUE
		jmp	TCOMMAND
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

IFTRUE:					; ...
		call	scanoff
		mov	cx, si
		sub	cx, 81h
		sub	ds:80h,	cl
		mov	cl, ds:80h
		mov	ds:COMBUF+1, cl
		mov	di, (offset COMBUF+2)
		cld
		rep movsb
		mov	al, 0Dh
		stosb
		push	ds
		mov	ds, ds:RESSEG
		mov	ds:IfFlag, 0FFh
		pop	ds
		jmp	DOCOM1
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

IFERRORJ3:				; ...
		jmp	IFERRORJ2
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

IFERLEV:				; ...
		mov	bh, 10
		xor	bl, bl

GETNUMLP:				; ...
		lodsb
		cmp	al, 0Dh
		jz	short IFERRORJ3
		call	DELIM
		jz	short GOTNUM
		sub	al, '0'
		xchg	al, bl
		mul	bh
		add	al, bl
		xchg	al, bl
		jmp	short GETNUMLP
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

GOTNUM:					; ...
		push	ds
		mov	ds, ds:RESSEG
		mov	ah, byte ptr ds:RetCode
		pop	ds
		xor	al, al
		cmp	ah, bl
		jnb	short IFRET
		dec	al
		jmp	short IFRET
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

SHIFT:					; ...
		mov	ds, ds:RESSEG
		mov	ax, ds:Batch
		or	ax, ax
		jnz	short SHIFT1

SHIFT_RETN:				; ...
		retn
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

SHIFT1:					; ...
		mov	es, ax
		mov	ds, ax
		mov	di, 0Ch		; BATCHSEGMENT.BatParm
		lea	si, [di+2]
		mov	cx, 9
		rep movsw
		cmp	word ptr [di], 0FFFFh
		jz	short SHIFT_RETN
		mov	si, [di]
		mov	word ptr [di], 0FFFFh ;	-1

SKIPCRLP:				; ...
		lodsb
		cmp	al, 0Dh
		jnz	short SKIPCRLP
		cmp	byte ptr [si], 0
		jz	short SHIFT_RETN
		mov	[di], si
		retn

; ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ S U B	R O U T	I N E ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ


SKIPDELIM	proc near		; ...
		test	ds:Batch, 0FFFFh ; -1
		jz	short SKIPERR
		call	GETBATBYT
		call	DELIM
		jz	short SKIPDELIM
		clc
		retn
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

SKIPERR:				; ...
		stc

GOTO_RETN:				; ...
		retn
SKIPDELIM	endp

; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

_$CALL:					; ...
		push	si
		push	di
		push	ax
		push	cx
		mov	si, (offset COMBUF+2)
		call	scanoff
		add	si, 4		; length_call
		mov	di, (offset COMBUF+2)
		mov	cx, 124		; COMBUFLEN-length_call
		rep movsb
		pop	cx
		pop	ax
		pop	di
		pop	si
		push	ds
		mov	ds, ds:RESSEG
		mov	ds:Call_Flag, 1
		mov	ds:Call_Batch_Flag, 1
		cmp	ds:PipeFiles, 0
		jz	short _NOPIPE
		call	PIPEDEL

_NOPIPE:				; ...
		pop	ds
		retn
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

GOTO:					; ...
		mov	ds, ds:RESSEG
		test	ds:Batch, 0FFFFh
		jz	short GOTO_RETN
		xor	dx, dx
		push	ds
		mov	ds, ds:Batch
		mov	ds:8, dx	; [BATCHSEGMENT.BatSeek]
		mov	ds:10, dx	; [BATCHSEGMENT.BatSeek+2]
		mov	byte ptr ds:2, 0 ; [BATCHSEGMENT.BatchEOF]
		pop	ds
		call	PROMPTBAT
		mov	di, 5Dh		; FCB+1
		mov	cx, 11
		mov	al, 20h	; ' '
		repne scasb
		jnz	short NOINC
		inc	cx

NOINC:					; ...
		sub	cx, 11
		neg	cx
		mov	es:BWDBUF, cx
		call	SKIPDELIM
		jb	short BADGOTO
		cmp	al, ':'
		jz	short CHKLABEL

LABLKLP:				; ...
		call	GETBATBYT
		cmp	al, 0Ah
		jnz	short LABLKTST
		call	SKIPDELIM
		jb	short BADGOTO
		cmp	al, ':'
		jz	short CHKLABEL

LABLKTST:				; ...
		test	ds:Batch, 0FFFFh ; -1
		jnz	short LABLKLP

BADGOTO:				; ...
		call	BATCLOSE
		call	free_batch
		push	cs
		pop	ds
		assume ds:TRANGROUP
		mov	dx, offset BADLAB_PTR
		jmp	cerror
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

CHKLABEL:				; ...
		call	SKIPDELIM
		jb	short BADGOTO
		mov	di, 5Dh		; FCB+1
		mov	cx, es:BWDBUF	; GOTOLEN
		jmp	short GOTBYTE
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

NEXTCHRLP:				; ...
		push	cx
		call	GETBATBYT
		pop	cx

GOTBYTE:				; ...
		call	testkanj
		jz	short NOTKANJ1
		cmp	al, es:[di]
		jnz	short LABLKTST
		inc	di
		dec	cx
		jcxz	short LABLKTST
		push	cx
		call	GETBATBYT
		pop	cx
		cmp	al, es:[di]
		jmp	short KNEXTLABCHR
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

NOTKANJ1:				; ...
		or	al, 20h
		cmp	al, es:[di]
		jnz	short TRYUPPER
		jmp	short NEXTLABCHR
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

TRYUPPER:				; ...
		sub	al, 20h	; ' '
		cmp	al, es:[di]

KNEXTLABCHR:				; ...
		jnz	short LABLKTST

NEXTLABCHR:				; ...
		inc	di
		loop	NEXTCHRLP
		call	GETBATBYT
		cmp	es:BWDBUF, 8	; [es:GOTOLEN],8
		jge	short GOTOCONT
		cmp	al, 20h	; ' '
		ja	short LABLKTST

GOTOCONT:				; ...
		cmp	al, 0Dh
		jz	short SKIPLFEED

TONEXTBATLIN:				; ...
		call	GETBATBYT
		cmp	al, 0Dh
		jnz	short TONEXTBATLIN

SKIPLFEED:				; ...
		call	GETBATBYT
		push	es
		mov	es, ds:Batch
		mov	byte ptr es:2, 0 ; BATCHSEGMENT.BatchEOF
		pop	es
		call	BATCLOSE
		retn

; ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ S U B	R O U T	I N E ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ


BATCLOSE	proc near		; ...
		mov	bx, cs:BATHAND
		cmp	bx, 5
		jb	short CLOSERETURN
		mov	ah, 3Eh
		int	21h		; DOS -	2+ - CLOSE A FILE WITH HANDLE
					; BX = file handle

CLOSERETURN:				; ...
		mov	ds:In_Batch, 0
		retn
BATCLOSE	endp


; ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ S U B	R O U T	I N E ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ


BATOPEN		proc near		; ...
		push	ds
		mov	ds, ds:Batch
		assume ds:nothing
		mov	dx, 20h		; BATCHSEGMENT.BatFile
		mov	ax, 3D00h
		int	21h		; DOS -	2+ - OPEN DISK FILE WITH HANDLE
					; DS:DX	-> ASCIZ filename
					; AL = access mode
					; 0 - read
		jb	short SETERRDL
		mov	dx, ds:8	; [BATCHSEGMENT.BatSeek]
		mov	cx, ds:10	; [BATCHSEGMENT.BatSeek+2]
		pop	ds
		mov	es:BATHAND, ax
		mov	bx, ax
		mov	ax, 4200h
		int	21h		; DOS -	2+ - MOVE FILE READ/WRITE POINTER (LSEEK)
					; AL = method: offset from beginning of	file
		mov	es:BATBUFPOS, 0FFFFh
		retn
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

SETERRDL:				; ...
		mov	bx, dx
		call	get_ext_error_number
		mov	dx, ax
		mov	al, [bx]
		sub	al, '@'
		pop	ds
		stc
		retn
BATOPEN		endp


; ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ S U B	R O U T	I N E ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ


free_batch	proc near		; ...
		push	es
		mov	bx, ds:Next_Batch
		or	bx, bx
		jz	short fb_ret

_ClearBatch:				; ...
		mov	es, bx
		mov	bx, es:5	; [es:BATCHSEGMENT.BatForPtr]
		cmp	bx, 0
		jz	short no_bat_for
		push	es
		mov	es, bx
		mov	ah, 49h
		int	21h		; DOS -	2+ - FREE MEMORY
					; ES = segment address of area to be freed
		pop	es

no_bat_for:				; ...
		mov	cl, es:1	; [es:BATCHSEGMENT.BatEchoFlag]
		mov	bx, es:3	; [es:BATCHSEGMENT.BatLast]
		mov	ah, 49h
		int	21h		; DOS -	2+ - FREE MEMORY
					; ES = segment address of area to be freed
		mov	ds:Batch, bx
		dec	ds:Nest
		jnz	short _ClearBatch
		mov	ds:EchoFlag, cl
		mov	ds:Batch, 0

fb_ret:					; ...
		pop	es
		retn
free_batch	endp

; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ
; START	OF FUNCTION CHUNK FOR PROMPTBAT

FORTERM:				; ...
		push	cs
		pop	ds
		assume ds:TRANGROUP
		push	cs
		pop	es
		assume es:TRANGROUP
		call	FOROFF
		mov	ds, es:RESSEG
		assume ds:nothing
		cmp	ds:SingleCom, 0FF00h
		jnz	short BAT_CRLF
		cmp	ds:Nest, 0
		jnz	short BAT_CRLF
		mov	ds:SingleCom, 0FFFFh
		jmp	short NOFORP2
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

BAT_CRLF:				; ...
		test	ds:EchoFlag, 1
		jz	short NOFORP2
		test	ds:Batch, 0FFFFh
		jz	short NOFORP2
		call	CRLF2

NOFORP2:				; ...
		jmp	TCOMMAND
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

_$FOR_EXIT:				; ...
		jmp	short FORTERM
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

FORPROC:				; ...
		mov	ax, ds:ForPtr
		mov	ds, ax
		mov	es, ax
		assume es:nothing
		mov	dx, 5CBh	; FOR_INFO.FORDMA
		mov	ax, 1A00h
		int	21h		; DOS -	SET DISK TRANSFER AREA ADDRESS
					; DS:DX	-> disk	transfer buffer

FOR_BEGIN:				; ...
		cmp	word ptr ds:545h, 0 ; FOR_INFO.FOR_EXPAND
		jz	short FOR_BEGIN1
		inc	word ptr ds:547h ; FOR_INFO.FOR_MINARG

FOR_BEGIN1:				; ...
		mov	bx, ds:547h
		cmp	bx, ds:549h	; FOR_INFO.FOR_MAXARG
		jg	short _$FOR_EXIT
		mov	ax, 0
		call	argv_calc
		mov	cx, [bx+3]	; [bx+ARGV_ELE.argstartel]
		mov	dx, [bx]	; [bx+ARGV_ELE.argpointer]
		test	byte ptr [bx+2], 4 ; [bx+ARGV_ELE.argflags]
		jnz	short FORSUB
		mov	si, [bx]
		mov	al, '('
		cmp	[si-1],	al
		jnz	short FORSUB
		inc	cx
		mov	al, ':'
		cmp	[si+1],	al
		jnz	short FORSUB
		add	cx, 2

FORSUB:					; ...
		sub	cx, dx
		cmp	word ptr ds:545h, 0 ; [FOR_INFO.FOR_EXPAND]
		jz	short FOR_FIND_NEXT
		test	byte ptr [bx+2], 2
		jnz	short FOR_FIND_FIRST
		mov	cx, [bx+5]	; [bx+ARGV_ELE.arglen]
		jmp	short FOR_SMOOSH
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

FOR_FIND_FIRST:				; ...
		push	cx
		xor	cx, cx
		mov	ax, 4E00h
		int	21h		; DOS -	2+ - FIND FIRST	ASCIZ (FINDFIRST)
					; CX = search attributes
					; DS:DX	-> ASCIZ filespec
					; (drive, path,	and wildcards allowed)
		pop	cx
		jmp	short FOR_RESULT
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

FOR_FIND_NEXT:				; ...
		mov	ax, 4F00h
		int	21h		; DOS -	2+ - FIND NEXT ASCIZ (FINDNEXT)
					; [DTA]	= data block from
					; last AH = 4Eh/4Fh call

FOR_RESULT:				; ...
		mov	ax, 0FFFFh
		jb	short FOR_CHECK
		mov	ax, 0

FOR_CHECK:				; ...
		mov	ds:545h, ax	; [FOR_INFO.FOR_EXPAND]
		or	ax, ax
		jnz	short FOR_BEGIN

FOR_SMOOSH:				; ...
		mov	si, [bx]
		mov	di, 54Bh	; FOR_INFO.FORBUF
		rep movsb
		cmp	word ptr ds:545h, 0 ; [FOR_INFO.FOR_EXPAND]
		jnz	short FOR_MAKE_COM
		mov	si, 5E9h	; FOR_INFO.FORDMA+FIND_BUF.PNAME

FOR_MORE:				; ...
		cmp	byte ptr [si], 0
		jz	short FOR_MAKE_COM
		movsb
		jnz	short FOR_MORE

FOR_MAKE_COM:				; ...
		xor	al, al
		stosb
		xor	cx, cx
		not	cx
		xor	bx, bx
		mov	di, (offset COMBUF+2)
		mov	bl, ds:544h	; [FOR_INFO.FOR_COM_START]
		mov	dh, ds:64Bh	; [FOR_INFO.FOR_VAR]
		push	cs
		pop	es
		assume es:TRANGROUP
		mov	ax, 0		; FOR_INFO.FOR_ARGS = 0
		call	argv_calc
		mov	si, [bx+9]	; [bx+ARGV_ELE.arg_ocomptr]
		inc	si

FOR_MAKE_LOOP:				; ...
		mov	al, [si]
		inc	si
		cmp	al, '%'
		jnz	short FOR_STOSB
		cmp	[si], dh
		jnz	short FOR_STOSB
		inc	si
		push	si
		mov	si, 54Bh	; FOR_INFO.FORBUF

SLOOP:					; ...
		lodsb
		stosb
		or	al, al
		loopne	SLOOP
		dec	di
		inc	cx
		pop	si
		jmp	short FOR_MAKE_LOOP
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

FOR_STOSB:				; ...
		stosb
		dec	cx
		cmp	al, 0Dh
		jnz	short FOR_MAKE_LOOP
		not	cl
		mov	es:COMBUF+1, cl
		mov	ds, es:RESSEG
		test	ds:EchoFlag, 1
		jz	short NOECHO3
		cmp	ds:NullFlag, 1
		jz	short NO_CRLF_PR
		call	CRLF2

NO_CRLF_PR:				; ...
		mov	ds:NullFlag, 0
		push	cs
		pop	ds
		assume ds:TRANGROUP
		push	di
		call	PRINT_PROMPT
		pop	di
		mov	byte ptr es:[di-1], 0
		mov	string_ptr_2, (offset COMBUF+2)
		mov	dx, offset string_buf_ptr
		call	std_printf
		mov	byte ptr es:[di-1], 0Dh
		jmp	DOCOM
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

NOECHO3:				; ...
		mov	ds:NullFlag, 0
		push	cs
		pop	ds
		jmp	DOCOM1
; END OF FUNCTION CHUNK	FOR PROMPTBAT
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

FORNESTERRJ:				; ...
		call	FOROFF
		jmp	FORNESTERR
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

FORERRORJ:				; ...
		jmp	IFFORERROR
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

_$FOR:					; ...
		mov	es, RESSEG
		assume es:nothing
		cmp	es:ForFlag, 0
		jnz	short FORNESTERRJ
		cmp	es:PipeFiles, 0
		jz	short NO_PIPE
		call	PIPEDEL

NO_PIPE:				; ...
		xor	dx, dx
		call	NEXTARG
		jb	short FORERRORJ
		cmp	al, '%'
		jnz	short FORERRORJ
		mov	bp, ax
		lodsb
		or	al, al
		jnz	short FORERRORJ
		call	NEXTARG
		jb	short FORERRORJ
		and	ax, 0DFDFh	; ~2020h (not 2020h)
		cmp	ax, 'NI'        ; "IN"
		jnz	short FORERRORJ
		lodsb
		or	al, al
		jnz	short FORERRORJ

CHECKLPAREN:
		call	NEXTARG
		jb	short FORERRORJ

LPCHECK:
		cmp	al, '('
		jnz	short FORERRORJ
		cmp	ah, 0
		jz	short FOR_PAREN_TOKEN
		cmp	ah, ')'
		jnz	short FOR_LIST_NOT_EMPTY
		jmp	FORTERM
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

FOR_LIST_NOT_EMPTY:			; ...
		inc	word ptr [bx]	; [bx+ARGV_ELE.argpointer] ; [bx+0]
		dec	word ptr [bx+5]	; [bx+ARGV_ELE.arglen]
		inc	si
		jmp	short FOR_LIST
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

FOR_PAREN_TOKEN:			; ...
		call	NEXTARG
		jb	short FORERRORJ
		cmp	ax, 29h		; db 29h,0 ; db	')',0
		jnz	short FOR_LIST
		jmp	FORTERM
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

FORERORJJ:				; ...
		jmp	IFFORERROR
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

FOR_LIST:				; ...
		mov	cx, dx

SKIP_LIST:				; ...
		add	si, [bx+5]	; [bx+ARGV_ELE.arglen]
		sub	si, 3
		mov	al, ')'
		cmp	[si], al
		jz	short FOR_END_LIST
		call	NEXTARG
		jb	short FORERORJJ
		jmp	short SKIP_LIST
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

FOR_END_LIST:				; ...
		mov	di, dx
		mov	byte ptr [si], 0
		cmp	ax, 29h		; db 29h,0 ; db	')',0
		jz	short FOR_DO
		inc	di

FOR_DO:					; ...
		call	NEXTARG
		jb	short FORERORJJ
		and	ax, 0DFDFh	; and ax,~2020h
		cmp	ax, 'OD'        ; "DO" ; 4F44h
		jnz	short FORERORJJ
		lodsb
		or	al, al
		jnz	short FORERORJJ
		call	NEXTARG
		jb	short FORERORJJ
		push	ax
		push	bx
		push	cx
		push	dx
		push	di
		push	si
		push	bp
		call	FREE_TPA
		call	FOROFF
		mov	bx, 264		; FOR_INFO.size-ARG_UNIT.SIZE
		call	SAVE_ARGS
		pushf
		mov	es:ForPtr, ax
		call	ALLOC_TPA
		popf
		pop	bp
		pop	si
		pop	di
		pop	dx
		pop	cx
		pop	bx
		pop	ax
		jb	short FOR_ALLOC_ERR
		push	es
		push	es:ForPtr
		pop	es
		dec	cx
		dec	di
		mov	es:547h, cx	; [es:FOR_INFO.FOR_MINARG]
		mov	es:549h, di	; [es:FOR_INFO.FOR_MAXARG]
		mov	es:544h, dl	; [es:FOR_INFO.FOR_COM_START]
		mov	word ptr es:545h, 0FFFFh ; [es:FOR_INFO.FOR_EXPAND]
		mov	ax, bp
		mov	es:64Bh, ah	; [es:FOR_INFO.FOR_VAR]
		pop	es
		inc	es:ForFlag
		cmp	es:SingleCom, 0FFFFh
		jnz	short FOR_RET
		mov	es:SingleCom, 0FF00h

FOR_RET:				; ...
		retn
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

FOR_ALLOC_ERR:				; ...
		mov	msg_disp_class,	1 ; ext_msg_class
		mov	dx, offset extend_buf_ptr
		mov	extend_buf_ptr,	8 ; ERROR_NOT_ENOUGH_MEMORY
		jmp	cerror

; ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ S U B	R O U T	I N E ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ


NEXTARG		proc near		; ...
		inc	dx
		cmp	dx, ARG_ARGVCNT	; [ARG+ARG_UNIT.argvcnt]
		jge	short NEXTARG_ERR
		mov	bx, dx
		mov	ax, offset ARG_ARGV ; ARG+ARG_UNIT.argv
		call	argv_calc
		mov	si, [bx]	; [bx+ARGV_ELE.argpointer] ; mov si,[bx+0]
		lodsw
		clc
		retn
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

NEXTARG_ERR:				; ...
		stc
		retn
NEXTARG		endp

; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

FORNESTERR:				; ...
		push	ds
		mov	ds, RESSEG
		assume ds:nothing
		mov	dx, offset FORNESTMES_PTR
		cmp	ds:SingleCom, 0FF00h
		jnz	short NOFORP3
		mov	ds:SingleCom, 0FFFFh

NOFORP3:				; ...
		pop	ds
		jmp	cerror

; ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ S U B	R O U T	I N E ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ


FOROFF		proc near		; ...
		push	ax
		push	es
		mov	es, cs:RESSEG
		mov	ax, es:ForPtr
		or	ax, ax
		jz	short FREEDONE
		push	es
		mov	es, ax
		mov	ah, 49h
		int	21h		; DOS -	2+ - FREE MEMORY
					; ES = segment address of area to be freed
		pop	es

FREEDONE:				; ...
		mov	es:ForPtr, 0
		mov	es:ForFlag, 0
		pop	es
		pop	ax
		retn
FOROFF		endp

; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

CATALOG:				; ...
		call	SetDefaults
		call	ParseEnvironment
		call	ParseCmdLine
		jnb	short catalog1
		jmp	catErr
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

catalog1:				; ...
		call	SetOptions
		call	SetCollatingTable
		test	ds:_Bits, 8	; mask.bare
		jnz	short catalog2
		call	OkVolArg
		sub	ds:LeftOnPage, 2
		jmp	short catCrunch
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

catalog2:				; ...
		call	DisAppend
		mov	dx, offset BWDBUF ; offset TRANGROUP:DirBuf
		mov	ah, 1Ah
		int	21h		; DOS -	SET DISK TRANSFER AREA ADDRESS
					; DS:DX	-> disk	transfer buffer
		mov	di, 5Ch		; FCB
		inc	di
		mov	al, '?'
		mov	cx, 11
		rep stosb

catCrunch:				; ...
		call	CrunchPath
		jb	short catRecErr
		cmp	ds:COMSW, 0
		jnz	short catRecErr
		call	InstallCtrlC
		call	ZeroTotals
		call	ListDir
		jb	short catExtErr
		test	ds:_Bits, 4	; mask.subd
		jz	short catalog3
		call	ListSubds
		jb	short catExtErr

catalog3:				; ...
		test	ds:_Bits, 8
		jnz	short catRet
		mov	ax, word ptr ds:FileCntTotal
		or	ax, ax
		jz	short catNoFiles
		call	DisplayTotals
		jmp	short catRet
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

catRecErr:				; ...
		test	ds:COMSW, 1	; mask.dev
		jnz	short catNoFiles
		test	ds:COMSW, 2	; mask.baddir
		jz	short catExtErr
		mov	dx, offset badcd_ptr
		jmp	short catErr
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

catNoFiles:				; ...
		call	DisplayHeader
		mov	ax, 2		; ERROR_FILE_NOT_FOUND
		mov	ds:msg_disp_class, 1 ; ext_msg_class
		mov	dx, offset extend_buf_ptr
		mov	ds:extend_buf_ptr, ax
		jmp	short catErr
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

catExtErr:				; ...
		call	Set_Ext_Error_Msg
		cmp	ds:extend_buf_ptr, 18 ;	ERROR_NO_MORE_FILES
		jnz	short catErr
		mov	ds:extend_buf_ptr, 2 ; ERROR_FILE_NOT_FOUND

catErr:					; ...
		jmp	cerror
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

catRet:					; ...
		retn

; ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ S U B	R O U T	I N E ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ


SetDefaults	proc near		; ...
		mov	di, offset SrcBuf
		mov	ds:PathPos, di
		mov	al, '*'
		stosb
		mov	al, 0Dh
		stosb
		mov	ds:PathCnt, 1
		xor	ax, ax
		mov	ds:COMSW, ax
		mov	ds:_Bits, ax
		mov	ds:DestBuf, al
		mov	ds:AttrSpecified, 6 ; ATTR_HIDDEN+ATTR_SYSTEM
		mov	ds:AttrSelect, al
		retn
SetDefaults	endp

; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

ParseEnvironment:			; ...
		call	GetEnvValue
		jb	short peRet
		call	Parse_Line
		cmp	ax, 0FFFFh	; END_OF_LINE
		jz	short peRet
		call	std_eprintf
		mov	ds:msg_disp_class, 0FFh
		mov	dx, offset errparsenv_ptr
		call	Printf_Crlf

peRet:					; ...
		retn

; ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ S U B	R O U T	I N E ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ


ParseCmdLine	proc near		; ...
		mov	si, 81h
		call	Parse_Line
		cmp	ax, 0FFFFh
		jz	short pcOk
		stc
		jmp	short pcRet
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

pcOk:					; ...
		clc

pcRet:					; ...
		retn
ParseCmdLine	endp


; ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ S U B	R O U T	I N E ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ


SetCollatingTable proc near		; ...
		mov	dx, offset STACK ; offset TRANGROUP:InternatVars
		mov	ax, 3800h
		int	21h		; DOS -	2+ - GET COUNTRY-DEPENDENT INFORMATION
					; get current-country info
					; DS:DX	-> buffer for returned info
		jb	short scNoTable
		cmp	bx, 1
		jz	short scNoTable
		mov	ax, 6506h
		mov	bx, 0FFFFh
		mov	cx, 5
		mov	dx, bx
		mov	di, offset CountryPtrInfo
		int	21h		; DOS -	4.x internal - COUNTRY-DEPENDENT FILENAME CAPITALIZATION
					; AL = function	-
		jnb	short scRet

scNoTable:				; ...
		mov	ds:CountryPtrInfo, 0 ; CountryPtrId

scRet:					; ...
		retn
SetCollatingTable endp


; ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ S U B	R O U T	I N E ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ


SetOptions	proc near		; ...
		test	ds:_Bits, 8	; mask.bare
		jz	short setopts1
		and	ds:_Bits, 0FFFEh ; ~mask.wide ;	not mask.wide

setopts1:				; ...
		push	ds
		mov	ax, 40h		; ROMBIOS_DATA
		mov	ds, ax
		assume ds:nothing
		mov	al, ds:84h	; CRT_Rows
		pop	ds
		assume ds:nothing
		or	al, al
		jnz	short setopts2
		mov	al, 25

setopts2:				; ...
		xor	ah, ah

setopts3:
		inc	al
		mov	ds:LinPerPag, ax
		mov	ax, 440Ch
		mov	bx, 1
		mov	ch, 3
		mov	cl, 7Fh
		mov	dx, offset Display_Ioctl
		int	21h		; DOS -	2+ - IOCTL -
		mov	ax, ds:LinPerPag
		mov	ds:LeftOnPage, ax
		mov	ds:PerLine, 1	; NORMPERLIN
		test	ds:_Bits, 1	; mask.wide
		jz	short setopts4
		mov	ds:PerLine, 5	; WIDEPERLIN

setopts4:				; ...
		cmp	ds:SrcBuf+1, ':'
		jnz	short soRet
		mov	al, ds:SrcBuf
		and	al, 0DFh
		sub	al, '@'
		mov	ds:5Ch,	al	; [FCB]

soRet:					; ...
		retn
SetOptions	endp

; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

CrunchPath:				; ...
		call	FileIsDevice
		jnz	short crpath1
		or	ds:COMSW, 1	; mask.dev
		jmp	short cpErr
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

crpath1:				; ...
		push	ds:PathPos
		mov	ds:DirFlag, 0FFh ; -1
		call	PathCrunch
		mov	ds:DirFlag, 0
		pop	si
		jb	short cpNoDir
		jz	short cpRet
		mov	si, ds:DestTail
		jmp	short cpFile
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

cpNoDir:				; ...
		mov	ax, ds:Msg_Numb
		or	ax, ax
		jnz	short cpErr
		cmp	ds:DestIsDir, 0
		jz	short cpMaybe
		or	ds:COMSW, 2	; mask.baddir
		jmp	short cpErr
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

cpMaybe:				; ...
