lsRet:					; ...
		retn
ListSubds	endp


; ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ S U B	R O U T	I N E ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ


CheckChild	proc near		; ...
		test	ds:DIRBUF_find_buf_attr, 10h ; [DIRBUF+FIND_BUF.ATTR],ATTR_DIRECTORY
		jz	short ccRet
