		call	CmpAscz
		jbe	short ccRet
		mov	si, offset DIRBUF_find_buf_pname
		mov	di, bp
		call	CmpAscz
		jnb	short ccRet
		mov	si, offset DIRBUF_find_buf_pname
		mov	di, bp
		mov	cx, 13
		rep movsb

ccRet:					; ...
		retn
CheckChild	endp


; ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ S U B	R O U T	I N E ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ


CmpEntry	proc near		; ...
		mov	si, offset DestBuf

ceLoop:					; ...
		xor	ax, ax
		mov	al, [si]
		or	al, al
		jz	short ceDone
		inc	si
		push	si
		dec	al
		shl	al, 1
		mov	si, ax
		mov	ax, cs:FieldCmps[si]
		jb	short ceDn
		call	ax
		jmp	short ceNs
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

ceDn:					; ...
		xchg	bx, bp
		call	ax
		xchg	bx, bp

ceNs:					; ...
		pop	si
		jz	short ceLoop

ceDone:					; ...
		retn
CmpEntry	endp

; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ
FieldCmps	dw offset CmpName	; ...
		dw offset CmpExt
		dw offset CmpTime
		dw offset CmpSize
		dw offset CmpType
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

CmpName:				; ...
		mov	si, bx
		mov	di, bp
		add	si, 1		; entry.filename
		add	di, 1		; entry.filename
		mov	cx, 8		; size entry.filename
		jmp	short CmpStr
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

CmpExt:					; ...
		mov	si, bx
		mov	di, bp
		add	si, 9		; entry.fileext
		add	di, 9		; entry.fileext
		mov	cx, 3		; size entry.fileext

CmpStr:					; ...
		cmp	ds:CountryPtrInfo, 6 ; CountryPtrId
		jnz	short cnNoCollTable
		push	bp
		push	bx
		push	ds
		lds	bx, ds:CountryPtr
		mov	bp, [bx]
		inc	bx
		inc	bx
		xor	ax, ax

cnNextChar:				; ...
		mov	al, es:[di]
		inc	di
		cmp	ax, bp
		jnb	short cn1
		xlat

cn1:					; ...
		mov	dx, ax
		lods	byte ptr es:[si]
		cmp	ax, bp
		jnb	short cn2
		xlat

cn2:					; ...
		cmp	ax, dx
		loope	cnNextChar
		pop	ds
		pop	bx
		pop	bp
		retn
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

cnNoCollTable:				; ...
		repe cmps byte ptr es:[si], byte ptr es:[di]
		retn
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

CmpTime:				; ...
		mov	si, bx
		mov	di, bp
		add	si, 16		; entry.filedate + size	entry.filedate - 1
		add	di, 16		; entry.filedate + size	entry.filedate - 1
		mov	cx, 4		; size entry.filetime +	size entry.filedate
		std
		repe cmps byte ptr es:[si], byte ptr es:[di]
		cld
		retn
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

CmpSize:				; ...
		mov	si, bx
		mov	di, bp
		add	si, 20
		add	di, 20
		mov	cx, 4
		std
		repe cmps byte ptr es:[si], byte ptr es:[di]
		cld
		retn
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

CmpType:				; ...
		mov	al, es:[bx+12]	; entry.fileattr
		mov	ah, es:[bp+12]
		and	ax, 1010h	; (ATTR_DIRECTORY<<8)+ATTR_DIRECTORY
		cmp	ah, al
		retn

; ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ S U B	R O U T	I N E ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ


