DefaultAttr	endp


; ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ S U B	R O U T	I N E ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ


DisplayTotals	proc near		; ...
		test	ds:_Bits, 4	; mask.subd
		jz	short dtFree
		call	CRLF2
		call	UseLine
		mov	dx, offset total_ptr
		call	std_printf
		call	UseLine
		mov	ax, word ptr ds:FileCntTotal
		mov	si, offset FileSizTotal
		mov	di, offset FileSiz
		movsw
		movsw
		call	DisplayCntSiz

dtFree:					; ...
		mov	ah, 36h
		mov	dl, ds:5Ch
		int	21h		; DOS -	2+ - GET DISK SPACE
					; DL = drive code (0 = default,	1 = A, 2 = B, etc.)
		cmp	ax, 0FFFFh	; -1
		jz	short dtRet
		mul	cx
		mul	bx
		mov	word ptr ds:Bytes_Free,	ax
		mov	word ptr ds:Bytes_Free+2, dx
		mov	dx, offset bytmes_ptr
		call	std_printf
		call	UseLine

dtRet:					; ...
		retn
DisplayTotals	endp


; ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ S U B	R O U T	I N E ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ


FileIsDevice	proc near		; ...
		mov	dx, ds:PathPos
		mov	di, dx
		add	di, ds:PathCnt
		xor	bl, bl
		xchg	bl, [di]
		xor	cx, cx
		mov	ah, 4Eh
		int	21h		; DOS -	2+ - FIND FIRST	ASCIZ (FINDFIRST)
					; CX = search attributes
					; DS:DX	-> ASCIZ filespec
					; (drive, path,	and wildcards allowed)
		xchg	bl, [di]
		jb	short piCol
		test	ds:DIRBUF_find_buf_attr, 40h ; ATTR_DEVICE
		jz	short piCol
		xor	cx, cx
		jmp	short piRet
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

piCol:					; ...
		dec	di
		mov	al, ':'         ; COLON_CHAR
		mov	cx, ds:PathCnt
		dec	cx
		dec	cx
		or	cx, cx
		js	short piRet
		or	di, di
		std
		repne scasb
		cld

piRet:					; ...
		retn
FileIsDevice	endp


; ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ S U B	R O U T	I N E ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ


