FindNextChild	proc near		; ...
		sub	sp, 12
		mov	ax, 0FFh
		push	ax
		mov	bp, sp
		mov	ax, '*'         ; 002Ah
		push	ax
		mov	ax, 2E2Ah	; '*.'
		push	ax
		call	GetDriveLtr
		push	ax
		mov	dx, sp
		mov	cx, 10h		; ATTR_DIRECTORY
		mov	ah, 4Eh
		int	21h		; DOS -	2+ - FIND FIRST	ASCIZ (FINDFIRST)
					; CX = search attributes
					; DS:DX	-> ASCIZ filespec
					; (drive, path,	and wildcards allowed)
		jb	short fcRet
		call	CheckChild

fcNext:					; ...
		mov	cx, 10h
		mov	ah, 4Fh
		int	21h		; DOS -	2+ - FIND NEXT ASCIZ (FINDNEXT)
