		call	DisplayFile
		jmp	short ldNext
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

ldErr:					; ...
		cmp	ax, 2		; ERROR_FILE_NOT_FOUND
		jz	short ldDone
		cmp	ax, 18		; ERROR_NO_MORE_FILES
		jz	short ldDone
		stc
		jmp	short ldRet
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

ldDone:					; ...
		cmp	ds:FileCnt, 0
		jz	short ld2
		call	DisplayTrailer

ld2:					; ...
		clc

ldRet:					; ...
		retn
ListDir		endp


; ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ S U B	R O U T	I N E ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ


LoadEntries	proc near		; ...
		push	es
		mov	es, ds:TPA
		xor	di, di
		and	ds:_Bits, 0FFDFh ; ~mask.inmem ; not mask.inmem
		call	GetFirst
		jb	short leRet
		call	LoadEntry

leNext:					; ...
		call	GetNext
		jb	short leLoaded
		mov	ax, ds:BYTCNT
		sub	ax, di
		cmp	ax, 23		; size EntryStruc+2
		jb	short leOk
		call	LoadEntry
		jmp	short leNext
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

leLoaded:				; ...
		mov	byte ptr es:[di], 0FFh
		or	ds:_Bits, 20h	; mask.inmem

leOk:					; ...
		clc

leRet:					; ...
		pop	es
		retn
LoadEntries	endp


; ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ S U B	R O U T	I N E ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ


LoadEntry	proc near		; ...
		mov	si, offset DIRBUF_8 ; DIRBUF+8
		xor	al, al
		stosb
		mov	cx, 11
		rep movsb
		lodsb
		stosb
		add	si, 10		; (DIR_ENTRY.DIR_TIME-DIR_ENTRY.DIR_ATTR)-1
		movsw
		movsw
		inc	si
		inc	si
		and	al, 10h		; ATTR_DIRECTORY
		jnz	short leSetDirSize
		movsw
		movsw
		retn
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

leSetDirSize:				; ...
		xor	ax, ax
		stosw
		stosw
		retn
LoadEntry	endp


; ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ S U B	R O U T	I N E ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ


NoOrder		proc near		; ...
		mov	ds:DestBuf, 0
		clc
		retn
NoOrder		endp


; ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ S U B	R O U T	I N E ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ


OnOffSw		proc near		; ...
		mov	cx, di
		shr	cx, 1
		shr	cx, 1
		mov	ax, 1
		shl	ax, cl
		test	di, 2
		jz	short oo1
		or	ds:_Bits, ax
		jmp	short ooRet
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

oo1:					; ...
		not	ax
		and	ds:_Bits, ax

ooRet:					; ...
		clc
		retn
OnOffSw		endp


; ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ S U B	R O U T	I N E ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ


ParseAttr	proc near		; ...
		push	si
		mov	ds:AttrSpecified, 0
		mov	si, [bx+4]	; [bx+ResultBuffer.ValuePtr]

paLoop:					; ...
		mov	dx, 1
		lodsb
		or	al, al
		jz	short paOk
		cmp	al, '-'
		jnz	short pa1
		dec	dx
		lodsb

pa1:					; ...
		mov	di, offset AttrLtrs ; "RHSvDA"
		mov	cx, 6		; NUM_ATTR_LTRS
		repne scasb
		jnz	short paErr
		not	cx
		add	cx, 6
		mov	al, 1
		shl	al, cl
		or	ds:AttrSpecified, al
		not	al
		and	ds:AttrSelect, al
		shl	dl, cl
		or	ds:AttrSelect, dl
		jmp	short paLoop
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

paErr:					; ...
		call	SetupParamError
		stc
		jmp	short paRet
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

paOk:					; ...
		clc

paRet:					; ...
		pop	si
		retn
ParseAttr	endp


; ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ S U B	R O U T	I N E ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ


Parse_Line	proc near		; ...
		mov	di, offset PARSE_DIR
		xor	cx, cx

plPars:					; ...
		call	Parse_With_Msg
		cmp	ax, 0FFFFh	; END_OF_LINE
		jz	short plRet
		cmp	ax, 0
		jnz	short plRet
		mov	bx, dx
		cmp	byte ptr [bx], 5 ; RESULT_FILESPEC
		jz	short plFil
		call	ParseSwitch
		jb	short plRet
		jmp	short plPars
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

plFil:					; ...
		call	CopyPathname
		jmp	short plPars
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

plRet:					; ...
		retn
Parse_Line	endp


; ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ S U B	R O U T	I N E ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ


ParseOrder	proc near		; ...
		push	si
		push	bx
		mov	si, [bx+4]	; [bx+ResultBuffer.ValuePtr]
		mov	bx, offset DestBuf
		mov	al, [si]
		or	al, al
		jnz	short poLtr
		mov	byte ptr [bx], 5 ; sort	1st by group
		inc	bx
		mov	byte ptr [bx], 1 ; then	by name
		inc	bx
		mov	byte ptr [bx], 2 ; then	by extension
		inc	bx
		jmp	short poOk
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

poLtr:					; ...
		xor	dl, dl
		lodsb
		or	al, al
		jz	short poOk
		cmp	al, '-'
		jnz	short po1
		mov	dl, 80h
		lodsb

po1:					; ...
		mov	di, offset OrderLtrs ; "NEDSG"
		mov	cx, 5		; NUM_ORDER_LTRS
		repne scasb
		jnz	short poErr
		neg	cx
		add	cx, 5		; NUM_ORDER_LTRS
		or	cl, dl
		mov	[bx], cl
		inc	bx
		cmp	bx, offset EndDestBuf
		jnb	short poErr
		jmp	short poLtr
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

poErr:					; ...
		pop	bx
		call	SetupParamError
		stc
		jmp	short poRet
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

poOk:					; ...
		mov	byte ptr [bx], 0
		pop	bx
		clc

poRet:					; ...
		pop	si
		retn
ParseOrder	endp


; ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ S U B	R O U T	I N E ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ


ParseSwitch	proc near		; ...
		push	cx
		push	di
		mov	ax, [bx+2]	; [bx+ResultBuffer.SynPtr]
		mov	di, offset Dir_Sw_Ptrs
		mov	cx, 14		; NUM_DIR_SWS
		cld
		repne scasw
		sub	di, offset Dir_Sw_Ptrs_2 ; Dir_Sw_Ptrs+2
		call	cs:SwHandler[di]
		pop	di
		pop	cx
		retn
ParseSwitch	endp

; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ
SwHandler	dw offset OnOffSw	; ...
		dw offset OnOffSw
		dw offset OnOffSw
		dw offset OnOffSw
		dw offset OnOffSw
		dw offset OnOffSw
		dw offset OnOffSw
		dw offset OnOffSw
		dw offset OnOffSw
		dw offset OnOffSw
		dw offset NoOrder
		dw offset ParseOrder
		dw offset DefaultAttr
		dw offset ParseAttr

; ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ S U B	R O U T	I N E ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ


ChangeDir	proc near		; ...
		mov	di, offset BWDBUF ; DIRBUF
		call	GetDriveLtr
		stosw
		mov	si, dx

cdLoop:					; ...
		lodsb
		stosb
		or	al, al
		jnz	short cdLoop
		mov	dx, offset BWDBUF ; DIRBUF
		mov	ah, 3Bh
		int	21h		; DOS -	2+ - CHANGE THE	CURRENT	DIRECTORY (CHDIR)
					; DS:DX	-> ASCIZ directory name	(may include drive)
		retn
ChangeDir	endp


; ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ S U B	R O U T	I N E ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ


CmpAscz		proc near		; ...
		push	di
		mov	di, si
		xor	al, al
		mov	cx, 0FFFFh
		repne scasb
		not	cx
		pop	di
		repe cmpsb
		retn
CmpAscz		endp


; ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ S U B	R O U T	I N E ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ


CopyPathname	proc near		; ...
		push	si
		lds	si, [bx+4]	; [bx+ResultBuffer.ValuePtr]
		call	Move_To_SrcBuf
		pop	si
		retn
CopyPathname	endp


; ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ S U B	R O U T	I N E ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ


CountFile	proc near		; ...
		push	es
		mov	es, ds:TPA
		inc	ds:FileCnt
		inc	word ptr ds:FileCntTotal
		jnz	short cntf1
		inc	word ptr ds:FileCntTotal+2

cntf1:					; ...
		mov	ax, es:[bx+17]	; EntryStruc.filesize
		mov	dx, es:[bx+19]	; EntryStruc.filesize +	2
		add	word ptr ds:FileSiz, ax
		adc	word ptr ds:FileSiz+2, dx
		add	word ptr ds:FileSizTotal, ax
		adc	word ptr ds:FileSizTotal+2, dx
		pop	es
		retn
CountFile	endp


; ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ S U B	R O U T	I N E ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ


DisplayBare	proc near		; ...
		mov	cx, ds
		mov	ds, ds:TPA
		cmp	byte ptr [bx+1], '.' ; [bx+EntrStruc.filename]
		mov	ds, cx
		jz	short dbRet
		test	ds:_Bits, 4	; mask.subd
		jz	short dbNameExt
		call	build_dir_string
		mov	di, offset BWDBUF
		test	ds:_Bits, 10h
		jz	short dbare1
		mov	si, di
		call	LowercaseString

dbare1:					; ...
		xor	al, al
		mov	cx, 0FFFFh
		cld
		repne scasb
		dec	di
		cmp	byte ptr es:[di-1], '\'
		jz	short dbare2
		mov	ax, '\'
		stosw

dbare2:					; ...
		mov	ds:string_ptr_2, offset	BWDBUF
		mov	dx, offset string_buf_ptr
		call	std_printf

dbNameExt:				; ...
		call	DisplayDotForm
		call	CRLF2
		call	UseLine

dbRet:					; ...
		retn
DisplayBare	endp


; ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ S U B	R O U T	I N E ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ


DisplayDotForm	proc near		; ...
		push	ds
		push	es
		mov	ax, cs:TPA
		mov	ds, ax
		assume ds:RESGROUP
		mov	es, ax
		assume es:RESGROUP
		mov	di, bx
		add	di, 8		; filename + size filename - 1
		mov	cx, 8		; size filename	= 8 ; size of EntryStruc.filename
		mov	al, 20h	; ' '
		std
		repe scasb
		inc	cx
		mov	dx, cx
		mov	si, bx
		add	si, 1		; add si,EntryStruc.filename

NextNameChar:				; ...
		cld
		lodsb
		test	ss:_Bits, 10h	; mask.lcase
		jz	short ddf1
		call	LowerCase

ddf1:					; ...
		call	PRINT_CHAR
		loop	NextNameChar
		mov	di, bx
		add	di, 11		; fileext + size fileext - 1
		mov	cx, 3		; size fileext
		mov	al, 20h	; ' '
		std
		repe scasb
		jz	short ddDone
		inc	cx
		add	dx, cx
		inc	dx
		mov	al, '.'
		call	PRINT_CHAR
		mov	si, bx
		add	si, 9		; EntryStruc.fileext

NextExtChar:				; ...
		cld
		lodsb
		test	cs:_Bits, 10h
		jz	short ddf2
		call	LowerCase

ddf2:					; ...
		call	PRINT_CHAR
		loop	NextExtChar

ddDone:					; ...
		pop	es
		assume es:nothing
		pop	ds
		assume ds:nothing
		cld
		retn
DisplayDotForm	endp


; ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ S U B	R O U T	I N E ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ


DisplayFile	proc near		; ...
		test	ds:_Bits, 8	; mask.bare
		jz	short dfNorm
		call	DisplayBare
		jmp	short dfRet
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

dfNorm:					; ...
		call	DisplayNext
		test	ds:_Bits, 1	; mask.wide
		jz	short dfFul
		call	DisplayWide
		jmp	short dfCnt
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

dfFul:					; ...
		call	DisplayName
		call	DisplayTheRest

dfCnt:					; ...
		call	CountFile

dfRet:					; ...
		retn
DisplayFile	endp


; ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ S U B	R O U T	I N E ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ


DisplayHeader	proc near		; ...
		test	ds:_Bits, 8	; mask.bare
		jnz	short dhRet
		test	ds:_Bits, 4	; mask.subd
		jz	short dhNorm
		call	CRLF2
		call	UseLine
		jmp	short dhCom
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

dhNorm:					; ...
		mov	al, 20h	; ' '
		call	PRINT_CHAR

dhCom:					; ...
		call	build_dir_string
		mov	dx, offset dirhead_ptr
		call	std_printf
		call	UseLine
		call	CRLF2
		call	UseLine

dhRet:					; ...
		retn
DisplayHeader	endp


; ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ S U B	R O U T	I N E ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ


DisplayName	proc near		; ...
		push	ds
		mov	ds, ds:TPA
		mov	si, bx
		add	si, 1		; EntryStruc.filename
		mov	di, offset CHARBUF
		mov	cx, 8
		cld
		rep movsb
		mov	al, 20h	; ' '
		stosb
		mov	cx, 3
		rep movsb
		xor	al, al
		stosb
		pop	ds
		test	ds:_Bits, 10h	; mask.lcase
		jz	short dn1
		mov	si, offset CHARBUF
		call	LowercaseString

dn1:					; ...
		mov	ds:string_ptr_2, offset	CHARBUF
		mov	dx, offset string_buf_ptr
		call	std_printf
		retn
DisplayName	endp


; ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ S U B	R O U T	I N E ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ


DisplayNext	proc near		; ...
		cmp	ds:FileCnt, 0
		jz	short dn1st
		cmp	ds:LeftOnLine, 0
		jle	short dnEol
		mov	dx, offset tab_ptr
		call	std_printf
		jmp	short dnDone
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

dnEol:					; ...
		call	CRLF2
		call	UseLine

dn1st:					; ...
		mov	al, ds:PerLine
		mov	ds:LeftOnLine, al

dnDone:					; ...
		dec	ds:LeftOnLine
		retn
DisplayNext	endp


; ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ S U B	R O U T	I N E ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ


DisplayTheRest	proc near		; ...
		push	es
		mov	es, ds:TPA
		mov	bp, bx
		test	byte ptr es:[bp+12], 10h ; [es:bp+EntryStruc.fileattr],ATTR_DIRECTORY
		jz	short drNonDir
		mov	dx, offset dmes_ptr
		call	std_printf
		jmp	short drCom
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

drNonDir:				; ...
		mov	dx, es:[bp+17]	; [es:bp+EntryStruc.filesize]
		mov	ds:File_Size_Low, dx
		mov	dx, es:[bp+19]	; [es:bp+EntryStruc.filesize+2]
		mov	ds:File_Size_High, dx
		mov	dx, offset disp_file_size_ptr
		call	std_printf

drCom:					; ...
		mov	ax, es:[bp+15]	; [es:bp+EntryStruc.filedate]
		or	ax, ax
		jz	short drDone
		mov	bx, ax
		and	ax, 1Fh
		mov	dl, al
		mov	ax, bx
		mov	cl, 5
		shr	ax, cl
		and	al, 0Fh
		mov	dh, al
		mov	cl, bh
		shr	cl, 1
		xor	ch, ch
		add	cx, 80
		cmp	cl, 100
		jb	short dr_1
		sub	cl, 100

dr_1:					; ...
		xchg	dh, dl
		mov	ds:DirDat_Yr, cx
		mov	ds:DirDat_Mo_Day, dx
		mov	cx, es:[bp+13]
		jcxz	short drPrint
		shr	cx, 1
		shr	cx, 1
		shr	cx, 1
		shr	cl, 1
		shr	cl, 1
		xchg	ch, cl
		mov	ds:DirTim_Hr_Min, cx

drPrint:				; ...
		mov	dx, offset dirdattim_ptr
		call	std_printf

drDone:					; ...
		pop	es
		mov	bx, bp
		retn
