		pop	ds
		assume ds:TRANGROUP
		push	es
		call	FIND_PROMPT
		jb	short PP0
		cmp	byte ptr es:[di], 0
		jnz	short PP1

PP0:					; ...
		call	PRINT_DRIVE
		mov	al, '>'         ; SYM
		call	PRINT_CHAR
		jmp	short PP5
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

PP1:					; ...
		mov	al, es:[di]
		inc	di
		or	al, al
		jz	short PP5
