		xor	al, al
		mov	di, offset SRCXNAME
		stosb
		dec	di
		call	PGETARG
		jz	short disppath
		cmp	al, ';'         ; 3Bh
		jnz	short pathslp
		inc	si
		jmp	short scan_white
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

pathslp:				; ...
		lodsb
		cmp	al, 0Dh		; END_OF_LINE_IN
		jz	short path_eol
		call	testkanj
		jz	short notkanj2
		stosb
		lodsb

path_hold:				; ...
		stosb
		jmp	short pathslp
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

notkanj2:				; ...
		call	UPCONV
		cmp	al, ';'         ; 3Bh ; semicolon
		jz	short path_hold
		call	DELIM
		jnz	short path_hold

scan_white:				; ...
		lodsb
		cmp	al, 0Dh
		jz	short path_eol
		cmp	al, 20h	; ' '
		jz	short scan_white
		cmp	al, 9
		jz	short scan_white
		mov	dx, offset extend_buf_ptr
		mov	extend_buf_ptr,	1 ; MoreArgs_Ptr
		mov	msg_disp_class,	2 ; parse_msg_class
		jmp	cerror
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

path_eol:				; ...
		xor	al, al
		stosb
		call	find_path
		call	delete_path
		call	scan_double_null
		call	move_name
		mov	si, offset SRCXNAME

store_path:				; ...
		lodsb
		cmp	al, 0
		jz	short got_paths
		call	store_char
		jmp	short store_path
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

