		retn
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

disppath:				; ...
		call	find_path
		call	print_path
		call	CRLF2
		retn

; ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ S U B	R O U T	I N E ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ


print_path	proc near		; ...
		cmp	byte ptr es:[di], 0
		jnz	short path1

path0:					; ...
		mov	dx, offset NULLPATH_PTR
		push	cs
		pop	es
		assume es:TRANGROUP
		push	cs
		pop	ds
		jmp	std_printf
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

path1:					; ...
		push	es
		pop	ds
		sub	di, 5
		mov	si, di
		call	SCASB2
		cmp	cx, 0FFh	; 255
		jz	short path0
		push	cs
		pop	es
		mov	di, offset Arg_Buf
		mov	dx, 100h	; 256
		sub	dx, cx
		xchg	dx, cx
