PRESCAN		proc near		; ...
		xor	cx, cx
		mov	es, RESSEG
		mov	si, (offset COMBUF+2)
		mov	di, si

COUNTQUOTES:				; ...
		lodsb
		cmp	al, 22h	; '"'
		jnz	short COUNTEND
		inc	ch
		jmp	short COUNTQUOTES
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

COUNTEND:				; ...
		cmp	al, 0Dh		; 13
		jnz	short COUNTQUOTES
		push	cx
		mov	si, di

KanjiScan:				; ...
		lodsb
		call	testkanj
		jz	short KanjiQuote
		mov	ah, al
		lodsb
		cmp	ax, 2020h	; DB_SPACE
		jnz	short KanjiScan
		mov	word ptr [si-2], 2020h
		jmp	short KanjiScan
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

KanjiQuote:				; ...
		cmp	al, 22h	; '"'
		jnz	short KanjiEnd
		dec	ch
		jz	short KanjiScan

KanjiQuoteLoop:				; ...
		lodsb
		cmp	al, 22h	; '"'
		jnz	short KanjiQuoteLoop
		dec	ch
		jmp	short KanjiScan
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

KanjiEnd:				; ...
		cmp	al, 0Dh
		jnz	short KanjiScan
		pop	cx
		mov	si, di

PRESCANLP:				; ...
		lodsb
		call	testkanj
		jz	short NOTKANJ6
		mov	[di], al
		inc	di
		lodsb
		mov	[di], al
		inc	di
		inc	cl
		inc	cl
		jmp	short PRESCANLP
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

NOTKANJ6:				; ...
		cmp	al, 22h	; '"'
		jnz	short TRYGREATER
		dec	ch
		jz	short TRYGREATER

QLOOP:					; ...
		mov	[di], al
		inc	di
		inc	cl
		lodsb
		cmp	al, 22h	; '"'
		jnz	short QLOOP
		dec	ch

TRYGREATER:				; ...
		cmp	al, 3Eh	; '>'   ; rabracket
		jnz	short NOOUT
		cmp	[si], al
		jnz	short NOAPPND
		lodsb
		inc	es:Re_Out_App

NOAPPND:				; ...
		call	scanoff
		cmp	al, 3Ch	; '<'   ; labracket
		jz	short REOUT_ERRSET
		cmp	al, 0Dh
		jnz	short GOTREOFIL

REOUT_ERRSET:				; ...
		mov	byte ptr [di], 0Dh
		mov	word ptr es:Re_OutStr, 9
		jmp	PRESCANEND
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

GOTREOFIL:				; ...
		push	di
		mov	di, offset Re_OutStr
		mov	bx, di
		push	es

SETREOUTSTR:				; ...
		push	cx
		mov	cx, 77		; 64+13

SETREOUTSTR_LOOP:			; ...
		lodsb
		cmp	al, 0Dh
		jz	short GOTRESTR_J
		call	DELIM
		jz	short GOTRESTR_J
		cmp	al, SWITCHAR
		jz	short GOTRESTR_J
		cmp	al, 22h	; '"'   ; quote
		jz	short PIPEERRSYNJ5_J
		cmp	al, 3Ch	; '<'   ; labracket
		jz	short ABRACKET_TERM
		cmp	al, 3Eh	; '>'   ; rabracket
		jnz	short NO_ABRACKET

ABRACKET_TERM:				; ...
		dec	si
		mov	al, 20h	; ' '   ; BLANK

GOTRESTR_J:				; ...
		pop	cx
		jmp	short GOTRESTR
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

NO_ABRACKET:				; ...
		stosb
		loop	SETREOUTSTR_LOOP
		jmp	short GOTRESTR_J
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

PIPEERRSYNJ5_J:				; ...
		pop	cx
		jmp	short PIPEERRSYNJ5
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

NOOUT:					; ...
		cmp	al, 3Ch	; '<'   ; labracket
		jnz	short CHKPIPE
		mov	bx, si
		call	scanoff
		cmp	al, 3Eh	; '>'   ; rabracket
		jz	short REIN_ERRSET
		cmp	al, 0Dh
		jnz	short GOTREIFIL

REIN_ERRSET:				; ...
		mov	byte ptr [di], 0Dh
		mov	word ptr RE_INSTR, 9
		jmp	short PRESCANEND
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

GOTREIFIL:				; ...
		push	di
		mov	di, offset RE_INSTR
		mov	bx, di
		push	es
		push	cs
		pop	es
		assume es:TRANGROUP
		jmp	short SETREOUTSTR
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

CHKPIPE:				; ...
		mov	ah, al
		cmp	ah, 7Ch	; '|'   ; ALTPIPECHR
		jz	short ISPIPE3
		cmp	ah, 7Ch	; '|'   ; vbar
		jnz	short CONTPRESCAN

ISPIPE3:				; ...
		cmp	es:PipeFlag, 0
		jnz	short NOECHOPUSH
		shl	es:EchoFlag, 1	; shl byte [es:EchoFlag],1

NOECHOPUSH:				; ...
		inc	es:PipeFlag
		call	scanoff
		cmp	al, 0Dh
		jz	short PIPEERRSYNJ5
		cmp	al, 7Ch	; '|'   ; ALTPIPECHR
		jz	short PIPEERRSYNJ5
		cmp	al, 7Ch	; '|'   ; vbar
		jnz	short CONTPRESCAN

PIPEERRSYNJ5:				; ...
		push	es
		pop	ds
		jmp	PIPEERRSYN
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

GOTRESTR:				; ...
		xchg	ah, al
		mov	al, 3Ah	; ':'
		sub	bx, di
		cmp	bx, 0FFFFh	; -1
		jz	short NOTRAILCOL
		cmp	es:[di-1], al
		jnz	short NOTRAILCOL
		dec	di

NOTRAILCOL:				; ...
		xor	al, al
		stosb
		pop	es
		assume es:nothing
		pop	di

CONTPRESCAN:				; ...
		mov	[di], ah
		inc	di
		cmp	ah, 0Dh
		jz	short PRESCANEND
		inc	cl
		jmp	PRESCANLP
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

PRESCANEND:				; ...
		cmp	es:PipeFlag, 0
		jz	short ISNOPIPE
		mov	di, 3C0h	; PipeStr ; RESGROUP:EndInit+160
		mov	es:3BEh, di	; [es:PIPEPTR],di
					; (RESGROUP:EndInit+158)
		mov	si, (offset COMBUF+2)
		call	scanoff

PIPESETLP:				; ...
		lodsb
		stosb
		cmp	al, 0Dh
		jnz	short PIPESETLP

ISNOPIPE:				; ...
		mov	COMBUF+1, cl
		cmp	es:PipeFlag, 0
		push	cs
		pop	es
		assume es:TRANGROUP
		retn
PRESCAN		endp


; ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ S U B	R O U T	I N E ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ


cmd_copy	proc near		; ...
		mov	si, (offset COMBUF+2)
		call	scanoff
		add	si, PathPos
		mov	di, 81h
		xor	cx, cx

cmdcopy:				; ...
		lodsb
		stosb
		cmp	al, 0Dh
		jz	short copy_done
		inc	cx
		jmp	short cmdcopy
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

copy_done:				; ...
		mov	ds:80h,	cl
		retn
cmd_copy	endp


; ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ S U B	R O U T	I N E ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ


test_append	proc near		; ...
		mov	bx, offset COMBUF
		mov	si, offset IDLEN
		mov	dx, 0FFFFh	; -1
		mov	ax, 0AE00h
		int	2Fh		; - Multiplex -	DOS 3.3+ internal - INSTALLABLE	COMMAND	- INSTALL CHECK
					; DX = FFFFh, DS:BX -> command line
					; Return: AL = FFh if this command is a	TSR extension to COMMAND.COM
					; AL = 00h if the command should be executed as	usual
		cmp	al, 0
		retn
test_append	endp


; ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ S U B	R O U T	I N E ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ


SETPATH		proc near		; ...
		mov	ax, PathCnt
		mov	si, PathPos

GETPATH:
		mov	DestInfo, 0
		mov	DestIsDir, 0
		mov	si, PathPos
		mov	cx, PathCnt
		mov	dx, si
		jcxz	short PATHDONE
		push	cx
		push	si
		call	SWITCH
		mov	PathSw,	ax
		pop	bx
		sub	bx, si
		pop	cx
		add	cx, bx
		mov	dx, si

SKIPPATH:				; ...
		mov	KPARSE,	0

SKIPPATH2:				; ...
		jcxz	short PATHDONE
		dec	cx
		lodsb
		call	testkanj
		jz	short TESTPPSEP
		dec	cx
		inc	si
		inc	KPARSE
		jmp	short SKIPPATH2
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

TESTPPSEP:				; ...
		call	pathchrcmp
		jnz	short TESTPMETA
		inc	DestIsDir

TESTPMETA:				; ...
		cmp	al, 3Fh	; '?'
		jnz	short TESTPSTAR
		or	DestInfo, 2

TESTPSTAR:				; ...
		cmp	al, 2Ah	; '*'
		jnz	short TESTPDELIM
		or	DestInfo, 2

TESTPDELIM:				; ...
		call	DELIM
		jz	short PATHDONEDEC
		cmp	al, SWITCHAR
		jnz	short SKIPPATH

PATHDONEDEC:				; ...
		dec	si

PATHDONE:				; ...
		xor	al, al
		xchg	al, [si]
		inc	si
		cmp	al, 0Dh
		jnz	short NOPSTORE
		mov	[si], al

NOPSTORE:				; ...
		mov	PathPos, si
		mov	PathCnt, cx

SETPATH_RETN:				; ...
		retn
SETPATH		endp


; ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ S U B	R O U T	I N E ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ


PGETARG		proc near		; ...
		mov	si, 80h
		lodsb
		or	al, al
		jz	short SETPATH_RETN
		call	PSCANOFF
		cmp	al, 0Dh
		retn
PGETARG		endp


; ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ S U B	R O U T	I N E ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ


PSCANOFF	proc near		; ...
		lodsb
		call	DELIM
		jnz	short PSCANOFFD
		cmp	al, ';'         ; 3Bh
		jnz	short PSCANOFF

PSCANOFFD:				; ...
		dec	si
		retn
PSCANOFF	endp


; ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ S U B	R O U T	I N E ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ


IOSET		proc near		; ...
		push	ds
		push	dx
		push	ax
		push	bx
		push	cx
		mov	ds, cs:RESSEG
		assume ds:nothing
		cmp	ds:PipeFlag, 0
		jnz	short NOREDIR
		test	ds:IfFlag, 0FFh
		jnz	short NOREDIR
		call	TESTDOREIN
		call	TESTDOREOUT

NOREDIR:				; ...
		pop	cx
		pop	bx
		pop	ax
		pop	dx
		pop	ds

IOSET_RETN:				; ...
		retn
IOSET		endp


; ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ S U B	R O U T	I N E ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ


TESTDOREIN	proc near		; ...
		cmp	cs:RE_INSTR, 0
		jz	short IOSET_RETN
		push	ds
		push	cs
		pop	ds
		assume ds:TRANGROUP
		mov	dx, offset RE_INSTR
		mov	ax, 3D00h	; OPEN*256
		mov	bx, ax
		int	21h		; DOS -	2+ - OPEN DISK FILE WITH HANDLE
					; DS:DX	-> ASCIZ filename
					; AL = access mode
					; 0 - read
		pop	ds
		assume ds:nothing
		jb	short REDIRERR
		mov	bx, ax
		mov	al, 0FFh
		xchg	al, [bx+18h]	; [bx+PDB.JFN_TABLE]
		mov	ds:18h,	al	; [PDB.JFN_TABLE]
		retn
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

REDIRERR:				; ...
		push	cs
		pop	ds
		assume ds:TRANGROUP
		call	TriageError
		cmp	ax, 65
		jz	short _CERRORJ
		cmp	bh, 3Dh		; OPEN
		jz	short OpenError
		mov	dx, offset FULLDIR_PTR

_CERRORJ:				; ...
		jmp	cerror
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

OpenError:				; ...
		mov	cs:msg_disp_class, 1 ; ext_msg_class
		mov	dx, offset extend_buf_ptr
		mov	cs:extend_buf_ptr, ax
		jmp	cerror
TESTDOREIN	endp


; ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ S U B	R O U T	I N E ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ


TESTDOREOUT	proc near		; ...
		cmp	ds:Re_OutStr, 0
		jnz	short REOUTEXISTS
		jmp	NOREOUT
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

REOUTEXISTS:				; ...
		cmp	ds:Re_Out_App, 0
		jz	short REOUTCRT
		mov	dx, offset Re_OutStr
		mov	ax, 3D02h
		push	ax
		int	21h		; DOS -	2+ - OPEN DISK FILE WITH HANDLE
					; DS:DX	-> ASCIZ filename
					; AL = access mode
					; 2 - read & write
		pop	bx
		jb	short OpenWriteError
		mov	bx, ax
		mov	ax, 4400h
		int	21h		; DOS -	2+ - IOCTL - GET DEVICE	INFORMATION
					; BX = file or device handle
		test	dl, 80h
		jnz	short SET_REOUT
		mov	ax, 4202h
		mov	cx, 0FFFFh	; -1
		mov	dx, cx
		int	21h		; DOS -	2+ - MOVE FILE READ/WRITE POINTER (LSEEK)
					; AL = method: offset from end of file
		push	cs
		pop	ds
		mov	ax, 3F00h
		mov	cx, 1
		mov	dx, offset One_Char_Val
		int	21h		; DOS -	2+ - READ FROM FILE WITH HANDLE
					; BX = file handle, CX = number	of bytes to read
					; DS:DX	-> buffer
		jb	short OpenWriteError
		cmp	ax, cx
		jnz	short reout_0_length
		cmp	One_Char_Val, 1Ah
		mov	ds, RESSEG
		assume ds:nothing
		jnz	short SET_REOUT
		mov	ax, 4201h
		mov	cx, 0FFFFh	; -1
		mov	dx, cx
		int	21h		; DOS -	2+ - MOVE FILE READ/WRITE POINTER (LSEEK)
					; AL = method: offset from present location
		jmp	short SET_REOUT
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

reout_0_length:				; ...
		mov	ds, cs:RESSEG
		mov	ax, 4200h
		xor	cx, cx
		mov	dx, cx
		int	21h		; DOS -	2+ - MOVE FILE READ/WRITE POINTER (LSEEK)
					; AL = method: offset from beginning of	file
		jmp	short SET_REOUT
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

OpenWriteError:				; ...
		cmp	ax, 5		; ERROR_ACCESS_DENIED
		stc
		jnz	short REOUTCRT
		jmp	REDIRERR
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

REOUTCRT:				; ...
		mov	dx, offset Re_OutStr
		xor	cx, cx
		mov	ah, 3Ch
		push	ax
		int	21h		; DOS -	2+ - CREATE A FILE WITH	HANDLE (CREAT)
					; CX = attributes for file
					; DS:DX	-> ASCIZ filename (may include drive and path)
		pop	bx
		jnb	short NOREDIRERR
		jmp	REDIRERR
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

NOREDIRERR:				; ...
		mov	bx, ax

SET_REOUT:				; ...
		mov	al, 0FFh
		xchg	al, [bx+18h]	; [bx+PDB.JFN_TABLE]
		mov	ds:19h,	al	; [PDB.JFN_TABLE+1],al

NOREOUT:				; ...
		retn
TESTDOREOUT	endp


; ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ S U B	R O U T	I N E ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ


dstrlen		proc near		; ...
		push	ax
		xor	cx, cx
		cld

dloop:					; ...
		lodsb
		inc	cx
		or	al, al
		jnz	short dloop
		sub	si, cx
		pop	ax

TRIAGEERR_RETN:				; ...
		retn
dstrlen		endp


; ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ S U B	R O U T	I N E ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ


TriageError	proc near		; ...
		jnb	short TRIAGEERR_RETN
		pushf
		push	bx
		push	cx
		push	si
		push	di
		push	bp
		push	es
		push	ds
		push	ax
		push	dx
		mov	ah, 59h
		int	21h		; DOS -	3+ - GET EXTENDED ERROR	CODE
					; BX = version code (0000h for DOS 3.x)
		pop	cx
		pop	bx
		mov	dx, offset ACCDEN_PTR
		cmp	ax, 65
		jz	short NoMove
		mov	ax, bx
		mov	dx, cx

NoMove:					; ...
		pop	ds
		pop	es
		assume es:nothing
		pop	bp
		pop	di
		pop	si
		pop	cx
		pop	bx
		popf
		retn
TriageError	endp

; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

Triage_Init:				; ...
		call	TriageError
		retf

