PIPEDEL		proc near		; ...
		push	ds
		push	dx
		mov	ds, cs:RESSEG
		mov	dx, 320h	; offset Pipe1 = offset	RESGROUP:EndInit
		mov	ah, 41h
		int	21h		; DOS -	2+ - DELETE A FILE (UNLINK)
					; DS:DX	-> ASCIZ pathname of file to delete (no	wildcards allowed)
		mov	dx, 36Fh	; offset Pipe2 = offset	RESGROUP:EndInit+79
		mov	ah, 41h
		int	21h		; DOS -	2+ - DELETE A FILE (UNLINK)
					; DS:DX	-> ASCIZ pathname of file to delete (no	wildcards allowed)
		pop	dx
		call	PipeOff
		mov	ds:PipeFiles, 0
		pop	ds
		retn
PIPEDEL		endp

; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ
; START	OF FUNCTION CHUNK FOR PROMPTBAT

PIPEERRSYN:				; ...
		mov	dx, offset SYNTMES_PTR
		call	PIPEDEL
		push	cs
		pop	ds
		assume ds:TRANGROUP
		jmp	cerror
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

PIPERR:					; ...
		pushf
		call	TriageError
		push	ax
		push	dx
		mov	dx, offset PIPEEMES_PTR
		call	PIPEDEL
		push	cs
		pop	ds
		call	std_eprintf
		pop	dx
		pop	ax
		popf
		cmp	ax, 65
		jnz	short TCOMMANDJ
		jmp	cerror
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

TCOMMANDJ:				; ...
		jmp	TCOMMAND
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

PIPEPROCSTRT:				; ...
		mov	ds, RESSEG
		assume ds:nothing
		inc	ds:PipeFiles
		push	es
		push	di
		push	ds
		push	si
		push	ds
		push	es
		pop	ds
		mov	si, offset TempVarName ; "TEMP="
		call	find_name_in_environment
		pop	ds
		jb	short no_temp_path
		push	ds
		push	es
		pop	ds
		pop	es
		mov	si, di
		call	skip_white
		call	copy_pipe_path
		push	es
		pop	ds
		mov	dx, offset EndInit ; offset DATARES:Pipe1 = RESGROUP:EndInit
		mov	ax, 4300h
		int	21h		; DOS -	2+ - GET FILE ATTRIBUTES
					; DS:DX	-> ASCIZ file name or directory
					; name without trailing	slash
		jb	short no_temp_path
		test	cx, 10h
		jnz	short no_temp_path
		stc

no_temp_path:				; ...
		pop	si
		pop	ds
		pop	di
		pop	es
		jnb	short crt_temp
		mov	ah, '.'
		mov	ds:320h, ah	; offset DATARES:Pipe1 = RESGROUP:EndInit
		mov	ds:36Fh, ah	; offset DATARES:Pipe2 = RESGROUP:EndInit+79
		xor	ah, ah
		mov	ds:321h, ah	; offset DATARES:Pipe1+1 = RESGROUP:EndInit+1
		mov	ds:370h, ah	; offset DATARES:Pipe2+1 = RESGROUP:EndInit+80

crt_temp:				; ...
		mov	dx, 320h	; offset DATARES:Pipe1 = offset	RESGROUP:EndInit
		xor	cx, cx
		mov	ah, 5Ah
		int	21h		; DOS -	3+ - CREATE UNIQUE FILE
					; DS:DX	-> ASCIZ directory path	name ending with a '' + 13 bytes to
					; receive generated filename
					; CX = file attributes (only bits 0,1,2,5 may be set)
		jnb	short pps0
		jmp	short PIPERR
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

pps0:					; ...
		mov	bx, ax
		mov	ah, 3Eh
		int	21h		; DOS -	2+ - CLOSE A FILE WITH HANDLE
					; BX = file handle
		mov	dx, 36Fh	; offset DATARES:Pipe2 = offset	RESGROUP:EndInit+79
		mov	ah, 5Ah
		int	21h		; DOS -	3+ - CREATE UNIQUE FILE
					; DS:DX	-> ASCIZ directory path	name ending with a '' + 13 bytes to
					; receive generated filename
					; CX = file attributes (only bits 0,1,2,5 may be set)
		jnb	short pps1
		jmp	PIPERR
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

pps1:					; ...
		mov	bx, ax
		mov	ah, 3Eh
		int	21h		; DOS -	2+ - CLOSE A FILE WITH HANDLE
					; BX = file handle
		call	TESTDOREIN
		mov	si, ds:3BEh	; offset DATARES:PipePtr = offset RESGROUP:EndInit+158
		cmp	ds:SingleCom, 0FFFFh
		jnz	short NOSINGP
		mov	ds:SingleCom, 0F000h

NOSINGP:				; ...
		jmp	short FIRSTPIPE
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

PIPEPROC:				; ...
		and	ds:EchoFlag, 0FEh
		mov	si, ds:3BEh	; offset DATARES:PipePtr = offset RESGROUP:EndInit+158
		lodsb
		cmp	al, 7Ch	; '|'   ; ALTPIPECHR
		jz	short ISPIPE1
		cmp	al, 7Ch	; '|'   ; VBAR
		jz	short ISPIPE1
		jmp	PIPEEND
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

ISPIPE1:				; ...
		mov	dx, ds:InPipePtr
		mov	ax, 3D00h
		int	21h		; DOS -	2+ - OPEN DISK FILE WITH HANDLE
					; DS:DX	-> ASCIZ filename
					; AL = access mode
					; 0 - read

PIPEERRJ:				; ...
		jnb	short NO_PIPEERR
		jmp	PIPERR
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

NO_PIPEERR:				; ...
		mov	bx, ax
		mov	al, 0FFh
		xchg	al, [bx+18h]	; [bx+PDB.JFN_TABLE]
		mov	ds:18h,	al	; [PDB.JFN_TABLE],al  ;	Redirect

FIRSTPIPE:				; ...
		mov	di, (offset COMBUF+2)
		xor	cx, cx
		cmp	byte ptr [si], 0Dh ; '|<CR>'
		jnz	short PIPEOK1

PIPEERRSYNJ:				; ...
		jmp	PIPEERRSYN
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

PIPEOK1:				; ...
		mov	al, 7Ch	; '|'
		cmp	[si], al
		jz	short PIPEERRSYNJ
		cmp	byte ptr [si], 7Ch ; '|'
		jz	short PIPEERRSYNJ

PIPECOMLP:				; ...
		lodsb
		stosb
		call	testkanj
		jz	short NOTKANJ5
		movsb
		inc	cx
		inc	cx
		jmp	short PIPECOMLP
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

NOTKANJ5:				; ...
		cmp	al, 0Dh
		jz	short LASTPIPE
		inc	cx
		cmp	al, 7Ch	; '|'
		jz	short ISPIPE2
		cmp	al, 7Ch	; '|'
		jnz	short PIPECOMLP

ISPIPE2:				; ...
		mov	byte ptr es:[di-1], 0Dh
		dec	cx
		mov	es:COMBUF+1, cl
		dec	si
		mov	ds:3BEh, si	; [PipePtr] = [EndInit+158]
		mov	dx, ds:OutPipePtr
		push	cx
		xor	cx, cx
		mov	ax, 3C00h
		int	21h		; DOS -	2+ - CREATE A FILE WITH	HANDLE (CREAT)
					; CX = attributes for file
					; DS:DX	-> ASCIZ filename (may include drive and path)
		pop	cx
		jb	short PIPEERRJ
		mov	bx, ax
		mov	al, 0FFh
		xchg	al, [bx+18h]	; [bx+PDB.JFN_TABLE]
		mov	ds:19h,	al	; [PDB.JFN_TABLE+1]
		xchg	dx, ds:InPipePtr
		mov	ds:OutPipePtr, dx
		jmp	short PIPECOM
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

LASTPIPE:				; ...
		mov	es:COMBUF+1, cl
		dec	si
		mov	ds:3BEh, si	; [PipePtr] = [EndInit+158]
		call	TESTDOREOUT

PIPECOM:				; ...
		push	cs
		pop	ds
		assume ds:TRANGROUP
		jmp	NOPIPEPROC
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

PIPEEND:				; ...
		call	PIPEDEL
		cmp	ds:SingleCom, 0F000h
		jnz	short NOSINGP2
		mov	ds:SingleCom, 0FFFFh ; -1

NOSINGP2:				; ...
		jmp	TCOMMAND
; END OF FUNCTION CHUNK	FOR PROMPTBAT

; ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ S U B	R O U T	I N E ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ


DATINIT		proc far		; ...
		mov	cs:RESSEG, ds
		push	es
		push	ds
		mov	ax, cs
		mov	es, ax
		assume es:TRANGROUP
		mov	ds, ax
		call	TSYSLOADMSG
		mov	dx, offset STACK ; mov dx, offset INTERNATVARS
		mov	ax, 3800h
		int	21h		; DOS -	2+ - GET COUNTRY-DEPENDENT INFORMATION
					; get current-country info
					; DS:DX	-> buffer for returned info
		mov	word ptr TRANSTART+81h,	0Dh
		mov	COMBUF,	128
		mov	word ptr COMBUF+1, 0D01h
		call	DATE
		call	CTIME
		pop	ds
		assume ds:nothing
		pop	es
		assume es:nothing
		retf
DATINIT		endp


; ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ S U B	R O U T	I N E ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ


DATE		proc near		; ...
		mov	si, 81h
		mov	di, offset PARSE_DATE
		xor	cx, cx
		xor	dx, dx
		call	cmd_parse
		cmp	ax, 0FFFFh
		jz	short PRMTDAT
		cmp	ax, 0
		jnz	short DATERR
		jmp	short COMDAT
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

PRMTDAT:				; ...
		call	GetDate
		xchg	dh, dl
		mov	ds:CurDat_yr, cx
		mov	ds:CurDat_mo_day, dx
		mov	dx, offset CurDat_Ptr
		call	std_printf

GET_NEW_DATE:				; ...
		call	GETDAT
		cmp	ax, 0FFFFh	; END_OF_LINE (-1)
		jz	short date_end
		cmp	ax, 0
		jnz	short DATERR

COMDAT:					; ...
		mov	cx, ds:DATE_YEAR
		mov	dh, ds:DATE_MONTH
		mov	dl, ds:DATE_DAY
		push	cx
		push	dx
		mov	cx, 1
		xor	dx, dx
		call	cmd_parse
		cmp	al, 0FFh	; END_OF_LINE (-1)
		pop	dx
		pop	cx
		jnz	short DATERR
		mov	ah, 2Bh
		int	21h		; DOS -	SET CURRENT DATE
					; DL = day, DH = month,	CX = year
					; Return: AL = 00h if no error /= FFh if bad value sent	to routine
		or	al, al
		jnz	short DATERR

date_end:				; ...
		retn
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

DATERR:					; ...
		call	CRLF2
		mov	dx, offset BADDAT_PTR
		call	std_printf
		jmp	short GET_NEW_DATE
DATE		endp


; ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ S U B	R O U T	I N E ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ


CTIME		proc near		; ...
		mov	si, 81h
		mov	di, offset PARSE_TIME
		xor	cx, cx
		xor	dx, dx
		call	cmd_parse
		cmp	ax, 0FFFFh	; END_OF_LINE
		jz	short PRMTTIM
		cmp	ax, 0		; RESULT_NO_ERROR
		jnz	short TIMERR
		jmp	short COMTIM
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

PRMTTIM:				; ...
		mov	ah, 2Ch
		int	21h		; DOS -	GET CURRENT TIME
					; Return: CH = hours, CL = minutes, DH = seconds
					; DL = hundredths of seconds
		xchg	ch, cl
		xchg	dh, dl
		mov	ds:CurTim_hr_min, cx
		mov	ds:CurTim_Sec_hn, dx
		mov	dx, offset CurTim_Ptr
		call	std_printf

GET_NEW_TIME:				; ...
		call	GETTIM
		cmp	ax, 0FFFFh	; -1 ; END_OF_LINE
		jz	short time_end
		cmp	ax, 0		; 0 ; RESULT_NO_ERROR
		jnz	short TIMERR

COMTIM:					; ...
		mov	ch, ds:TIME_HOUR
		mov	cl, ds:TIME_MINUTES
		mov	dh, ds:TIME_SECONDS
		mov	dl, ds:TIME_FRACTION
		push	cx
		push	dx
		mov	cx, 1
		xor	dx, dx
		call	cmd_parse
		cmp	al, 0FFh	; -1 ; END_OF_LINE
		pop	dx
		pop	cx
		jnz	short TIMERR
		mov	ah, 2Dh
		int	21h		; DOS -	SET CURRENT TIME
					; CH = hours, CL = minutes, DH = seconds, DL = hundredths of seconds
					; Return: AL = 00h if no error / = FFh if bad value sent to routine
		or	al, al
		jnz	short TIMERR

time_end:				; ...
		retn
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

TIMERR:					; ...
		call	CRLF2
		mov	dx, offset BadTim_Ptr
		call	std_printf
		jmp	short GET_NEW_TIME
CTIME		endp


; ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ S U B	R O U T	I N E ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ


