; ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ S U B	R O U T	I N E ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ


GETDAT		proc near		; ...
		mov	ax, 3800h
		mov	dx, 5Ch
		int	21h		; DOS -	2+ - GET COUNTRY-DEPENDENT INFORMATION
					; get current-country info
					; DS:DX	-> buffer for returned info
		mov	si, dx
		lodsw
		mov	dx, cs:usadat_Ptr
		dec	ax
		js	short printformat
		mov	dx, cs:eurdat_ptr
		jz	short printformat
		mov	dx, cs:japdat_ptr

printformat:				; ...
		mov	ax, dx
		mov	dh, 0FFh	; util_msg_class
		call	TSYSGETMSG
		mov	cs:NewDat_Format, si
		mov	dx, offset NewDat_Ptr
		call	std_printf
		mov	cs:NewDat_Format, 0
		mov	ah, 0Ah		; STD_CON_STRING_INPUT
		mov	dx, offset COMBUF
		mov	cx, 2		; INITSPECIAL
		call	SetInitFlag
		int	21h		; DOS -
		xor	cx, cx
		call	SetInitFlag
		call	CRLF2
		mov	si, (offset COMBUF+2)
		mov	di, offset PARSE_DATE
		xor	cx, cx
		xor	dx, dx
		call	cmd_parse
		retn
GETDAT		endp


; ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ S U B	R O U T	I N E ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ


GETTIM		proc near		; ...
		xor	cx, cx
		mov	dx, offset NewTim_Ptr
		call	std_printf
		mov	ah, 0Ah		; STD_CON_STRING_INPUT
		mov	dx, offset COMBUF
		mov	cx, 2		; INITSPECIAL
		call	SetInitFlag
		int	21h		; DOS -
		xor	cx, cx
		call	SetInitFlag
		call	CRLF2
		mov	si, (offset COMBUF+2)
		mov	di, offset PARSE_TIME
		xor	cx, cx
		xor	dx, dx
		call	cmd_parse
		retn
GETTIM		endp


; ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ S U B	R O U T	I N E ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ


skip_white	proc near		; ...
		cld

skw_lp:					; ...
		lodsb
		cmp	al, 20h	; ' '
		jz	short skw_lp
		cmp	al, 9
		jz	short skw_lp
		dec	si
		retn
skip_white	endp


; ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ S U B	R O U T	I N E ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ


copy_pipe_path	proc near		; ...
		mov	cx, 0FFFFh	; 65535
		xor	al, al
		mov	di, si
		push	es
		push	ds
		pop	es
		cld
		push	di
		repne scasb
		pop	di
		pop	es
		not	cx
		mov	di, 320h	; offset DATARES:Pipe1
					; (offset RESGROUP:EndINit)
		push	di
		push	cx
		rep movsb
		pop	cx
		pop	di
		push	ds
		push	es
		pop	ds
		mov	si, di
		mov	di, 36Fh	; offset DATARES:Pipe2
					; (offset RESGROUP:EndINit+79)
		rep movsb
		pop	ds
		retn
copy_pipe_path	endp


; ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ S U B	R O U T	I N E ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ


PARSELINE	proc near		; ...
		push	ax
		push	bx
		push	cx
		push	dx
		push	di
		push	si
		pushf
		mov	ds:cpyflag, 0
		mov	ds:LASTARG, 0FFFFh
		xor	ax, ax
		mov	cx, 1348	; ARG_UNIT.SIZE
		mov	di, offset ARG_ARGV ; offset ARG
		rep stosb
		mov	ds:ARGBUF_PTR, offset ARG_ARGBUF ; ARG+ARG_UNIT.argbuf
		mov	ds:ARG_ARGSWINFO, 0 ; [ARG+ARG_UNIT.argswinfo]
		mov	ds:ARG_ARGVCNT,	0 ; [ARG+ARG_UNIT.argvcnt]
		mov	si, (offset COMBUF+2)
		push	ds
		mov	ds, ds:RESSEG
		cmp	ds:ForFlag, 0
		pop	ds
		jnz	short PCONT
		mov	di, offset ARG_ARGFORCOMBUF ; ARG+ARG_UNIT.argforcombuf
		xor	ch, ch
		mov	cl, ds:COMBUF+1
		inc	cl
		rep movsb
		mov	si, offset ARG_ARGFORCOMBUF

PCONT:					; ...
		mov	di, offset TPBUF
		mov	bl, 20h	; ' '

PARSELOOP:				; ...
		mov	ds:COMPTR, si
		xor	bp, bp
		mov	ds:expand_star,	0
		call	scanoff
		call	cparse
		jnb	short MORE_PRSE
		or	bp, bp
		jz	short PARSEDONE
		call	newarg
		jmp	short PARSEDONE
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

MORE_PRSE:				; ...
		mov	ds:cpyflag, 2
		call	newarg
		jnb	short PARSELOOP
		jmp	short PARSE_ERROR
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

PARSEDONE:				; ...
		popf
		clc
		jmp	short PARSE_EXIT
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

PARSE_ERROR:				; ...
		popf
		stc

PARSE_EXIT:				; ...
		pop	si
		pop	di
		pop	dx
		pop	cx
		pop	bx
		pop	ax
		retn
PARSELINE	endp


; ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ S U B	R O U T	I N E ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ


newarg		proc near		; ...
		push	bx
		push	cx
		push	dx
		push	di
		push	si
		pushf
		call	arg_switch
		cmp	ds:ARG_ARGVCNT,	64 ; [ARG+ARG_UNIT.argvcnt],ARGMAX
		jge	short to_many_args
		mov	dh, bh
		mov	bx, ds:ARG_ARGVCNT ; [ARG+ARG_UNIT.argvcnt]
		inc	ds:ARG_ARGVCNT
		mov	ax, offset ARG_ARGV ; ARG+ARG_UNIT.argv
		call	argv_calc
		mov	word ptr [bx+7], 0 ; [bx+ARGV_ELE.argsw_word]
		mov	[bx+5],	cx	; [bx+ARGV_ELE.arglen]
		mov	[bx+2],	dh	; [bx+ARGV_ELE.argflags]
		mov	si, ds:ARGBUF_PTR
		mov	[bx], si	; [bx+ARGV_ELE.argpointer]
		add	si, ds:STARTEL
		sub	si, di
		mov	[bx+3],	si	; [bx+ARGV_ELE.argstartel]
		mov	si, ds:COMPTR
		mov	[bx+9],	si	; [bx+ARGV_ELE.arg_ocomptr]
		mov	si, di
		mov	di, ds:ARGBUF_PTR
		add	di, cx
		cmp	di, (offset ARG_ARGBUF+0FFh) ; ARG+ARG_UNIT.argbuf+ARGBLEN-1
		jge	short buf_oflow
		sub	di, cx
		cld
		rep movsb
		mov	al, 0		; ANULL
		stosb
		mov	ds:ARGBUF_PTR, di

newarg_done:
		popf
		clc
		jmp	short newarg_exit
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

to_many_args:				; ...
		mov	ax, 1
		jmp	short newarg_error
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

buf_oflow:				; ...
		mov	ax, 2

newarg_error:				; ...
		popf
		stc

newarg_exit:				; ...
		pop	si
		pop	di
		pop	dx
		pop	cx
		pop	bx
		retn
newarg		endp


; ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ S U B	R O U T	I N E ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ


arg_switch	proc near		; ...
		push	ax
		push	bx
		push	cx
		push	di
		pushf
		test	bh, 1
		jz	short arg_no_switch0
		cmp	ds:LASTARG, 0FFFFh ; -1
		jz	short arg_no_switch1
		mov	bx, ds:LASTARG
		mov	ax, offset ARG_ARGV ; ARG+ARG_UNIT.argv	; ARG+0
		call	argv_calc
		or	[bx+7],	bp	; [bx+ARGV_ELE.argsw_word]
		or	ds:ARG_ARGSWINFO, bp ; [ARG+ARG_UNIT.argswinfo]

arg_yes_switch:
		popf
		stc
		jmp	short arg_switch_exit
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

arg_no_switch0:				; ...
		mov	ax, ds:ARG_ARGVCNT ; [ARG+ARG_UNIT.argvcnt]
		mov	ds:LASTARG, ax

arg_no_switch1:				; ...
		popf
		clc

arg_switch_exit:			; ...
		pop	di
		pop	cx
		pop	bx
		pop	ax
		retn
arg_switch	endp


; ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ S U B	R O U T	I N E ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ


argv_calc	proc near		; ...
		push	ax
		mov	al, bl
		mov	bl, 11		; ARGV_ELE.SIZE
		mul	bl
		pop	bx
		add	ax, bx
		xchg	ax, bx
		retn
argv_calc	endp


; ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ S U B	R O U T	I N E ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ


path_search	proc near		; ...
		push	bx
		push	cx
		push	dx
		push	si
		push	di
		push	bp
		pushf
		test	ds:ARGV0_ARG_FLAGS, 3 ;	[ARG+ARGV_ELE.argflags],wildcard+sw_flag
		jz	short path_search_ok

path_failure_jmp:			; ...
		jmp	path_failure
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

path_search_ok:				; ...
		call	STORE_PCHAR
		mov	dx, offset FBUF
		mov	ax, 1A00h
		int	21h		; DOS -	SET DISK TRANSFER AREA ADDRESS
					; DS:DX	-> disk	transfer buffer
		push	es
		call	find_path
		mov	ds:pathinfo, es	; [pathinfo+0]
		mov	ds:pathinfo+2, di
		mov	ds:pathinfo+4, di
		pop	es
		mov	bx, 128		; PBUFLEN
		mov	si, offset EXECPATH
		call	STRIP
		jb	short path_failure_jmp
		mov	dx, si
		mov	ds:search_error, offset	baddrv_ptr
		call	PSEARCH
		or	ax, ax
		jz	short path_noinit
		mov	bp, ax
		mov	di, offset EXECPATH
		mov	si, ds:ARG_ARGV	; [ARG+ARGV_ELE.argpointer]
		mov	cx, ds:ARGV0_ARGSTARTEL	; [ARG+ARGV_ELE.argstartel]
		sub	cx, si
		mov	ah, ':'
		cmp	cx, 2
		jb	short adddrive
		cmp	[si+1],	ah
		jz	short movedrive

adddrive:				; ...
		mov	al, ds:CURDRV
		add	al, 'A'
		stosw
		jmp	short checkpath
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

movedrive:				; ...
		lodsw
		stosw
		sub	cx, 2

checkpath:				; ...
		or	al, 20h
		mov	dl, al
		sub	dl, 60h		; 'a'-1
		mov	al, ds:psep_char
		stosb
		cmp	cx, 1
		jb	short addpath
		lodsb
		dec	cx
		cmp	al, ds:psep_char
		jz	short movepath
		inc	cx
		dec	si

addpath:				; ...
		push	si
		mov	si, di
		mov	ax, 4700h
		int	21h		; DOS -	2+ - GET CURRENT DIRECTORY
					; DL = drive (0=default, 1=A, etc.)
					; DS:SI	points to 64-byte buffer area
		mov	di, si
		pop	si
		mov	al, ds:psep_char
		cmp	byte ptr [di], 0
		jz	short movepath

scanend:				; ...
		cmp	byte ptr [di], 0
		jz	short foundend
		inc	di
		jmp	short scanend
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

foundend:				; ...
		stosb

movepath:				; ...
		cmp	[si], al
		jnz	short copypath
		inc	si
		dec	cx

copypath:				; ...
		jcxz	short _copydone
		rep movsb

_copydone:				; ...
		jmp	path_success
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

path_noinit:				; ...
		test	ds:ARGV0_ARG_FLAGS, 4 ;	[ARG+ARGV_ELE.argflags],path_sep
		jnz	short path_failure
		mov	bh, ';'
		mov	dx, ds:ARGV0_ARGSTARTEL	; [ARG+ARGV_ELE.argstartel]
		sub	dx, ds:ARG_ARGV	; [ARG+ARGV_ELE.argpointer]
		add	dx, offset EXECPATH

path_loop:				; ...
		call	path_crunch
		mov	bp, ax
		lahf
		or	bp, bp
		jnz	short path_found
		sahf
		jnb	short path_loop

path_failure:				; ...
		xor	ax, ax
		jmp	path_exit
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

path_found:				; ...
		mov	di, offset EXECPATH
		mov	cx, ds:pathinfo+4
		mov	si, ds:pathinfo+2
		push	es
		push	ds:pathinfo	; [pathinfo+0]
		pop	es
		cmp	byte ptr es:[si+2], '.' ; 2Eh
		jnz	short path_cpy
		cmp	byte ptr es:[si+1], ':' ; 3Ah
		jnz	short path_cpy
		push	cx
		mov	al, es:[si]
		mov	[di], al
		mov	al, es:[si+1]
		mov	[di+1],	al
		mov	al, ds:psep_char
		mov	[di+2],	al
		push	si
		mov	dl, es:[si]
		or	dl, 20h
		sub	dl, 60h		; 'a'-1
		mov	si, di
		add	si, 3
		mov	ax, 4700h
		int	21h		; DOS -	2+ - GET CURRENT DIRECTORY
					; DL = drive (0=default, 1=A, etc.)
					; DS:SI	points to 64-byte buffer area
		call	dstrlen
		add	si, cx
		dec	si
		mov	di, si
		pop	si
		add	si, 3
		pop	cx

path_cpy:				; ...
		pop	es
		sub	cx, si
		push	ds
		push	ds:pathinfo	; [pathinfo+0]
		pop	ds
		cld

kloop:					; ...
		lodsb
		stosb
		call	testkanj
		jz	short notkanj1
		dec	cx
		jcxz	short popdone
		movsb
		dec	cx
		cmp	cx, 1
		ja	short kloop

popdone:				; ...
		pop	ds
		mov	al, ds:psep_char
		jmp	short path_store
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

notkanj1:				; ...
		loop	kloop
		pop	ds
		dec	di
		mov	al, ds:psep_char
		cmp	al, [di-1]
		jz	short path_success

path_store:				; ...
		stosb

path_success:				; ...
		mov	si, offset search_best_buf
		xor	cx, cx

path_succ_loop:				; ...
		lodsb
		stosb
		or	al, al
		jnz	short path_succ_loop
		mov	ax, bp

path_exit:				; ...
		popf
		pop	bp
		pop	di
		pop	si
		pop	dx
		pop	cx
		pop	bx
		retn
path_search	endp


; ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ S U B	R O U T	I N E ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ


STORE_PCHAR	proc near		; ...
		push	ax
		mov	al, 2Fh	; '/'
		call	pathchrcmp
		jz	short STORE_SLASH
		mov	al, 5Ch	; '\'
		mov	ds:psep_char, al
		pop	ax
		retn
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

STORE_SLASH:				; ...
		mov	ds:psep_char, al
		pop	ax
		retn
STORE_PCHAR	endp


; ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ S U B	R O U T	I N E ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ


path_crunch	proc near		; ...
		push	bx
		push	cx
		push	dx
		push	di
		push	si
		pushf
		call	STORE_PCHAR
		mov	di, offset TPBUF
		mov	si, ds:pathinfo+4
		mov	ds:pathinfo+2, si
		push	ds
		push	ds:pathinfo	;  [pathinfo+0]
		pop	ds
		xor	cl, cl

path_cr_copy:				; ...
		lodsb
		or	al, al
		jz	short path_seg
		cmp	al, bh
		jz	short path_seg
		call	testkanj
		jz	short _notkanj2
		stosb
		movsb
		mov	cl, 1
		jmp	short path_cr_copy
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

_notkanj2:				; ...
		xor	cl, cl
		stosb
		jmp	short path_cr_copy
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

path_seg:				; ...
		pop	ds
		mov	ds:pathinfo+4, si
		mov	bl, al
		xor	ax, ax
		cmp	di, offset TPBUF
		jz	short path_cr_leave

path_cr_look:
		mov	al, ds:psep_char
		or	cl, cl
		jnz	short path_cr_store
		cmp	al, [di-1]
		jz	short path_cr_l1

path_cr_store:				; ...
		stosb

path_cr_l1:				; ...
		mov	si, dx

path_cr_l2:				; ...
		lodsb
		stosb
		or	al, al
		jnz	short path_cr_l2
		mov	dx, offset TPBUF
		mov	ds:search_error, offset	BADPMES_PTR
		call	PSEARCH

path_cr_leave:				; ...
		or	bl, bl
		jz	short path_cr_empty
		popf
		clc
		jmp	short path_cr_exit
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

path_cr_empty:				; ...
		popf
		stc

path_cr_exit:				; ...
		pop	si
		pop	di
		pop	dx
		pop	cx
		pop	bx
		retn
path_crunch	endp


; ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ S U B	R O U T	I N E ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ


PSEARCH		proc near		; ...
		push	cx
		push	dx
		push	di
		push	si
		pushf
		push	dx
		mov	di, dx
		mov	si, offset search_curdir_buf
		xor	dx, dx
		cmp	byte ptr [di+1], 3Ah ; ':'
		jnz	short SEARCH_DIR_CHECK
		mov	dl, [di]
		and	dl, 0DFh	; ~20h	; uppercase
		sub	dl, 40h	; '@'   ; convert to drive number

SEARCH_DIR_CHECK:			; ...
		mov	ax, 4700h
		int	21h		; DOS -	2+ - GET CURRENT DIRECTORY
					; DL = drive (0=default, 1=A, etc.)
					; DS:SI	points to 64-byte buffer area
		pop	dx
		jb	short SEARCH_INVALID_DRIVE
		mov	cx, 13h		; search_attr
		mov	ax, 4E00h
		int	21h		; DOS -	2+ - FIND FIRST	ASCIZ (FINDFIRST)
					; CX = search attributes
					; DS:DX	-> ASCIZ filespec
					; (drive, path,	and wildcards allowed)
		jb	short SEARCH_NO_FILE
		mov	ds:search_best,	0 ; SEARCH_FILE_NOT_FOUND ; 0
		mov	ds:search_best_buf, 0 ;	ANULL ;	0

SEARCH_LOOP:				; ...
		call	SEARCH_FTYPE
		cmp	al, ds:search_best
		jle	short SEARCH_NEXT
		mov	ds:search_best,	al
		mov	si, (offset FBUF+1Eh) ;	FBUF+FIND_BUF.PNAME
		mov	di, offset search_best_buf
		mov	cx, 13		; FNAME_MAX_LEN
		cld
		rep movsb
		cmp	al, 8		; SEARCH_COM
		jz	short SEARCH_DONE

SEARCH_NEXT:				; ...
		mov	cx, 13h		; SEARCH_ATTR
		mov	ax, 4F00h
		int	21h		; DOS -	2+ - FIND NEXT ASCIZ (FINDNEXT)
					; [DTA]	= data block from
					; last AH = 4Eh/4Fh call
		jnb	short SEARCH_LOOP

SEARCH_DONE:				; ...
		mov	al, ds:search_best
		cmp	ds:ext_entered,	1
		jz	short SEARCH_EXIT
		mov	al, ds:ext_entered
		mov	ds:search_best,	al
		jmp	short SEARCH_EXIT
