		retn
$P_SValue	endp


; ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ S U B	R O U T	I N E ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ


$P_Value	proc near		; ...
		push	ax
		push	cx
		push	dx
		push	si
		xor	cx, cx
		xor	dx, dx
		push	bx

$P_Value_Loop:				; ...
		mov	al, cs:[si]
		or	al, al
		jz	short $P_Value00
		call	$P_0099
		jb	short $P_Value_Err0
		xor	ah, ah
		mov	bp, ax
		shl	dx, 1
		rcl	cx, 1
		call	$P_Check_OVF
		jb	short $P_Value_Err0
		mov	bx, dx
		mov	ax, cx
		shl	dx, 1
		rcl	cx, 1
		call	$P_Check_OVF
		jb	short $P_Value_Err0
		shl	dx, 1
		rcl	cx, 1
		call	$P_Check_OVF
		jb	short $P_Value_Err0
		add	dx, bx
		adc	cx, ax
		call	$P_Check_OVF
		jb	short $P_Value_Err0
		add	dx, bp
		adc	cx, 0
		call	$P_Check_OVF
		jb	short $P_Value_Err0
		inc	si
		jmp	short $P_Value_Loop
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

$P_Value_Err0:				; ...
		pop	bx
		jmp	$P_Value_Err
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

$P_Value00:				; ...
		pop	bx
		test	cs:$P_Flags2, 2	; $P_Neg
		jz	short $P_Value01
		not	cx
		not	dx
		add	dx, 1
		adc	cx, 0

$P_Value01:				; ...
		mov	si, es:[bx+6]	; [es:bx+$P_CONTROL_BLK.$P_Value_List]
		mov	al, es:[si]
		cmp	al, 0		; $P_nval_None
		jnz	short $P_Value02
		mov	al, 1
		mov	ah, 0FFh
		jmp	short $P_Value_Exit
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ
		nop

$P_Value02:				; ...
		inc	si
		mov	al, es:[si]
		cmp	al, 0
		jz	short $P_Value_Err ; $P_Value03
		inc	si

$P_Val02_Loop:				; ...
		test	cs:$P_Flags2, 80h ; $P_Signed
		jnz	short $P_Val02_Sign
		cmp	cx, es:[si+3]	; [es:si+$P_VAL_LIST.$P_Val_XH]
		jb	short $P_Val02_Next
		ja	short $P_Val_In
		cmp	dx, es:[si+1]	; [es:si+$P_VAL_LIST.$P_Val_XL]
		jb	short $P_Val02_Next

$P_Val_In:				; ...
		cmp	cx, es:[si+7]	; [es:si+$P_VAL_LIST.$P_Val_YH]
		ja	short $P_Val02_Next
		jb	short $P_Val_Found
		cmp	dx, es:[si+5]	; [es:si+$P_VAL_LIST.$P_Val_YL]
		ja	short $P_Val02_Next
		jmp	short $P_Val_Found
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

$P_Val02_Sign:				; ...
		cmp	cx, es:[si+3]
		jl	short $P_Val02_Next
		jg	short $P_SVal_In
		cmp	dx, es:[si+1]
		jl	short $P_Val02_Next

$P_SVal_In:				; ...
		cmp	cx, es:[si+7]
		jg	short $P_Val02_Next
		jl	short $P_Val_Found
		cmp	dx, es:[si+5]
		jg	short $P_Val02_Next
		jmp	short $P_Val_Found
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

$P_Val02_Next:				; ...
		add	si, 9		; $P_Len_Range
