		push	bp
		push	dx
		push	si
		mov	dl, 2		; $P_DOSTBL_Char

$P_SCOM_Loop:				; ...
		mov	al, cs:[si]	; 1st string
		call	$P_Chk_DBCS
		jb	short $P_SCOM00
		call	$P_Do_CAPS_Char
		test	cs:$P_Flags2, 8	; $P_Key_Cmp
		jz	short $P_SCOM04
		cmp	al, 3Dh	; '='   ; $P_Keyword
		jnz	short $P_SCOM03
		cmp	byte ptr es:[bp+1], 0 ;	$P_NULL
		jnz	short $P_SCOM_Differ
		jmp	short $P_SCOM05
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

$P_SCOM04:				; ...
		test	cs:$P_Flags2, 10h ; $P_SW_Cmp
		jz	short $P_SCOM03
		cmp	al, 3Ah	; ':'   ; $P_Colon
		jnz	short $P_SCOM03
		cmp	byte ptr es:[bp+0], 0 ;	$P_NULL
		jnz	short $P_SCOM_Differ

$P_SCOM05:				; ...
