		mov	ds:$M_RT_$M_CLASS_ADDRS, di ; $M_RT.$M_CLASS_ADDRS+$M_INDEX ; $M_INDEX = 0
					; [$M_RT+44]
		call	$M_GET_DBCS_VEC
		clc
		jb	short $MIF20
		pop	di
		pop	es
		assume es:nothing
		pop	dx
		pop	bx
		pop	ax
		jmp	short $MEN20
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

$MIF20:					; ...
