SETDRV:					; ...
		mov	ah, 0Eh
		int	21h		; DOS -	SELECT DISK
					; DL = new default drive number	(0 = A,	1 = B, etc.)
					; Return: AL = number of logical drives

TCOMMAND:				; ...
		mov	ds, cs:RESSEG
		mov	ax, 0FFFFh	; -1
		xchg	ax, ds:VerVal
		cmp	ax, 0FFFFh
		jz	short NOSETVER2
		mov	ah, 2Eh
		int	21h		; DOS -	SET VERIFY FLAG
					; DL = 00h, AL = 01h VERIFY on / 00h VERIFY off

NOSETVER2:				; ...
		call	dword ptr cs:HEADCALL ;	call far [cs:HEADCALL]
		xor	bp, bp
		cmp	ds:SingleCom, 0FFFFh ; -1
		jnz	short COMMAND

_$EXITPREP:				; ...
		push	cs
		pop	ds
		assume ds:TRANGROUP
		jmp	_$EXIT
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

COMMAND:				; ...
		cld
		mov	ax, cs
		cli
		mov	ss, ax
		assume ss:TRANGROUP
		mov	sp, offset STACK
		sti
		mov	es, ax
		assume es:TRANGROUP
		mov	ds, ax
		call	TSYSLOADMSG
		mov	append_exec, 0
		mov	ds, RESSEG
		assume ds:nothing
		mov	ss:UCOMBUF, 128
		mov	ss:COMBUF, 128
		or	bp, bp
		jz	short TESTRDIR
		mov	word ptr ss:UCOMBUF+1, 0D01h
		jmp	short NOSETBUF
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

TESTRDIR:				; ...
		cmp	ds:RestDir, 0
		jz	short NOSETBUF
		push	ds
		mov	ds:RestDir, 0
		push	cs
		pop	ds
		assume ds:TRANGROUP
		mov	dx, offset USERDIR1
		mov	ah, 3Bh
		int	21h		; DOS -	2+ - CHANGE THE	CURRENT	DIRECTORY (CHDIR)
					; DS:DX	-> ASCIZ directory name	(may include drive)
		pop	ds
		assume ds:nothing

NOSETBUF:				; ...
		cmp	ds:PipeFiles, 0
		jz	short NOPCLOSE
		cmp	ds:PipeFlag, 0
		jnz	short NOPCLOSE
		call	PIPEDEL

NOPCLOSE:				; ...
		mov	ds:ExtCom, 0
		mov	ax, cs
		mov	ds, ax
		assume ds:TRANGROUP
		push	ax
		mov	dx, offset STACK ; offset INTERNATVARS
		mov	ax, 3800h	; mov ax,INTERNATIONAL*256
		int	21h		; DOS -	2+ - GET COUNTRY-DEPENDENT INFORMATION
					; get current-country info
					; DS:DX	-> buffer for returned info
		pop	ax
		sub	ax, TPA
		push	bx
		mov	bx, 16
		mul	bx
		pop	bx
		or	dx, dx
		jz	short SAVSIZ
		mov	ax, 0FFFFh	; -1

SAVSIZ:					; ...
		cmp	ax, 512
		jbe	short GOTSIZE
		and	ax, 0FE00h	; ~1FFh	(NOT 511)

GOTSIZE:				; ...
		mov	BYTCNT,	ax
		mov	ds, RESSEG
		assume ds:nothing
		test	ds:EchoFlag, 1
		jz	short GETCOM
		call	SINGLETEST
		jb	short GETCOM
		test	ds:PipeFlag, 0FFh ; -1
		jnz	short GETCOM
		test	ds:ForFlag, 0FFh
		jnz	short GETCOM
		test	ds:Batch, 0FFFFh ; -1
		jnz	short GETCOM
		call	CRLF2

GETCOM:					; ...
		cmp	ds:SingleCom, 0
		jnz	short GETCOM2
		test	ds:Batch, 0FFFFh
		jnz	short GETCOM2
		and	byte ptr ds:Y_Flag, 0EFh ; Y/N question	overwrite flag

GETCOM2:				; ...
		mov	ds:Call_Flag, 0
		mov	ds:Call_Batch_Flag, 0
		mov	ah, 19h
		int	21h		; DOS -	GET DEFAULT DISK NUMBER
		mov	ss:CURDRV, al
		test	ds:PipeFlag, 0FFh
		jz	short NOPIPE
		jmp	PIPEPROC
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

NOPIPE:					; ...
		test	ds:EchoFlag, 1
		jz	short NOPDRV
		call	SINGLETEST
		jb	short NOPDRV
		test	ds:ForFlag, 0FFh
		jnz	short NOPDRV
		test	ds:Batch, 0FFFFh
		jnz	short TESTFORBAT
		call	PRINT_PROMPT

NOPDRV:					; ...
		test	ds:ForFlag, 0FFh
		jz	short TESTFORBAT
		jmp	FORPROC
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

TESTFORBAT:				; ...
		mov	ss:RE_INSTR, 0
		mov	ds:Re_OutStr, 0
		mov	ds:Re_Out_App, 0
		mov	ds:IfFlag, 0
		test	ds:Batch, 0FFFFh
		jz	short ISNOBAT
		push	es
		push	ds
		mov	ax, 1902h	; mult_shell_get
		mov	es, ds:Batch
		assume es:nothing
		mov	di, 20h
		push	cs
		pop	ds
		assume ds:TRANGROUP
		mov	dx, offset COMBUF
		int	2Fh		; - Multiplex -	DOS 4.x	only SHELLB.COM	- COMMAND.COM INTERFACE
					; ES:DI	-> ASCIZ full filename of current batch	file, with at least the
					; final	filename element uppercased
					; DS:DX	-> buffer for results
		cmp	al, 0FFh	; shell_action
		pop	ds
		assume ds:nothing
		pop	es
		jz	short JDOCOM1
		push	ds
		call	READBAT
		pop	ds
		mov	ds:NullFlag, 0
		test	ds:Batch, 0FFFFh
		jnz	short JDOCOM1
		mov	bx, ds:Next_Batch
		cmp	bx, 0
		jz	short JDOCOM1
		mov	ds:Batch, bx
		mov	ds:Next_Batch, 0

JDOCOM1:				; ...
		push	cs
		pop	ds
		assume ds:TRANGROUP
		jmp	short DOCOM0
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

