		cmp	ds:SingleCom, 0
		jz	short REGCOM
		mov	si, ds:SemiPermCom
		xchg	si, ds:SingleCom
		mov	di, (offset COMBUF+2)
		xor	cx, cx

SINGLELOOP:				; ...
		lodsb
		stosb
		inc	cx
		cmp	al, 0Dh
		jnz	short SINGLELOOP
		dec	cx
		push	cs
		pop	ds
		mov	COMBUF+1, cl
		jmp	short DOCOM0
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

REGCOM:					; ...
		mov	ax, 5D09h	; (SERVERCALL<<8)+9
		int	21h		; DOS -	3.1+ internal -	FLUSH REDIRECTED PRINTER OUTPUT
		mov	ax, 5D08h	; (SERVERCALL<<8)+8
		mov	dl, 1
		int	21h		; DOS -	3.1+ internal -	SET REDIRECTED PRINTER MODE
					; DL = 00h redirected output is	combined
					;      01h redirected output placed in separate	jobs
					; start	new print job now
		push	cs
		pop	ds
		mov	dx, offset UCOMBUF
		mov	ax, 4810h	; AX = DOSKey Read Line	function
		int	2Fh
		or	ax, ax
		jz	short GOTCOM
		mov	ah, 0Ah		; Std_Con_String_Input
		int	21h		; DOS -	BUFFERED KEYBOARD INPUT
					; DS:DX	-> buffer

GOTCOM:					; ...
		mov	cl, UCOMBUF
		xor	ch, ch
		add	cx, 3
		mov	si, offset UCOMBUF
		mov	di, offset COMBUF
		rep movsb
		call	CRLF2
		xor	ax, ax
		jmp	short DOCOM2
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

DOCOM:					; ...
		call	CRLF2

DOCOM0:					; ...
		mov	ax, 1		; cox_Y_option = yes (1)

DOCOM2:					; ...
		push	ds
		mov	ds, RESSEG
		assume ds:nothing
		mov	ds:cox_Y_option, ax
		pop	ds
		mov	si, offset COMBUF
		mov	cl, [si+1]
		xor	ch, ch
		add	si, 2
		call	get_cox_y_n_opt
		jnb	short DOCOM1	; YES answer
		jmp	NULLCOM		; NO answer
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

DOCOM1:					; ...
		call	PRESCAN
		jz	short NOPIPEPROC
		jmp	PIPEPROCSTRT
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

NULLCOMJ:				; ...
		jmp	NULLCOM
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

NOPIPEPROC:				; ...
		call	PARSELINE
		jnb	short OKPARSE

BADPARSE:				; ...
		push	cs
		pop	ds
		assume ds:TRANGROUP
		mov	dx, offset BADNAM_PTR
		call	std_eprintf
		jmp	TCOMMAND
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

OKPARSE:				; ...
		test	ARGV0_ARG_FLAGS, 2 ; [ARG+ARGV_ELE.argflags],wildcard
		jnz	short BADPARSE
		cmp	ARG_ARGVCNT, 0	;  [ARG+ARG_UNIT.argvcnt]
		jz	short NULLCOMJ
		cmp	ARGV0_ARGLEN, 0	; [ARG+ARGV_ELE.arglen]
		jz	short NULLCOMJ
		mov	si, (offset COMBUF+2)
		mov	di, offset IDLEN
		mov	ax, 2901h
		int	21h		; DOS -	PARSE FILENAME
					; DS:SI	-> string to parse
					; ES:DI	-> buffer to fill with unopened	FCB
					; AL = bit mask	to control parsing
		mov	bx, ARG_ARGV	; [ARG+ARG_UNIT.argv]
		cmp	byte ptr [bx+1], ':'
		jnz	short DRVGD
		mov	dl, [bx]
		and	dl, 0DFh
		sub	dl, 'A'
		cmp	al, 0FFh	; -1
		jz	short DRVBADJ
		mov	di, ARGV0_ARGSTARTEL ; [ARG+ARGV_ELE.argstartel]
		cmp	byte ptr [di], 0
		jnz	short DRVGD
		jmp	SETDRV
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

DRVBADJ:				; ...
		jmp	DRVBAD
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

DRVGD:					; ...
		mov	al, [di]
		mov	SPECDRV, al
		mov	al, 20h	; ' '
		mov	cx, 9
		inc	di
		repne scasb
		mov	al, 8
		sub	al, cl
		mov	IDLEN, al
		mov	di, 81h
		push	si
		mov	si, (offset COMBUF+2)
		call	scanoff
		inc	si

DO_SKIPCOM:				; ...
		lodsb
		call	DELIM
		jz	short DO_SKIPPED
		cmp	al, 0Dh
		jz	short DO_SKIPPED
		cmp	al, SWITCHAR
		jnz	short DO_SKIPCOM

DO_SKIPPED:				; ...
		dec	si
		xor	cx, cx

COMTAIL:				; ...
		lodsb
		stosb
		cmp	al, 0Dh
		loopne	COMTAIL
		dec	di
		mov	bp, di
		not	cl
		mov	TRANSTART+80h, cl ; mov	[80h],cl
		pop	si
		mov	di, ARGV0_ARGSW_WORD ; [ARG+ARGV_ELE.argsw_word]
		mov	COMSW, di
		mov	si, ARGV1_ARGPOINTER ; [ARG+ARGV_ELE.SIZE+ARGV_ELE.argpointer]
		or	si, si
		jnz	short DOPARSE
		mov	si, bp

DOPARSE:				; ...
		mov	di, 5Ch		; mov di,FCB ; 5Ch
		mov	ax, 2901h
		int	21h		; DOS -	PARSE FILENAME
					; DS:SI	-> string to parse
					; ES:DI	-> buffer to fill with unopened	FCB
					; AL = bit mask	to control parsing
		mov	PARM1, al
		mov	di, ARGV1_ARGSW_WORD ; [ARG+ARGV_ELE.SIZE+ARGV_ELE.argsw_word]
		mov	ARG1S, di
		mov	si, ARGV2_ARGPOINTER ; [ARG+(2*ARGV_ELE.SIZE)+ARGV_ELE.argpointer]
		or	si, si
		jnz	short DOPARSE2
		mov	si, bp

DOPARSE2:				; ...
		mov	di, 6Ch		; mov di,FCB+10h ; 6Ch
		mov	ax, 2901h
		int	21h		; DOS -	PARSE FILENAME
					; DS:SI	-> string to parse
					; ES:DI	-> buffer to fill with unopened	FCB
					; AL = bit mask	to control parsing
		mov	PARM2, al
		mov	di, ARGV2_ARGSW_WORD ; [ARG+(2*ARGV_ELE.SIZE)+ARGV_ELE.argsw_word]
		mov	ARG2S, di
		mov	di, ARGV0_ARGSW_WORD ; [ARG+ARGV_ELE.argsw_word]
		not	di
		and	di, ARG_ARGSWINFO ; [ARG+ARG_UNIT.argswinfo]
		mov	ARGTS, di
		mov	al, IDLEN
		mov	dl, SPECDRV
		or	dl, dl
		jnz	short EXTERNALJ1
		dec	al
		jmp	FNDCOM
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

EXTERNALJ1:				; ...
		jmp	EXTERNAL
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

NULLCOM:				; ...
		mov	ds, RESSEG
		assume ds:nothing
		test	ds:Batch, 0FFFFh ; -1
		jz	short NOSETFLAG
		mov	ds:NullFlag, 1	; mov byte [NullFlag],nullcommand

NOSETFLAG:				; ...
		cmp	ds:SingleCom, 0FFFFh ; -1
		jz	short EXITJ
		jmp	GETCOM
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

EXITJ:					; ...
		jmp	_$EXITPREP
; END OF FUNCTION CHUNK	FOR _$IF

; ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ S U B	R O U T	I N E ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ


get_cox_y_n_opt	proc near		; ...
		jcxz	short ccydp4	; empty	input buffer

ccydp0:
		cmp	byte ptr [si], 0Dh
		jz	short ccydp4
		cmp	byte ptr [si], 0Ah
		jz	short ccydp4
		push	es
		mov	es, ds:RESSEG
		mov	al, byte ptr es:Y_Flag
		test	al, 10h		; bit 1	= 1 -> Y/N answer is needed
		jz	short ccydp3
		cmp	es:Call_Batch_Flag, 1 ;	(in) Batch file	?
		jz	short ccydp3	; yes, don't check for ESCAPE
		test	al, 40h		; ESCAPE status
					; (bit 4 is zero if Y/N	is excaped)
		jz	short ccydp5

ccydp1:					; ...
		test	es:Batch, 0FFFFh
		jz	short ccydp2
		mov	es, es:Batch
		mov	byte ptr es:2, 1 ; [es:BATCHSEGMENT.BatchEOF]

ccydp2:					; ...
		stc
		pop	es
		retn
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

ccydp3:					; ...
		pop	es

ccydp4:					; ...
		clc
		retn
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

ccydp5:					; ...
		mov	dx, si
		mov	bx, 2
		mov	ah, 40h
		int	21h		; DOS -	2+ - WRITE TO FILE WITH	HANDLE
					; BX = file handle, CX = number	of bytes to write, DS:DX -> buffer
		mov	dx, offset cox_Y_quest_ptr ; msg number	pointer	of ' [Y/N]?' (is 1082)
		call	std_eprintf
		push	ds
		mov	ax, 1083	; cox_Y_answer number (overwrite Y/N answer letter)
		mov	dh, 0FFh	; utility_msg_class
		call	TSYSGETMSG
		mov	cx, 'NY'        ; 'YN' Yes/No (CL=Y)
		jb	short ccydp6
		mov	cx, [si]

ccydp6:					; ...
		pop	ds

ccydp7:					; ...
		mov	ah, 8
		int	21h		; DOS -	KEYBOARD INPUT,	NO ECHO
					; Return: AL = character
		test	al, al
		jnz	short ccydp8
		mov	ah, 8
		int	21h		; DOS -	KEYBOARD INPUT,	NO ECHO
					; Return: AL = character
		cmp	al, '?'
		jnz	short ccydp7
		or	byte ptr es:Y_Flag, 40h	; bit 4, question flag
		jmp	short ccydp1
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

ccydp8:					; ...
		cmp	al, 1Bh		; ESCAPE ?
		jnz	short ccydp9
		and	byte ptr es:Y_Flag, 0EFh ; (ESCAPE) Clear bit 4	; ~10h
		jmp	short ccydp12
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

ccydp9:					; ...
		and	al, 0DFh	; uppercase
		cmp	al, ch		; NO character (N)
		jnz	short ccydp10
		stc
		jmp	short ccydp11	; cf = 1 -> overwrite NO answer
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

ccydp10:				; ...
		cmp	al, cl		; YES character	(Y)
		jnz	short ccydp7

ccydp11:				; ...
		pushf			; cf = 0 -> overwrite YES answer
		mov	dl, al
		mov	ah, 2
		int	21h		; DOS -	DISPLAY	OUTPUT
					; DL = character to send to standard output
		popf

ccydp12:				; ...
		pushf
		call	CRLF2
		popf
		pop	es
		retn
get_cox_y_n_opt	endp


; ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ S U B	R O U T	I N E ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ


ROM_SCAN	proc near		; ...
		push	es
		push	si
		push	di
		push	cx
		push	ax
		push	bx
		mov	ax, 0F000h
		mov	es, ax
		assume es:nothing
		cmp	byte ptr es:0FFFEh, 0FDh
		jz	short SCAN_IT

NO_ROM:					; ...
		clc

ROM_RET:				; ...
		pop	bx
		pop	ax
		pop	cx
		pop	di
		pop	si
		pop	es
		assume es:nothing
		retn
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

SCAN_IT:				; ...
		mov	ax, 0C000h

SCAN_ONE:				; ...
		mov	es, ax
		assume es:nothing
		xor	di, di
		cmp	word ptr es:[di], 0AA55h
		jz	short SCAN_LIST
		add	ax, 80h

SCAN_END:				; ...
		cmp	ax, 0F000h
		jb	short SCAN_ONE
		jmp	short NO_ROM
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

SCAN_LIST:				; ...
		mov	bl, es:[di+2]	; [es:di+ROM_HEADER.rom_length]
		xor	bh, bh
		shl	bx, 1
		shl	bx, 1
		add	bx, 7Fh
		and	bx, 0FF80h	; round	to 2k
		mov	di, 6		; ROM_HEADER.name_list

SCAN_NAME:				; ...
		mov	cl, es:[di]
		inc	di
		xor	ch, ch
		or	cx, cx
		jnz	short SCAN_TEST
		add	ax, bx
		jmp	short SCAN_END
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

SCAN_TEST:				; ...
		mov	si, dx
		inc	si
		repe cmpsb
		jz	short SCAN_FOUND

SCAN_NEXT:				; ...
		add	di, cx
		add	di, 3
		jmp	short SCAN_NAME
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

SCAN_FOUND:				; ...
		cmp	byte ptr [si], 3Fh ; '?'
		jz	short SCAN_SAVE
		cmp	byte ptr [si], 20h ; ' '
		jnz	short SCAN_NEXT

SCAN_SAVE:				; ...
		mov	cs:ROM_CS, es
		mov	cs:ROM_IP, di
		stc
		jmp	short ROM_RET
ROM_SCAN	endp

; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ
; START	OF FUNCTION CHUNK FOR _$IF

ROM_EXEC:				; ...
		mov	bx, 0FFFFh
		mov	ah, 48h
		int	21h		; DOS -	2+ - ALLOCATE MEMORY
					; BX = number of 16-byte paragraphs desired
		mov	ah, 48h
		int	21h		; DOS -	2+ - ALLOCATE MEMORY
					; BX = number of 16-byte paragraphs desired
		push	bx
		push	ax
		mov	ax, 2522h	; (SET_INTERRUPT_VECTOR<<8)+INT_TERMINATE
		push	ds
		mov	ds, cs:RESSEG
		mov	dx, offset Exec_Wait
		int	21h		; DOS -	SET INTERRUPT VECTOR
					; AL = interrupt number
					; DS:DX	= new vector to	be used	for specified interrupt
		mov	dx, ds
		mov	es, dx
		assume es:nothing
		pop	ds
		pop	dx
		mov	ah, 55h
		int	21h		; DOS -	2+ internal - CREATE PSP
					; DX = segment number at which to set up PSP
					; SI = (DOS 3+)	value to place in memory size field at DX:[0002h]
		mov	ds, dx
		mov	dx, 80h
		mov	ah, 1Ah
		int	21h		; DOS -	SET DISK TRANSFER AREA ADDRESS
					; DS:DX	-> disk	transfer buffer
		mov	ax, es:EnvirSeg
		mov	ds:2Ch,	ax	; [PDB.ENVIRON]
		pop	bx
		mov	dx, ds
		add	dx, bx
		mov	ds:2, dx	; [PDB.BLOCK_LEN]
		mov	dx, ds
		dec	dx
		mov	ds, dx
		assume ds:nothing
		inc	dx
		mov	ds:1, dx	; [ARENA.owner]
		mov	ds, dx
		assume ds:nothing
		cmp	bx, 1000h
		jb	short GOT_STACK
		xor	bx, bx

GOT_STACK:				; ...
		mov	cl, 4
		shl	bx, cl
		mov	dx, ds
		mov	ss, dx
		assume ss:nothing
		mov	sp, bx
		xor	ax, ax
		push	ax
		not	ax
		push	cs:ROM_CS
		push	cs:ROM_IP
		mov	es, dx
		retf
; END OF FUNCTION CHUNK	FOR _$IF

; ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ S U B	R O U T	I N E ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ


PROMPTBAT	proc near		; ...
		call	BATOPEN
		jb	short PROMPTBAT1
		retn
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

PROMPTBAT1:				; ...
		cmp	dx, 2		; ERROR_FILE_NOT_FOUND
		jz	short BAT_REMCHECK
		cmp	dx, 3		; ERROR_PATH_NOT_FOUND
		jz	short BAT_REMCHECK
		call	output_batch_name
		jmp	short BATDIE
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

BAT_REMCHECK:				; ...
		call	cs:RCH_ADDR	; call far [cs:RCH_ADDR]
		jz	short ASKFORBAT
		call	FOROFF
		call	PipeOff
		mov	ds:IfFlag, al
		mov	dx, offset BADBAT_PTR

BATDIE:					; ...
		call	BATCHOFF
		push	cs
		pop	ds
		assume ds:TRANGROUP
		call	std_eprintf
		jmp	TCOMMAND
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

ASKFORBAT:				; ...
		push	ds
		push	cs
		pop	ds
		mov	dx, offset NEEDBAT_PTR
		call	std_eprintf
		mov	dx, offset PAUSEMES_PTR
		call	std_eprintf
		call	GETKEYSTROKE
		pop	ds
		assume ds:nothing
		jmp	short PROMPTBAT
PROMPTBAT	endp


; ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ S U B	R O U T	I N E ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ


output_batch_name proc near		; ...
		push	ds
		mov	ds, ds:Batch
		mov	si, 20h		; BATCHSEGMENT.BatFile
		call	dstrlen
		mov	di, offset BWDBUF
		rep movsb
		push	cs
		pop	ds
		assume ds:TRANGROUP
		mov	extend_buf_ptr,	dx
		mov	msg_disp_class,	1 ; ext_msg_class
		mov	dx, offset extend_buf_ptr
		mov	string_ptr_2, offset BWDBUF
		mov	extend_buf_sub,	1 ; one_subst
		pop	ds
		assume ds:nothing
		retn
output_batch_name endp


; ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ S U B	R O U T	I N E ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ


GETKEYSTROKE	proc near		; ...
		push	dx
		mov	ax, 6302h	; (ECS_call SHL	8) OR GetInterimMode
