		test	ds:EchoFlag, 1
		jnz	short TRY_NEXTFLAG

RESET:					; ...
		push	cs
		pop	ds
		assume ds:TRANGROUP
		jz	short READBAT_RETN

TRY_NEXTFLAG:				; ...
		cmp	ds:NullFlag, 1
