		pop	cx
		push	es
		pop	ds
		assume ds:nothing
		push	cs
		pop	es
		assume es:TRANGROUP
		mov	si, di
		pop	di
		jb	short GETENV6
		call	STRCPY

GETENV6:				; ...
		pop	ds
		jmp	RDBAT
READBAT		endp


