; ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ S U B	R O U T	I N E ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ


ALLOC_TPA	proc near		; ...
		push	es
		mov	es, ds:RESSEG
		mov	bx, 0FFFFh
		mov	ah, 48h
		int	21h		; DOS -	2+ - ALLOCATE MEMORY
					; BX = number of 16-byte paragraphs desired
		push	bx
		mov	ah, 48h
		int	21h		; DOS -	2+ - ALLOCATE MEMORY
					; BX = number of 16-byte paragraphs desired
		pop	bx
		mov	es:Res_Tpa, ax
		mov	ds:TRAN_TPA, ax
		mov	dx, ax
		and	ax, 0FFFh
		jnz	short CALC_TPA
		mov	ax, dx
		and	ax, 0F000h
		jnz	short NOROUND

CALC_TPA:				; ...
		mov	ax, dx
		and	ax, 0F000h
		add	ax, 1000h
		jb	short NOROUND
		mov	dx, es:Res_Tpa
		add	dx, bx
		cmp	dx, ax
		jb	short NOROUND
		mov	bx, cs
		cmp	bx, ax
		jb	short NOROUND
		sub	bx, ax
		cmp	bx, 1000h
		jnb	short ROUNDDONE

NOROUND:				; ...
		mov	ax, es:Res_Tpa

ROUNDDONE:				; ...
		mov	es:LTpa, ax
		mov	ds:TPA,	ax
		mov	bx, ax
		mov	ax, cs
		sub	ax, bx
		push	bx
		mov	bx, 16
		mul	bx
		pop	bx
		or	dx, dx
		jz	short SAVSIZ2
		mov	ax, 0FFFFh

SAVSIZ2:				; ...
		cmp	ax, 512
		jbe	short GOTSIZE2
		and	ax, 0FE00h	; NOT 511 = NOT	1FFh

GOTSIZE2:				; ...
		mov	ds:BYTCNT, ax
		pop	es
		retn
ALLOC_TPA	endp

