		call	IOSET

skip_ioset:				; ...
		call	FREE_TPA
		cmp	es:Call_Batch_Flag, 1 ;	call_in_progress
		jz	short GETECHO
		call	FOROFF

GETECHO:				; ...
		call	PipeOff
		mov	al, es:EchoFlag
		and	al, 1
		push	ax
		xor	ax, ax
		test	es:Batch, 0FFFFh
		jz	short LEAVEBAT
		mov	ax, es:Batch
		cmp	es:Call_Batch_Flag, 1 ;	call_in_progress
		jz	short LEAVEBAT
		push	es
		mov	es, ax
		mov	ax, es:3	; [es:BATCHSEGMENT.BatLast]
		pop	es

LEAVEBAT:				; ...
		push	ax
		cmp	es:Call_Batch_Flag, 1
		jz	short STARTBAT
		call	BATCHOFF

STARTBAT:				; ...
		mov	es:Call_Batch_Flag, 0
		mov	si, offset EXECPATH
		mov	ax, 0B711h	; AppendTruename
		int	2Fh		; - Multiplex -	DOS 4.0	APPEND - SET RETURN FOUND NAME STATE
		mov	ah, 4Eh
		mov	dx, si
		mov	cx, 13h
		int	21h		; DOS -	2+ - FIND FIRST	ASCIZ (FINDFIRST)
					; CX = search attributes
					; DS:DX	-> ASCIZ filespec
					; (drive, path,	and wildcards allowed)
		call	dstrlen
		mov	bx, cx
		add	bx, 191		; 15+BATCHSEGMENT.SIZE+COMBUFLEN+15
		push	cx
		mov	cl, 4
		shr	bx, cl
		push	bx
		mov	ah, 48h
		int	21h		; DOS -	2+ - ALLOCATE MEMORY
					; BX = number of 16-byte paragraphs desired
		pop	bx
		jb	short MEM_ERROR
		push	ax
		add	ax, bx
		add	ax, 20h
		mov	bx, cs
		mov	dx, 0AF95h	; mov dx,offset	TRANGROUP:TranSpaceEnd
		add	dx, 15
		shr	dx, cl
		add	dx, bx
		cmp	ax, bx
		jb	short ENOUGH_MEM
		cmp	ax, dx
		ja	short ENOUGH_MEM
		pop	ax
		push	es
		mov	es, ax
		mov	ax, 4900h
		int	21h		; DOS -	2+ - FREE MEMORY
					; ES = segment address of area to be freed
		pop	es

MEM_ERROR:				; ...
		jmp	NO_MEMORY
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

ENOUGH_MEM:				; ...
		pop	ax
		mov	es:Batch, ax
		call	ALLOC_TPA
		pop	dx
		pop	ax
		inc	es:Nest
		push	es
		mov	es, es:Batch
		mov	byte ptr es:0, 0 ; [es:BATCHSEGMENT.BatType]
		mov	es:3, ax	; [es:BATCHSEGMENT.BatLast]
		push	ds
		mov	ds, ds:RESSEG
		xor	ax, ax
		mov	bl, ds:ForFlag
		mov	es:7, bl	; [es:BATCHSEGMENT.BatForFlag]
		test	bl, 0FFh
		jz	short FOR_NOT_ON
		mov	ax, ds:ForPtr
		mov	ds:ForFlag, 0

FOR_NOT_ON:				; ...
		mov	es:5, ax	; [es:BATCHSEGMENT.BatForPtr]
		xor	ax, ax
		mov	ds:ForPtr, ax
		mov	bl, ds:EchoFlag
		pop	ds
		mov	es:1, bl	; [es:BATCHSEGMENT.BatEchoFlag]
		mov	byte ptr es:2, 0 ; [es:BATCHSEGMENT.BatchEOF]
		mov	es:8, ax	; [es:BATCHSEGMENT.BatSeek]
		mov	es:0Ah,	ax	; [es:BATCHSEGMENT.BatSeek+2]
		dec	ax
		mov	di, 0Ch		; BATCHSEGMENT.BatParm
		mov	bx, di
		mov	cx, 10
		rep stosw
		mov	cx, dx
		rep movsb
		mov	si, (offset COMBUF+2)
		mov	cx, 10

EACHPARM:				; ...
		call	scanoff
		cmp	al, 0Dh
		jz	short HAVPARM
		jcxz	short MOVPARM
		mov	es:[bx], di
		add	bx, 2

MOVPARM:				; ...
		lodsb
		call	DELIM
		jz	short ENDPARM
		stosb
		cmp	al, 0Dh
		jz	short HAVPARM
		jmp	short MOVPARM
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

ENDPARM:				; ...
