BATCHOFF	proc near		; ...
		push	ax
		push	es
		push	ds
		push	bx
		mov	es, cs:RESSEG
		mov	ds, cs:RESSEG
		mov	ax, ds:Batch
		or	ax, ax
		jz	short NOTFREE
		push	es
		mov	es, ax
		test	ds:EchoFlag, 1
		jnz	short ECHO_LAST_LINE
		mov	ds:Suppress, 0	; NO_ECHO

ECHO_LAST_LINE:				; ...
		mov	bl, es:1	; [es:BATCHSEGMENT.BatEchoFlag]
		mov	ds:EchoFlag, bl
		mov	bx, es:5	; [es:BATCHSEGMENT.BatForPtr]
		mov	ds:ForPtr, bx
		mov	bl, es:7	; [es:BATCHSEGMENT.BatForFlag]
		mov	ds:ForFlag, bl
		mov	bx, es:3	; [es:BATCHSEGMENT.BatLast]
		mov	ah, 49h
		int	21h		; DOS -	2+ - FREE MEMORY
					; ES = segment address of area to be freed
		pop	es
		mov	ds:Next_Batch, bx
		dec	es:Nest
		xor	ax, ax
		mov	ds:Batch, ax

NOTFREE:				; ...
		pop	bx
		pop	ds
		pop	es
		pop	ax
		retn
BATCHOFF	endp


