		jz	short getbatbyt1
		jmp	BATEOF
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

getbatbyt1:				; ...
		test	ds:Batch, 0FFFFh
		jnz	short getbatbyt2
		jmp	BATEOF
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

getbatbyt2:				; ...
		push	es
		mov	es, ds:Batch
		cmp	byte ptr es:2, 0 ; [es:BATCHSEGMENT.BatchEOF]
		jz	short not_eof
		jmp	At_EOF
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

not_eof:				; ...
		add	word ptr es:8, 1 ; [es:BATCHSEGMENT.BatSeek]
		adc	word ptr es:0Ah, 0 ; [es:BATCHSEGMENT.BatSeek+2]
		pop	es
		mov	ax, cs
		mov	ds, ax
		assume ds:TRANGROUP
		mov	bx, BATBUFPOS
		cmp	bx, 0FFFFh
		jnz	short UNBUF
		mov	dx, offset BATBUF
		mov	cx, BATBUFLEN
		mov	bx, BATHAND
		mov	ah, 3Fh
		int	21h		; DOS -	2+ - READ FROM FILE WITH HANDLE
					; BX = file handle, CX = number	of bytes to read
					; DS:DX	-> buffer
		jnb	short bat_read_ok
		call	get_ext_error_number
		push	ds
		mov	ds, RESSEG
		assume ds:nothing
		mov	dx, ax
		call	output_batch_name
		pop	ds
		call	std_eprintf
		mov	ds:COMBUF+2, 0Dh ; END_OF_LINE_IN
		mov	ds:COMBUF+3, 0	; END_OF_LINE_OUT
		mov	ds, ds:RESSEG
		jmp	short BATEOF
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

bat_read_ok:				; ...
		mov	cx, ax
		jcxz	short BATEOFDS
		mov	ds:BATBUFEND, cx
		xor	bx, bx
		mov	ds:BATBUFPOS, bx

UNBUF:					; ...
		mov	al, ds:BATBUF[bx]
		inc	bx
		cmp	bx, ds:BATBUFEND
		jb	short SETBUFPOS
		mov	bx, 0FFFFh	; -1

SETBUFPOS:				; ...
		mov	ds:BATBUFPOS, bx
		cmp	al, 1Ah		; ^Z for termination?
		jnz	short GETBYTEDONE

BATEOFDS:				; ...
		push	es
		mov	es, ds:RESSEG
		mov	es, es:Batch
		cmp	byte ptr es:2, 0
		jnz	short crpresent
		inc	byte ptr es:2	; [es:BATCHSEGMENT.BatchEOF]
		mov	bx, ds:BATBUFEND
		cmp	byte ptr ds:(BATBUFPOS+1)[bx], 0Ah ;
					; cmp byte [bx+BATBUF-1],0Ah
					; was a	LF present?
		jz	short crpresent
		add	byte ptr es:2, 3 ; BatchEOF == 4 to fake CR-LF

crpresent:				; ...
		mov	ds, ds:RESSEG

At_EOF:					; ...
		shr	byte ptr es:2, 1 ; decrement the flag
		jz	short turn_off	; zero,turn batch off
		cmp	byte ptr es:2, 1
		jz	short ret_lf	; BatchEOF was 2, return LF
		mov	al, 0Dh		; BatchEOF == 4,
					; indicates return fake	CR now
					; and fake LF next
		pop	es
		jmp	short GETBYTEDONE
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

ret_lf:					; ...
		mov	al, 0Ah		; return fake LF
		pop	es
		jmp	short GETBYTEDONE
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

turn_off:				; ...
		pop	es

BATEOF:					; ...
		call	BATCHOFF
		call	BATCLOSE
		mov	al, 0Dh
		test	ds:Batch_Abort,	0FFh
		mov	ds:Batch_Abort,	0
		jz	short CONT_GET_BYT
		mov	di, (offset COMBUF+2)
		xor	cx, cx
		jmp	short GETBYTEDONE
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

CONT_GET_BYT:				; ...
		cmp	ds:SingleCom, 0FFF0h
		jnz	short GETBYTEDONE
		cmp	ds:Nest, 0
		jnz	short GETBYTEDONE
		mov	ds:SingleCom, 0FFFFh ; -1

GETBYTEDONE:				; ...
		pop	ds
		pop	cx
		pop	bx
		retn
GETBATBYT	endp

; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ
; START	OF FUNCTION CHUNK FOR _$IF

IFERRORP:				; ...
		pop	ax

IFFORERROR:				; ...
		mov	dx, offset SYNTMES_PTR
		jmp	cerror
; END OF FUNCTION CHUNK	FOR _$IF

; ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ S U B	R O U T	I N E ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ


_$IF		proc far		; ...

; FUNCTION CHUNK AT 0100 SIZE 00000336 BYTES
; FUNCTION CHUNK AT 054E SIZE 0000006E BYTES
; FUNCTION CHUNK AT 0868 SIZE 00000178 BYTES
; FUNCTION CHUNK AT 0B62 SIZE 00000007 BYTES
; FUNCTION CHUNK AT 0ED1 SIZE 00000124 BYTES
; FUNCTION CHUNK AT 2E45 SIZE 000000F5 BYTES
; FUNCTION CHUNK AT 2F50 SIZE 000000A5 BYTES
; FUNCTION CHUNK AT 33CF SIZE 0000016D BYTES

		push	ds
		mov	ds, ds:RESSEG
		cmp	ds:PipeFiles, 0
		jz	short IFNoPipe
		call	PIPEDEL

IFNoPipe:				; ...
		pop	ds
		mov	ds:IFNOTFLAG, 0
		mov	ds:IF_NOT_COUNT, 0
		mov	si, 81h

IFREENT:				; ...
		call	scanoff
		cmp	al, 0Dh
		jz	short IFFORERROR
		mov	bp, si
		mov	di, offset IFTAB
		mov	ch, 0

IFINDCOM:				; ...
		mov	si, bp
		mov	cl, [di]
		inc	di
		jcxz	short IFSTRING
		jmp	short FIRSTCOMP
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

IFCOMP:					; ...
		jnz	short IF_DIF

FIRSTCOMP:				; ...
		lodsb
		mov	ah, es:[di]
		inc	di
		cmp	al, ah
		jz	short IFLP
		or	ah, 20h		; Try lower case
		cmp	al, ah

IFLP:					; ...
		loop	IFCOMP

IF_DIF:					; ...
		lahf
		add	di, cx
		mov	bx, [di]
		inc	di
		inc	di
		sahf
		jnz	short IFINDCOM
		lodsb
		cmp	al, 0Dh

IFERRJ:					; ...
		jz	short IFFORERROR
		call	DELIM
		jnz	short IFINDCOM
		call	scanoff
		jmp	bx
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

IFNOT:					; ...
		not	ds:IFNOTFLAG
		inc	ds:IF_NOT_COUNT
		jmp	short IFREENT
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

IFSTRING:				; ...
		push	si
		xor	cx, cx

FIRST_STRING:				; ...
		lodsb
		cmp	al, 0Dh
		jz	short IFERRORP
		call	DELIM
		jz	short EQUAL_CHECK
		inc	cx
		jmp	short FIRST_STRING
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

EQUAL_CHECK:				; ...
		cmp	al, '='
		jz	short EQUAL_CHECK2
		cmp	al, 0Dh
		jz	short IFERRPJ
		lodsb
		jmp	short EQUAL_CHECK
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

EQUAL_CHECK2:				; ...
		lodsb
		cmp	al, '='
		jnz	short IFERRPJ
		call	scanoff
		cmp	al, 0Dh
		jz	short IFERRPJ
		pop	di
		repe cmpsb
		jz	short MATCH
		dec	si
		cmp	byte ptr [si], 0Dh
		jz	short IFERRJ

SKIPSTRINGEND:				; ...
		lodsb

NOTMATCH:				; ...
		cmp	al, 0Dh

IFERRORJ2:				; ...
		jz	short IFERRJ
		call	DELIM
		jnz	short SKIPSTRINGEND
		mov	al, 0FFh	; -1
		jmp	short IFRET
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

IFERRPJ:				; ...
		jmp	IFERRORP
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

MATCH:					; ...
		lodsb
		call	DELIM
		jnz	short NOTMATCH
		xor	al, al
		jmp	short IFRET
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

IFEXISTS:				; ...
		lodsb
		call	DELIM
		jnz	short IFEXISTS
		mov	dx, offset BWDBUF ; DIRBUF
		mov	ax, 1A00h
		int	21h		; DOS -	SET DISK TRANSFER AREA ADDRESS
					; DS:DX	-> disk	transfer buffer
		mov	bx, 2
		add	bx, ds:IF_NOT_COUNT
		mov	ax, offset ARG_ARGV ; mov ax,ARG
					; (mov ax,ARG+ARG_UNIT.argv)
		call	argv_calc
		mov	dx, [bx]	; [bx+ARGV_ELE.argpointer]
					; mov dx,[bx+0]
		mov	cx, 6		; IFEXIST_ATTR ; filetypes to search for
		mov	ax, 4E00h
		int	21h		; DOS -	2+ - FIND FIRST	ASCIZ (FINDFIRST)
					; CX = search attributes
					; DS:DX	-> ASCIZ filespec
					; (drive, path,	and wildcards allowed)
		jb	short IF_EX_C
		xor	al, al
		jmp	short IFRET
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

IF_EX_C:				; ...
		mov	al, 0FFh	; -1

IFRET:					; ...
		test	ds:IFNOTFLAG, 0FFh
		jz	short REALTEST
		not	al

REALTEST:				; ...
		or	al, al
		jz	short IFTRUE
		jmp	TCOMMAND
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

IFTRUE:					; ...
		call	scanoff
		mov	cx, si
		sub	cx, 81h
		sub	ds:80h,	cl
		mov	cl, ds:80h
		mov	ds:COMBUF+1, cl
		mov	di, (offset COMBUF+2)
		cld
		rep movsb
		mov	al, 0Dh
		stosb
		push	ds
		mov	ds, ds:RESSEG
		mov	ds:IfFlag, 0FFh	; -1
		pop	ds
		jmp	DOCOM0
_$IF		endp

; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

IFERRORJ3:				; ...
		jmp	IFERRORJ2
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

IFERLEV:				; ...
		mov	bh, 10
		xor	bl, bl

GETNUMLP:				; ...
		lodsb
		cmp	al, 0Dh
		jz	short IFERRORJ3
		call	DELIM
		jz	short GOTNUM
		sub	al, '0'
		xchg	al, bl
		mul	bh
		add	al, bl
		xchg	al, bl
		jmp	short GETNUMLP
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

GOTNUM:					; ...
		push	ds
		mov	ds, ds:RESSEG
		mov	ah, byte ptr ds:RetCode
		pop	ds
		xor	al, al
		cmp	ah, bl
		jnb	short IFRET
		dec	al
		jmp	short IFRET
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

SHIFT:					; ...
		mov	ds, ds:RESSEG
		mov	ax, ds:Batch
		or	ax, ax
		jnz	short SHIFT1

SHIFT_RETN:				; ...
		retn
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

SHIFT1:					; ...
		mov	es, ax
		mov	ds, ax
		mov	di, 0Ch		; BATCHSEGMENT.BatParm
		lea	si, [di+2]
		mov	cx, 9
		rep movsw
		cmp	word ptr [di], 0FFFFh ;	-1
		jz	short SHIFT_RETN
		mov	si, [di]
		mov	word ptr [di], 0FFFFh ;	-1

SKIPCRLP:				; ...
		lodsb
		cmp	al, 0Dh
		jnz	short SKIPCRLP
		cmp	byte ptr [si], 0
		jz	short SHIFT_RETN
		mov	[di], si
		retn

; ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ S U B	R O U T	I N E ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ


SKIPDELIM	proc near		; ...
		test	ds:Batch, 0FFFFh
		jz	short SKIPERR
		call	GETBATBYT
		call	DELIM
		jz	short SKIPDELIM
		clc
		retn
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

SKIPERR:				; ...
		stc

GOTO_RETN:				; ...
		retn
SKIPDELIM	endp


; ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ S U B	R O U T	I N E ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ


_$CALL		proc near		; ...
		push	si
		push	di
		push	ax
		push	cx
		mov	si, (offset COMBUF+2)
		call	scanoff
		add	si, 4		; length_call
		mov	di, (offset COMBUF+2)
		mov	cx, 124		; COMBUFLEN-length_call
		rep movsb
		pop	cx
		pop	ax
		pop	di
		pop	si
		push	ds
		mov	ds, ds:RESSEG
		mov	ds:Call_Flag, 1	; call_in_progress
		mov	ds:Call_Batch_Flag, 1
		cmp	ds:PipeFiles, 0
		jz	short _NOPIPE
		call	PIPEDEL

_NOPIPE:				; ...
		pop	ds
		retn
_$CALL		endp

; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

GOTO:					; ...
		mov	ds, ds:RESSEG
		test	ds:Batch, 0FFFFh
		jz	short GOTO_RETN
		xor	dx, dx
		push	ds
		mov	ds, ds:Batch
		mov	ds:8, dx	; [BATCHSEGMENT.BatSeek]
		mov	ds:10, dx	; [BATCHSEGMENT.BatSeek+2]
		mov	byte ptr ds:2, 0 ; [BATCHSEGMENT.BatchEOF]
		pop	ds
		call	PROMPTBAT
		mov	di, 5Dh		;  FCB+1
		mov	cx, 11
		mov	al, 20h	; ' '
		repne scasb
		jnz	short NOINC
		inc	cx

NOINC:					; ...
		sub	cx, 11
		neg	cx
		mov	es:BWDBUF, cx	; mov [es:GOTOLEN],cx
		call	SKIPDELIM
		jb	short BADGOTO
		cmp	al, ':'
		jz	short CHKLABEL

LABLKLP:				; ...
		call	GETBATBYT
		cmp	al, 0Ah
		jnz	short LABLKTST
		call	SKIPDELIM
		jb	short BADGOTO
		cmp	al, ':'
		jz	short CHKLABEL

LABLKTST:				; ...
		test	ds:Batch, 0FFFFh
		jnz	short LABLKLP

BADGOTO:				; ...
		call	BATCLOSE
		call	free_batch
		push	cs
		pop	ds
		assume ds:TRANGROUP
		mov	dx, offset BADLAB_PTR
		jmp	cerror
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

CHKLABEL:				; ...
		call	SKIPDELIM
		jb	short BADGOTO
		mov	di, 5Dh		; FCB+1
		mov	cx, es:BWDBUF	; mov cx,[es:GOTOLEN]
		jmp	short GOTBYTE
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

NEXTCHRLP:				; ...
		push	cx
		call	GETBATBYT
		pop	cx

GOTBYTE:				; ...
		call	testkanj
		jz	short NOTKANJ1
		cmp	al, es:[di]
		jnz	short LABLKTST
		inc	di
		dec	cx
		jcxz	short LABLKTST
		push	cx
		call	GETBATBYT
		pop	cx
		cmp	al, es:[di]
		jmp	short KNEXTLABCHR
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

NOTKANJ1:				; ...
		or	al, 20h
		cmp	al, es:[di]
		jnz	short TRYUPPER
		jmp	short NEXTLABCHR
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

TRYUPPER:				; ...
		sub	al, 20h
		cmp	al, es:[di]

KNEXTLABCHR:				; ...
		jnz	short LABLKTST

NEXTLABCHR:				; ...
		inc	di
		loop	NEXTCHRLP
		call	GETBATBYT
		cmp	es:BWDBUF, 8	; cmp [es:GOTOLEN],8
		jge	short GOTOCONT
		cmp	al, 20h	; ' '
		ja	short LABLKTST

GOTOCONT:				; ...
		cmp	al, 0Dh
		jz	short SKIPLFEED

TONEXTBATLIN:				; ...
		call	GETBATBYT
		cmp	al, 0Dh
		jnz	short TONEXTBATLIN

SKIPLFEED:				; ...
		call	GETBATBYT
		push	es
		mov	es, ds:Batch
		mov	byte ptr es:2, 0 ; BATCHSEGMENT.BatchEOF
		pop	es
		call	BATCLOSE
		retn

; ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ S U B	R O U T	I N E ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ


BATCLOSE	proc near		; ...
		mov	bx, cs:BATHAND
		cmp	bx, 5
		jb	short CLOSERETURN
		mov	ah, 3Eh
		int	21h		; DOS -	2+ - CLOSE A FILE WITH HANDLE
					; BX = file handle

CLOSERETURN:				; ...
		mov	ds:In_Batch, 0
		retn
