		mov	di, bx
		mov	cx, 13
		xor	al, al
		repne scasb
		push	bx
		mov	bx, di
		call	lsNode
		pop	bx
		pushf
		mov	ax, 0
		push	ax
		mov	ax, '..'
		push	ax
		mov	dx, sp
		call	ChangeDir
		pop	ax
		pop	ax
		popf
		jb	short lsRet
		jmp	short lsLoop
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

lsErr:					; ...
		call	get_ext_error_number
		cmp	ax, 2		; ERROR_FILE_NOT_FOUND
		jz	short lsRet
		cmp	ax, 18		; ERROR_NO_MORE_FILES
