; ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ S U B	R O U T	I N E ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ


CmpEntry	proc near		; ...
		mov	si, offset DestBuf

ceLoop:					; ...
		xor	ax, ax
		mov	al, [si]
		or	al, al
		jz	short ceDone
		inc	si
		push	si
		dec	al
		shl	al, 1
		mov	si, ax
		mov	ax, cs:FieldCmps[si]
		jb	short ceDn
		call	ax
		jmp	short ceNs
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

ceDn:					; ...
		xchg	bx, bp
		call	ax
		xchg	bx, bp

ceNs:					; ...
