		xor	bl, bl
		xchg	bl, [di]
		xor	cx, cx
		mov	ah, 4Eh
		int	21h		; DOS -	2+ - FIND FIRST	ASCIZ (FINDFIRST)
					; CX = search attributes
					; DS:DX	-> ASCIZ filespec
					; (drive, path,	and wildcards allowed)
		xchg	bl, [di]
		jb	short piCol
		test	ds:DIRBUF_find_buf_attr, 40h ; [DIRBUF+21],40h
					; [DIRBUF+FIND_BUF.ATTR],ATTR_DEVICE
