		mov	al, ':'
		mov	cx, ds:PathCnt
		dec	cx
		dec	cx
		or	cx, cx
		js	short piRet
		or	di, di
		std
		repne scasb
		cld

piRet:					; ...
		retn
FileIsDevice	endp


