; ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ S U B	R O U T	I N E ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ


FindInMem	proc near		; ...
		mov	es, ds:TPA
		xor	bx, bx
		cld
		call	FindOneInMem
		jb	short fiNoMore

fiBest:					; ...
		mov	bp, bx

fiNext:					; ...
		call	FindNextInMem
		jb	short fiFound
		call	CmpEntry
		jnb	short fiNext
		jmp	short fiBest
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

fiNoMore:				; ...
		mov	ax, 18		; ERROR_NO_MORE_FILES
		stc
		jmp	short fiRet
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

fiFound:				; ...
