; ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ S U B	R O U T	I N E ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ


FindNextChild	proc near		; ...
		sub	sp, 12
		mov	ax, 0FFh
		push	ax
		mov	bp, sp
		mov	ax, '*'         ; 002Ah
		push	ax
		mov	ax, 2E2Ah	; '*.'
		push	ax
		call	GetDriveLtr
		push	ax
		mov	dx, sp
		mov	cx, 10h		; ATTR_DIRECTORY
		mov	ah, 4Eh
		int	21h		; DOS -	2+ - FIND FIRST	ASCIZ (FINDFIRST)
					; CX = search attributes
					; DS:DX	-> ASCIZ filespec
					; (drive, path,	and wildcards allowed)
		jb	short fcRet
		call	CheckChild

fcNext:					; ...
		mov	cx, 10h
		mov	ah, 4Fh
		int	21h		; DOS -	2+ - FIND NEXT ASCIZ (FINDNEXT)
					; [DTA]	= data block from
					; last AH = 4Eh/4Fh call
		jb	short fcErr
		call	CheckChild
		jmp	short fcNext
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

fcErr:					; ...
		call	get_ext_error_number
		cmp	ax, 18		; ERROR_NO_MORE_FILES
		jnz	short fcNope
		cmp	byte ptr [bp+0], 0FFh
		jz	short fcNope
		mov	si, bp
		mov	di, bx

fcMove:					; ...
		lodsb
		stosb
		or	al, al
		jz	short fcRet
		jmp	short fcMove
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

fcNope:					; ...
		stc

fcRet:					; ...
		lahf
		add	sp, 20
		sahf
		retn
FindNextChild	endp


; ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ S U B	R O U T	I N E ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ


FindOneInMem	proc near		; ...

; FUNCTION CHUNK AT 163F SIZE 00000002 BYTES

		mov	al, es:[bx]
		cmp	al, 1
		jz	short FindNextInMem
		cmp	al, 0FFh
		jz	short foNoMore
		clc
		retn
FindOneInMem	endp


; ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ S U B	R O U T	I N E ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ


FindNextInMem	proc near		; ...
		add	bx, 22		; size EntryStruc (22 =	21 + compratio)
