FindNextInMem	endp

; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ
; START	OF FUNCTION CHUNK FOR FindOneInMem

foNoMore:				; ...
		stc
		retn
; END OF FUNCTION CHUNK	FOR FindOneInMem

; ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ S U B	R O U T	I N E ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ


GetEnvValue	proc near		; ...
		push	es
		mov	si, offset DirEnvVar ; "DIRCMD="
		call	find_name_in_environment
		jb	short geRet
		push	ds
		push	es
		pop	ds
		pop	es
		mov	si, di
		mov	di, offset ScanBuf

geLoop:					; ...
		lodsb
		or	al, al
		stosb
		loopne	geLoop
		push	es
		pop	ds
		mov	si, offset ScanBuf

geRet:					; ...
		pop	es
		retn
GetEnvValue	endp


; ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ S U B	R O U T	I N E ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ


GetFirst	proc near		; ...
		mov	byte ptr ds:55h, 0FFh ;	[FCB-7]
		mov	byte ptr ds:5Bh, 16h ; mov byte	[FCB-1],ATTR_ALL
		mov	dx, 55h		; FCB-7
		mov	ah, 11h
		int	21h		; DOS -	SEARCH FIRST USING FCB
					; DS:DX	-> FCB
		shl	al, 1
		jb	short gfRet
		jmp	short gfFound
GetFirst	endp


; ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ S U B	R O U T	I N E ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ


GetNext		proc near		; ...
		mov	dx, 55h
		mov	ah, 12h
		int	21h		; DOS -	SEARCH NEXT USING FCB
					; DS:DX	-> FCB
					; Return: AL = status
		shl	al, 1
		jb	short gfRet

gfFound:				; ...
		mov	al, ds:DIRBUF_8_DIR_ATTR ;
					; [DIRBUF+8+DIR_ENTRY.DIR_ATTR]
		mov	ah, ds:AttrSpecified
		and	al, ah
		and	ah, ds:AttrSelect
		cmp	al, ah
		jnz	short GetNext

gfRet:					; ...
		retn
GetNext		endp


; ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ S U B	R O U T	I N E ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ


ListDir		proc near		; ...
		xor	ax, ax
		mov	ds:FileCnt, ax	; zero file count
		mov	ds:FileSiz, ax	; zero file size accumulator
		mov	ds:FileSiz+2, ax
		mov	ds:ccluUsedDir,	ax ; zero count	clusters used
		mov	ds:csecUsedDir,	ax ; zero count	compressed sectors used
		mov	ds:csecUsedDir+2, ax
		cmp	ds:DestBuf, 0	; check	for sort code
		jz	short ld1
		call	LoadEntries	; load entries for sorted listing
		jnb	short ld1
		call	get_ext_error_number
		stc
		jmp	short ldErr
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

ld1:					; ...
		call	FindFirst
		jb	short ldErr
		call	DisplayHeader
		call	DisplayFile

ldNext:					; ...
		call	FindNext
		jb	short ldErr
		call	DisplayFile
		jmp	short ldNext
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

ldErr:					; ...
		cmp	ax, 2		; ERROR_FILE_NOT_FOUND
		jz	short ldDone
		cmp	ax, 18		; ERROR_NO_MORE_FILES
		jz	short ldDone
		stc
		jmp	short ldRet
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

ldDone:					; ...
		cmp	ds:FileCnt, 0
		jz	short ld2
		call	DisplayTrailer

ld2:					; ...
		clc

ldRet:					; ...
		retn
ListDir		endp


; ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ S U B	R O U T	I N E ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ


LoadEntries	proc near		; ...
		push	es
		mov	es, ds:TPA
		xor	di, di
		and	ds:_Bits, 0FFBFh ;  ~mask.inmem	; not mask.inmem
		call	GetFirst
		jb	short leRet
		call	LoadEntry

leNext:					; ...
		call	GetNext
		jb	short leLoaded
		mov	ax, ds:BYTCNT
		sub	ax, di
		cmp	ax, 24		; size EntryStruc+2 (23	+ compratio)
		jb	short leOk
		call	LoadEntry
		jmp	short leNext
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

leLoaded:				; ...
		mov	byte ptr es:[di], 0FFh
		or	ds:_Bits, 40h	; mask.inmem

leOk:					; ...
		clc

leRet:					; ...
		pop	es
		retn
LoadEntries	endp


; ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ S U B	R O U T	I N E ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ


LoadEntry	proc near		; ...
		mov	si, offset DIRBUF_8 ; DIRBUF+8
		xor	al, al
		stosb
		mov	cx, 11
		rep movsb
		lodsb
		stosb
		add	si, 10		; (DIR_ENTRY.DIR_TIME-DIR_ENTRY.DIR_ATTR)-1
		movsw
		movsw
		mov	cl, al
		lodsw
		and	cl, 10h		; ATTR_DIRECTORY
		jnz	short leSetDirSize
		movsw
		movsw
		test	ds:_Bits, 1	; mask.cratio
		jnz	short leCalcRatio
		xor	ax, ax
		stosb
		retn
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

leCalcRatio:				; ...
		call	CalcCompRatio
		or	ax, ax
		jz	short leNoRatio
		dec	ah
		mov	cl, 4
		shl	ah, cl
		or	al, ah
		stosb
		retn
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

leSetDirSize:				; ...
		xor	ax, ax
		stosw
		stosw

leNoRatio:				; ...
		dec	al
		stosb
		retn
LoadEntry	endp


; ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ S U B	R O U T	I N E ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ


NoOrder		proc near		; ...
		mov	ds:DestBuf, 0	; no sort
		clc			; no error
		retn
NoOrder		endp


; ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ S U B	R O U T	I N E ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ


OnOffSw		proc near		; ...
		mov	cx, di
		shr	cx, 1
		shr	cx, 1
		mov	ax, 1
		shl	ax, cl
		test	di, 2
		jz	short oo1
		or	ds:_Bits, ax
		jmp	short ooRet
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

oo1:					; ...
		not	ax
		and	ds:_Bits, ax

ooRet:					; ...
		clc
		retn
OnOffSw		endp


; ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ S U B	R O U T	I N E ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ


ParseAttr	proc near		; ...
		push	si
		mov	ds:AttrSpecified, 0
		mov	si, [bx+4]	; [bx+ResultBuffer.ValuePtr]

paLoop:					; ...
		mov	dx, 1
		lodsb
		or	al, al
		jz	short paOk
		cmp	al, '-'
		jnz	short pa1
		dec	dx
		lodsb

pa1:					; ...
		mov	di, offset AttrLtrs ; "RHSvDA"
		mov	cx, 6		; NUM_ATTR_LTRS
		repne scasb
		jnz	short paErr
		not	cx
		add	cx, 6		; NUM_ATTR_LTRS
		mov	al, 1
		shl	al, cl
		or	ds:AttrSpecified, al
		not	al
		and	ds:AttrSelect, al
		shl	dl, cl
		or	ds:AttrSelect, dl
		jmp	short paLoop
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

paErr:					; ...
		call	SetupParamError
		stc
		jmp	short paRet
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

paOk:					; ...
		clc

paRet:					; ...
		pop	si
		retn
ParseAttr	endp


; ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ S U B	R O U T	I N E ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ


Parse_Line	proc near		; ...
		mov	di, offset PARSE_DIR
		xor	cx, cx

