		pop	bx
		call	SetupParamError
		stc
		jmp	short poRet
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

poOk:					; ...
		mov	byte ptr [bx], 0
		pop	bx
		clc

poRet:					; ...
		pop	si
		retn
ParseOrder	endp


; ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ S U B	R O U T	I N E ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ


ParseRatio	proc near		; ...
		call	OnOffSw
		push	si
		mov	si, [bx+4]	; mov si,word ptr [bx].ValuePtr
		mov	al, [si]
		or	al, al
		jz	short prDone
		cmp	al, 'H'
		jz	short prDone
		call	SetupParamError
		stc
		jmp	short prRet
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

prDone:					; ...
		mov	ds:fUseHostSize, al ; set Host cluster size flag
		clc

prRet:					; ...
		pop	si
		retn
ParseRatio	endp

; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

ParseSwitch:				; ...
		push	cx
		push	di
		mov	ax, [bx+2]	; [bx+ResultBuffer.SynPtr]
		mov	di, offset Dir_Sw_Ptrs
		mov	cx, 16		; NUM_DIR_SWS
		cld
		repne scasw
		sub	di, offset Dir_Sw_Ptrs_2 ; Dir_Sw_Ptrs+2
		call	cs:SwHandler[di] ; call	word [cs:di+SwHandler]
		pop	di
		pop	cx
		retn
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ
SwHandler	dw offset OnOffSw	; ...
		dw offset ParseRatio
		dw offset OnOffSw
		dw offset OnOffSw
		dw offset OnOffSw
		dw offset OnOffSw
		dw offset OnOffSw
		dw offset OnOffSw
		dw offset OnOffSw
		dw offset OnOffSw
		dw offset OnOffSw
		dw offset OnOffSw
		dw offset NoOrder
		dw offset ParseOrder
		dw offset DefaultAttr
		dw offset ParseAttr
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

ChangeDir:				; ...
		mov	di, offset BWDBUF ; DIRBUF
		call	GetDriveLtr
		stosw
		mov	si, dx

cdLoop:					; ...
		lodsb
		stosb
		or	al, al
		jnz	short cdLoop
		mov	dx, offset BWDBUF ; DIRBUF
		mov	ah, 3Bh
		int	21h		; DOS -	2+ - CHANGE THE	CURRENT	DIRECTORY (CHDIR)
					; DS:DX	-> ASCIZ directory name	(may include drive)
		retn

; ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ S U B	R O U T	I N E ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ


CmpAscz		proc near		; ...
		push	di
		mov	di, si
		xor	al, al
		mov	cx, 0FFFFh
		repne scasb
		not	cx
		pop	di
		repe cmpsb
		retn
CmpAscz		endp


; ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ S U B	R O U T	I N E ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ


CopyPathname	proc near		; ...
