CopyPathname	endp


; ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ S U B	R O U T	I N E ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ


CountFile	proc near		; ...
		push	es
		mov	es, ds:TPA
		inc	ds:FileCnt
		inc	ds:FileCntTotal
		jnz	short cntf1
		inc	ds:FileCntTotal+2

cntf1:					; ...
		mov	ax, es:[bx+17]	; EntryStruc.filesize
		mov	dx, es:[bx+19]	; EntryStruc.filesize +	2
		add	ds:FileSiz, ax
		adc	ds:FileSiz+2, dx
		add	word ptr ds:FileSizTotal, ax
		adc	word ptr ds:FileSizTotal+2, dx
		pop	es
		retn
CountFile	endp


; ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ S U B	R O U T	I N E ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ


DisplayBare	proc near		; ...
		mov	cx, ds
		mov	ds, ds:TPA
		cmp	byte ptr [bx+1], '.' ; [bx+EntrStruc.filename]
		mov	ds, cx
		jz	short dbRet
		test	ds:_Bits, 8	; mask.subd
		jz	short dbNameExt
		call	build_dir_string
		mov	di, offset BWDBUF
		test	ds:_Bits, 20h	; mask.lcase
		jz	short dbare1
		mov	si, di
		call	LowercaseString

dbare1:					; ...
		xor	al, al
		mov	cx, 0FFFFh
		cld
		repne scasb
		dec	di
		cmp	byte ptr es:[di-1], '\'
		jz	short dbare2
		mov	ax, '\'
		stosw

dbare2:					; ...
		mov	ds:string_ptr_2, offset	BWDBUF
		mov	dx, offset string_buf_ptr
		call	std_printf

dbNameExt:				; ...
		call	DisplayDotForm
		call	CRLF2
		call	UseLine

dbRet:					; ...
		retn
DisplayBare	endp


; ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ S U B	R O U T	I N E ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ


DisplayDotForm	proc near		; ...
		push	ds
		push	es
		mov	ax, cs:TPA
		mov	ds, ax
		mov	es, ax
		mov	di, bx
		add	di, 8		; filename + size filename - 1
		mov	cx, 8		; size filename	= 8 ; size of EntryStruc.filename
		mov	al, 20h	; ' '
		std
		repe scasb
		inc	cx
		mov	dx, cx
		mov	si, bx
		add	si, 1

NextNameChar:				; ...
		cld
		lodsb
		test	ss:_Bits, 20h	; mask.lcase
		jz	short ddf1
		call	LowerCase

ddf1:					; ...
		call	PRINT_CHAR
		loop	NextNameChar
		mov	di, bx
		add	di, 11		; fileext + size fileext - 1
		mov	cx, 3		; size fileext
		mov	al, 20h	; ' '
		std
		repe scasb
		jz	short ddDone
		inc	cx
		add	dx, cx
		inc	dx
		mov	al, '.'
		call	PRINT_CHAR
		mov	si, bx
		add	si, 9		; EntryStruc.fileext

NextExtChar:				; ...
		cld
		lodsb
		test	cs:_Bits, 20h
		jz	short ddf2
		call	LowerCase

ddf2:					; ...
		call	PRINT_CHAR
		loop	NextExtChar

ddDone:					; ...
		pop	es
		pop	ds
		cld
		retn
DisplayDotForm	endp


; ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ S U B	R O U T	I N E ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ


DisplayFile	proc near		; ...
		test	ds:_Bits, 10h	; mask.bare
		jz	short dfNorm
		call	DisplayBare
		jmp	short dfRet
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

dfNorm:					; ...
		call	DisplayNext
		test	ds:_Bits, 2
		jz	short dfFul
		call	DisplayWide
		jmp	short dfCnt
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

dfFul:					; ...
		call	DisplayName
		call	DisplayTheRest
		test	ds:_Bits, 1	; mask.cratio
		jz	short dfCnt
		call	DisplayCompRatio

dfCnt:					; ...
		call	CountFile

dfRet:					; ...
		retn
DisplayFile	endp


; ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ S U B	R O U T	I N E ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ


DisplayHeader	proc near		; ...
		test	ds:_Bits, 10h	; mask.bare
		jnz	short dhRet
		test	ds:_Bits, 8	; mask.subd
		jz	short dhNorm
		call	CRLF2
		call	UseLine
		jmp	short dhCom
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

dhNorm:					; ...
		mov	al, 20h	; ' '
		call	PRINT_CHAR

dhCom:					; ...
		call	build_dir_string
		mov	dx, offset dirhead_ptr
		call	std_printf
		call	UseLine
		call	CRLF2
		call	UseLine

dhRet:					; ...
		retn
DisplayHeader	endp


; ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ S U B	R O U T	I N E ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ


DisplayName	proc near		; ...
		push	ds
		mov	ds, ds:TPA
		mov	si, bx		; DS:SI	= ptr to entry
		add	si, 1		; EntryStruc.filename
		mov	di, offset CHARBUF
		mov	cx, 8
		cld
		rep movsb		; move filename	to CharBuf
		mov	al, 20h	; ' '
		stosb			; add a	blank
		mov	cx, 3
		rep movsb		; add extension
		xor	al, al
		stosb			; add a	NULL
		pop	ds
		test	ds:_Bits, 20h	; mask.lcase
		jz	short dn1	; lowercase not	required
		mov	si, offset CHARBUF
		call	LowercaseString

dn1:					; ...
		mov	ds:string_ptr_2, offset	CHARBUF
		mov	dx, offset string_buf_ptr
		call	std_printf
		retn
DisplayName	endp


; ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ S U B	R O U T	I N E ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ


DisplayNext	proc near		; ...
		cmp	ds:FileCnt, 0
		jz	short dn1st
		cmp	ds:LeftOnLine, 0
		jle	short dnEol
		mov	dx, offset tab_ptr
		call	std_printf
		jmp	short dnDone
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

dnEol:					; ...
		call	CRLF2
		call	UseLine

dn1st:					; ...
		mov	al, ds:PerLine
		mov	ds:LeftOnLine, al

dnDone:					; ...
		dec	ds:LeftOnLine
		retn
DisplayNext	endp


; ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ S U B	R O U T	I N E ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ


DisplayTheRest	proc near		; ...
		push	es
		mov	es, ds:TPA
		mov	bp, bx
		test	byte ptr es:[bp+12], 10h ;
					; [es:bp+EntryStruc.fileattr],ATTR_DIRECTORY
		jz	short drNonDir
		mov	dx, offset dmes_ptr
		call	std_printf
		test	ds:screen_f_1, 40h ; 80	columns	?
		jz	short dr_0	; no
		mov	dx, offset space_4_ptr ; 4 space chars
		call	std_printf

dr_0:					; ...
		jmp	short drCom
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

drNonDir:				; ...
		mov	dx, es:[bp+17]	; [es:bp+EntryStruc.filesize]
		mov	ds:File_Size_Low, dx
		mov	dx, es:[bp+19]	; [es:bp+EntryStruc.filesize+2]
		mov	ds:File_Size_High, dx
		mov	dx, offset disp_file_size_ptr
		call	std_printf

drCom:					; ...
		mov	ax, es:[bp+15]	; [es:bp+EntryStruc.filedate]
		or	ax, ax
		jz	short drDone
		mov	bx, ax
		and	ax, 1Fh
		mov	dl, al
		mov	ax, bx
		mov	cl, 5
		shr	ax, cl
		and	al, 0Fh
		mov	dh, al
		mov	cl, bh
		shr	cl, 1
		xor	ch, ch
		add	cx, 80
		cmp	cl, 100
		jb	short dr_1
		sub	cl, 100

dr_1:					; ...
		xchg	dh, dl
		mov	ds:DirDat_Yr, cx
		mov	ds:DirDat_Mo_Day, dx
		mov	cx, es:[bp+13]	; [es:bp+EntryStruc.filetime]
		jcxz	short drPrint
		shr	cx, 1
		shr	cx, 1
		shr	cx, 1
		shr	cl, 1
		shr	cl, 1
		xchg	ch, cl
		mov	ds:DirTim_Hr_Min, cx

drPrint:				; ...
		mov	dx, offset dirdattim_ptr
		call	std_printf

drDone:					; ...
		pop	es
		mov	bx, bp
		retn
DisplayTheRest	endp


; ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ S U B	R O U T	I N E ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ


DisplayCompRatio proc near		; ...
		push	es
		mov	es, ds:TPA
		mov	al, es:[bx+15h]	; mov al, es:[bx].compratio
					; mov al,[es:bx+EntryStruc.compratio]
		cmp	al, 0FFh
		jz	short dcrRet
		mov	ah, al
		mov	cl, 4
		shr	ah, cl
		inc	ah
		and	al, 0Fh
		mov	ds:Dir_CRatio_1, ah
		mov	ds:Dir_CRatio_2, al
		mov	dx, offset DirCompRatio_Ptr
		call	std_printf

dcrRet:					; ...
		pop	es
		retn
DisplayCompRatio endp


; ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ S U B	R O U T	I N E ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ


DisplayTrailer	proc near		; ...
		test	ds:_Bits, 10h	; mask.bare
		jnz	short dtrRet
		call	CRLF2
		call	UseLine
		mov	ax, ds:FileCnt

DisplayCntSiz:				; ...
		test	ds:_Bits, 1	; mask.cratio
		jz	short dtrSize
		push	ax
		mov	ax, ds:ccluUsedDir
		or	ax, ax
		jz	short dtrSkipRatio
		mov	cx, ds:csecUsedDir+2
		mov	bx, ds:csecUsedDir
		call	ComputeRatio
		mov	ds:Dir_CRatio_1, ah
		mov	ds:Dir_CRatio_2, al
		mov	dx, offset AveCompRatio_Ptr
		call	std_printf
		call	UseLine

dtrSkipRatio:				; ...
		pop	ax

dtrSize:				; ...
		mov	ds:Dir_Num, ax
		mov	dx, offset dirmes_ptr
		call	std_printf
		mov	dx, offset bytes_ptr
		call	std_printf
		call	UseLine

dtrRet:					; ...
		retn
DisplayTrailer	endp


; ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ S U B	R O U T	I N E ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ


DisplayWide	proc near		; ...
		push	ds
		mov	ds, ds:TPA
		test	byte ptr [bx+12], 10h ;
					; [bx+EntryStruc.fileattr],ATTR_DIRECTORY
		jz	short dw1
		mov	al, '['
		call	PRINT_CHAR

dw1:					; ...
		call	DisplayDotForm
		test	byte ptr [bx+12], 10h
		jz	short dw2
		mov	al, ']'
		call	PRINT_CHAR

dw2:					; ...
		mov	cx, 12		; size filename	+ size fileext + 1
		sub	cx, dx
		jcxz	short dwDone
		mov	al, 20h	; ' '

dw3:					; ...
		call	PRINT_CHAR
		loop	dw3

dwDone:					; ...
		pop	ds
		retn
DisplayWide	endp


; ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ S U B	R O U T	I N E ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ


EndPage		proc near		; ...
		test	ds:_Bits, 4	; mask.pagd
		jz	short epNew
		push	bx
		push	cx
		call	PAUSE
		call	build_dir_string
		mov	dx, offset dircont_ptr
		call	Printf_Crlf
		pop	cx
		pop	bx

epNew:					; ...
		mov	ax, ds:LinPerPag
		dec	ax
		mov	ds:LeftOnPage, ax
		retn
EndPage		endp


; ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ S U B	R O U T	I N E ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ


GetDriveLtr	proc near		; ...
		mov	al, ds:5Ch	; [FCB]
		or	al, al
		jnz	short gdl1
		mov	al, ds:CURDRV
		inc	al

gdl1:					; ...
		add	al, 40h		; 'A'-1
		mov	ah, ':'
		retn
GetDriveLtr	endp


; ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ S U B	R O U T	I N E ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ


SetupParamError	proc near		; ...
		mov	ax, 9
		mov	ds:msg_disp_class, 2 ; parse_msg_class
		mov	ds:extend_buf_ptr, ax
		mov	si, [bx+4]	; [bx+ResultBuffer.ValuePtr]
		mov	ds:string_ptr_2, si
		mov	ds:extend_buf_sub, 1 ; one_subst
		mov	dx, offset extend_buf_ptr
		retn
SetupParamError	endp


; ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ S U B	R O U T	I N E ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ


UseLine		proc near		; ...
		dec	ds:LeftOnPage
		cmp	ds:LeftOnPage, 2
		ja	short ulRet
		call	EndPage

ulRet:					; ...
		retn
UseLine		endp


; ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ S U B	R O U T	I N E ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ


ZeroTotals	proc near		; ...
		mov	di, offset FileCntTotal
		mov	cx, 26		; size FileCntTotal + size FileSizTotal
					; + csecSIZE + ccluSIZE
		xor	al, al
		rep stosb
		retn
ZeroTotals	endp

; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

CtrlCHandler:				; ...
		push	ds
		push	cs
		pop	ds
		assume ds:TRANGROUP
		push	ax
		push	bx
		push	dx
		call	CloseCVF	; close	CVF file if open
		call	RestUDir	; restore user's default directory
		pop	dx
		pop	bx
		pop	ax
		pop	ds
		assume ds:nothing
		jmp	cs:OldCtrlCHandler

; ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ S U B	R O U T	I N E ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ


LowerCase	proc near		; ...
		cmp	al, 'A'
		jb	short lcRet
		cmp	al, 'Z'
		ja	short lcRet
		or	al, 20h

lcRet:					; ...
		retn
LowerCase	endp


; ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ S U B	R O U T	I N E ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ


LowercaseString	proc near		; ...
		push	di
		mov	di, si
		cld

NextChar:				; ...
		lodsb
		or	al, al
		jz	short EndOfString
		call	LowerCase
		stosb
		jmp	short NextChar
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

EndOfString:				; ...
		pop	di
		retn
LowercaseString	endp


; ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ S U B	R O U T	I N E ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ


screen_f_set	proc near		; ...
		push	ds
		mov	ax, 40h
		mov	ds, ax
		assume ds:nothing
		cmp	word ptr ds:4Ah, 40 ; Check ROMBIOS DATA colums	per row	value (80 or 40)
		pop	ds
		assume ds:nothing
		jnz	short columns_80 ; 80 columns per line (video mode 3)
		and	ds:screen_f_1, 0BFh ; ~40h ; not 40h
		mov	word ptr ds:screen_f_2,	0A0Ah ;	10 bytes (file size field)
		and	ds:screen_f_3, 0BFh
		and	ds:screen_f_4, 0BFh
		mov	word ptr ds:screen_f_5,	0A0Ah
		and	ds:screen_f_6, 0BFh
		mov	word ptr ds:screen_f_7,	1C1Ch ;	28 bytes (free bytes field)
		jmp	short screen_f_set_retn
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

columns_80:				; ...
		or	ds:screen_f_1, 40h
		mov	word ptr ds:screen_f_2,	0E0Eh ;	14 bytes (file size field)
		or	ds:screen_f_3, 40h
		or	ds:screen_f_4, 40h
		mov	word ptr ds:screen_f_5,	0E0Eh
		or	ds:screen_f_6, 40h
		mov	word ptr ds:screen_f_7,	2020h ;	32 bytes (free bytes field)

screen_f_set_retn:			; ...
		retn
screen_f_set	endp


; ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ S U B	R O U T	I N E ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ


OpenCVF		proc near		; ...
		mov	ax, 0FFFFh	; -1
		mov	ds:fhCVF, ax	; CVF not open
		mov	ds:entInBuf, ax	; no FAT entries in buffers
		mov	dl, ds:5Ch	; [FCB]
		or	dl, dl
		jz	short ocvf_default
		dec	dl
		jmp	short ocvf_swap_info
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

ocvf_errorj:				; ...
		jmp	ocvf_error
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

ocvf_default:				; ...
		mov	dl, ds:CURDRV

ocvf_swap_info:				; ...
		mov	ax, 4A11h	; multMagicDrv
		mov	bx, 1		; MD_DRIVE_MAP
		int	2Fh
		or	ax, ax
		jnz	short ocvf_errorj
		test	bl, 80h
		jz	short ocvf_errorj
		and	bl, 7Fh
		push	bx
		push	dx
		mov	ax, 4A11h
		mov	dl, bl
		mov	bx, 1
		int	2Fh
		pop	dx
		pop	cx
		or	ax, ax
		jnz	short ocvf_error
		and	bl, 7Fh
		xchg	bx, cx
		cmp	dl, cl
		jz	short ocvf_got_host
		mov	bl, cl

ocvf_got_host:				; ...
		mov	di, offset szCVF
		mov	al, bl
		add	al, 'A'
		mov	ah, ':'
		cld
		stosw
		mov	si, offset sCVFRoot ; "\\DBLSPACE."
		mov	cx, 10		; cbCVFRoot
		rep movsb
		add	di, 3
		xor	al, al
		std
		stosb
		mov	al, bh
		mov	bl, 10
		mov	cx, 3

ocvf_1:					; ...
		xor	ah, ah
		div	bl
		add	ah, '0'
		xchg	ah, al
		stosb
		mov	al, ah
		loop	ocvf_1
		cld
		mov	di, offset szCVF
		mov	word ptr [di+4], 5652h ; 'RV'  (DRVSPACE)
		mov	ax, 3D00h
		mov	dx, offset szCVF
		int	21h		; DOS -	2+ - OPEN DISK FILE WITH HANDLE
					; DS:DX	-> ASCIZ filename
					; AL = access mode
					; 0 - read
		jnb	short ocvf_2
		mov	word ptr [di+4], 4C42h ; 'BL'  (DBLSPACE)
		mov	ax, 3D00h
		mov	dx, offset szCVF
		int	21h		; DOS -	2+ - OPEN DISK FILE WITH HANDLE
					; DS:DX	-> ASCIZ filename
					; AL = access mode
					; 0 - read
		jb	short ocvf_error

ocvf_2:					; ...
		mov	ds:fhCVF, ax
		mov	bx, ax
		mov	ah, 3Fh		; READ
		mov	cx, 64		; size MD_BPB
		mov	dx, offset MDBPB
		int	21h		; DOS -	2+ - READ FROM FILE WITH HANDLE
					; BX = file handle, CX = number	of bytes to read
					; DS:DX	-> buffer
		jb	short ocvf_error1
		cmp	ax, cx
		jz	short ocvf_pick_cluster_size

ocvf_error1:				; ...
		call	CloseCVF

ocvf_error:				; ...
		stc
		jmp	short ocvf_ret
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

ocvf_pick_cluster_size:			; ...
		cmp	ds:fUseHostSize, 0
		jz	short ovcf_use_CVF_size
		mov	ah, 1Ch		; Get_Drive_Data
		mov	dl, ds:szCVF
		sub	dl, 40h		; 'A'-1
		push	ds
		int	21h		; DOS -	GET ALLOCATION TABLE INFORMATION FOR SPECIFIC DRIVE
					; DL = drive number to check (0=default, 1=A, etc)
		pop	ds
		cmp	al, 0FFh
		jnz	short ovcf_set_size

ovcf_use_CVF_size:			; ...
		mov	al, ds:MDBPB+0Dh ; mov al,[MDBPB.dos_bpb.csecPerClu]

ovcf_set_size:				; ...
		mov	ds:csecPerCluster, al
		mov	ax, ds:BYTCNT
		mov	ds:savBytCnt, ax
		cmp	ax, 8000h	; 32*1024
		jnb	short ocvf_big_buf
		mov	ds:cFATEntries,	32 ; cRES_FAT_ENTRIES
		mov	ds:segFATBuf, ds
		mov	ds:pbufDOSFAT, offset bufDOSFAT
		mov	ds:pbufMDFAT, offset bufMDFAT
		jmp	short ocvf_success
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

ocvf_big_buf:				; ...
		mov	bx, 256		; cBIG_FAT_ENTRIES
		mov	ds:cFATEntries,	bx
		shl	bx, 1		; 6 bytes per entry (2 for DOS FAT, 4 MD FAT)
		mov	cx, bx		; entries * 2
		shl	bx, 1
		add	bx, cx		; bx = # entries * 6
		sub	ax, bx
		and	ax, 0FE00h
		mov	ds:BYTCNT, ax
		mov	bx, ds:TPA
		mov	ds:segFATBuf, bx
		mov	ds:pbufDOSFAT, ax
		add	ax, cx
		mov	ds:pbufMDFAT, ax

ocvf_success:				; ...
		clc

ocvf_ret:				; ...
		retn
OpenCVF		endp


; ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ S U B	R O U T	I N E ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ


CloseCVF	proc near		; ...
		mov	bx, ds:fhCVF
		cmp	bx, 0FFFFh	; -1
		jz	short ccvf_ret
		mov	ah, 3Eh		; CLOSE
		int	21h		; DOS -	2+ - CLOSE A FILE WITH HANDLE
					; BX = file handle
		mov	ds:fhCVF, 0FFFFh ; -1
		mov	ax, ds:savBytCnt
		mov	ds:BYTCNT, ax

ccvf_ret:				; ...
		retn
CloseCVF	endp


; ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ S U B	R O U T	I N E ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ


CalcCompRatio	proc near		; ...
		push	bx
		push	cx
		push	dx
		push	es
		mov	es, ds:segFATBuf
		xor	bx, bx
		mov	ds:ccluUsed, bx
		mov	ds:csecUsed, bx
		mov	ds:csecUsed+2, bx

ccr_next:				; ...
		cmp	ax, 2
		jb	short ccr_screwy
		cmp	ax, 0FFF0h
		jnb	short ccr_eof
		call	CheckFATBuffers
		jb	short ccr_screwy
		call	GetMDFATEntry
		jb	short ccr_screwy
		shl	bx, 1
		jnb	short ccr_screwy
		mov	ch, bh
		shl	bx, 1
		and	bx, 0F00h
		xchg	bh, bl
		inc	bx
		add	ds:csecUsed, bx
		adc	ds:csecUsed+2, 0
		mov	dx, ax
		mov	al, ch
		mov	cl, 3
		shr	al, cl
		and	ax, 0Fh
		dec	bx
		cmp	ax, bx
		jnb	short ccr_1
		mov	ax, bx

ccr_1:					; ...
		mov	cl, ds:csecPerCluster
		xor	ch, ch
		add	ax, cx
		div	cl
		xor	ah, ah
		add	ds:ccluUsed, ax
		mov	ax, dx
		call	GetDOSFATEntry
		jb	short ccr_screwy
		jmp	short ccr_next
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

ccr_eof:				; ...
		mov	ax, ds:ccluUsed
		add	ds:ccluUsedDir,	ax
		add	ds:ccluUsedTotal, ax
		mov	cx, ds:csecUsed+2
		mov	bx, ds:csecUsed
		add	ds:csecUsedDir,	bx
		adc	ds:csecUsedDir+2, cx
		add	word ptr ds:csecUsedTotal, bx
		adc	word ptr ds:csecUsedTotal+2, cx
		call	ComputeRatio
		jmp	short ccr_ret
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

ccr_screwy:				; ...
		xor	ax, ax

ccr_ret:				; ...
		pop	es
		pop	dx
		pop	cx
		pop	bx
		retn
CalcCompRatio	endp


; ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ S U B	R O U T	I N E ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ


ComputeRatio	proc near		; ...
		push	si
		push	di
		mov	si, bx
		mov	di, cx
		mov	bl, ds:csecPerCluster
		xor	bh, bh
		mul	bx
		mov	bx, si
		call	Div32
		push	ax
		mov	ax, bx
		or	ax, cx
		jz	short cr_got_tenths
		mov	ax, cx
		xor	dx, dx
		mov	cx, bx
		mov	bx, 10
		mul	bx
		xchg	ax, cx
		mul	bx
		add	dx, cx
		mov	cx, di
		mov	bx, si
		shr	cx, 1
		rcr	bx, 1
		add	ax, bx
		adc	dx, cx
		mov	cx, di
		mov	bx, si
		call	Div32

cr_got_tenths:				; ...
		pop	bx
		mov	ah, bl
		cmp	al, 10
		jb	short cr_exit
		inc	ah
		xor	al, al

cr_exit:				; ...
		pop	di
		pop	si
		retn
ComputeRatio	endp


; ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ S U B	R O U T	I N E ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ


Div32		proc near		; ...
		jcxz	short d32_16bit
		push	si
		push	di
		xor	si, si
		mov	di, si

div32_1:				; ...
		sub	ax, bx
		sbb	dx, cx
		jb	short d32_too_far
		add	si, 1
		adc	di, 0
		jmp	short div32_1
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

d32_too_far:				; ...
		add	ax, bx
		adc	dx, cx
		mov	cx, di
		mov	bx, si
		xchg	ax, bx
		xchg	dx, cx
		pop	di
		pop	si
		retn
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

d32_16bit:				; ...
		div	bx
		mov	bx, dx
		xor	dx, dx
		mov	cx, dx
		retn
Div32		endp


; ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ S U B	R O U T	I N E ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ


GetDOSFATEntry	proc near		; ...
		sub	ax, ds:entInBuf
		jb	short gdf_ret
		mov	bx, ax
		cmp	ds:MDBPB+3Dh, 0	; cmp MDBPB.f12BitFAT, 0
		jnz	short gdf_12
		shl	bx, 1
		add	bx, ds:pbufDOSFAT
		mov	ax, es:[bx]

gdf_success:				; ...
		clc

gdf_ret:				; ...
		retn
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

gdf_12:					; ...
		shr	bx, 1
		add	bx, ax
		add	bx, ds:pbufDOSFAT
		test	al, 1
		mov	ax, es:[bx]
		jnz	short gdf_odd
		and	ax, 0FFFh
		jmp	short gdf_testEOF
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

gdf_odd:				; ...
		mov	bx, cx
		mov	cl, 4
		shr	ax, cl
		mov	cx, bx

gdf_testEOF:				; ...
		cmp	ax, 0FF0h
		jb	short gdf_success
		or	ah, 0F0h
		jmp	short gdf_success
GetDOSFATEntry	endp


; ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ S U B	R O U T	I N E ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ


GetMDFATEntry	proc near		; ...
		mov	bx, ax
		sub	bx, ds:entInBuf
		jb	short gmf_ret
		shl	bx, 1
		shl	bx, 1		; * 4 bytes per	MDFAT entry
		add	bx, ds:pbufMDFAT
		mov	cx, es:[bx]
		mov	bx, es:[bx+2]
		clc

gmf_ret:				; ...
		retn
GetMDFATEntry	endp


; ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ S U B	R O U T	I N E ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ


CheckFATBuffers	proc near		; ...
		mov	bx, ax
		sub	bx, ds:entInBuf
		jb	short cfb_load_fat
		sub	bx, ds:cFATEntries
		jnb	short cfb_load_fat
		clc
		retn
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

cfb_load_fat:				; ...
		push	ax
		push	cx
		push	dx
		xor	cx, cx
		cmp	ds:MDBPB+3Dh, 0	; cmp MDBPB.f12BitFAT, 0
		jnz	short cfb_12
		mov	ds:entInBuf, ax
		shl	ax, 1
		rcl	cx, 1
		jmp	short cfb_common
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

cfb_12:					; ...
		and	al, 0FEh	; not 1
		mov	ds:entInBuf, ax
		mov	bx, ax
		shr	bx, 1
		add	ax, bx

cfb_common:				; ...
		mov	bx, ax
		mov	ax, word ptr ds:MDBPB+27h ; mov	ax, MDBPB.csecMDReserved
					; # magicDrv reserved sectors
		add	ax, word ptr ds:MDBPB+0Eh ; add	ax, MDBPB.dos_bpb.csecReserved
		mul	word ptr ds:MDBPB+0Bh ;	mul MDBPB.dos_bpb.cbPerSec
					; DX:AX	= DOS FAT file origin
		add	ax, bx
		adc	dx, cx
		mov	cx, ds:cFATEntries
		shl	cx, 1
		mov	bx, ds:pbufDOSFAT
		call	ReadCVFile
		jb	short cfb_error
		mov	ax, word ptr ds:MDBPB+24h ; mov	ax, MDBPB.secMDFATStart
		inc	ax
		mul	word ptr ds:MDBPB+0Bh ;	mul MDBPB.dos_bpb.cbPerSec
					; DX:AX	= MDFAT	file offset
					;
		mov	bx, ds:entInBuf
		xor	cx, cx
		add	bx, word ptr ds:MDBPB+2Dh ; add	bx, MDBPB.cluFirstData
					; CX:BX	= MDFAT	entry #
		adc	cx, cx
		shl	bx, 1
		rcl	cx, 1
		shl	bx, 1
		rcl	cx, 1
		add	ax, bx
		adc	dx, cx
		mov	cx, ds:cFATEntries
		shl	cx, 1
		shl	cx, 1
		mov	bx, ds:pbufMDFAT
		call	ReadCVFile
		jnb	short cfb_ret

cfb_error:				; ...
		stc

cfb_ret:				; ...
		pop	dx
		pop	cx
		pop	ax
		retn
CheckFATBuffers	endp


; ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ S U B	R O U T	I N E ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ


ReadCVFile	proc near		; ...
		push	bx
		push	cx
		mov	cx, dx
		mov	dx, ax
		mov	ax, 4200h	; (LSEEK shl 8)	or 0
		mov	bx, ds:fhCVF
		int	21h		; DOS -	2+ - MOVE FILE READ/WRITE POINTER (LSEEK)
					; AL = method: offset from beginning of	file
		jb	short rcf_ret
		mov	ah, 3Fh		; READ
		pop	cx
		pop	dx
		push	ds
		push	es
		pop	ds
		int	21h		; DOS -	2+ - READ FROM FILE WITH HANDLE
					; BX = file handle, CX = number	of bytes to read
					; DS:DX	-> buffer
		pop	ds
		jb	short rcf_ret
		cmp	ax, cx
		jz	short rcf_ret
		stc

rcf_ret:				; ...
		retn
ReadCVFile	endp


; ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ S U B	R O U T	I N E ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ


