; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

good_line:				; ...
		call	PathCrunch
		jnb	short checkdr
		mov	ax, ds:Msg_Numb
		cmp	ax, 0
		jnz	short extend_setup
		cmp	ds:DestIsDir, 0
		jnz	short badpath_err

checkdr:				; ...
		cmp	ds:COMSW, 0
		jz	short notest2j
		jmp	slashp_erase
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

notest2j:				; ...
		jmp	notest2
ERASE		endp

; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ
; START	OF FUNCTION CHUNK FOR BUILDPATH

badpath_err:				; ...
		mov	ax, 3		; ERROR_PATH_NOT_FOUND

extend_setup:				; ...
		mov	ds:msg_disp_class, 1 ; ext_msg_class
		mov	dx, offset extend_buf_ptr
		mov	ds:extend_buf_ptr, ax

errj2:					; ...
		jmp	cerror
; END OF FUNCTION CHUNK	FOR BUILDPATH

; ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ S U B	R O U T	I N E ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ


CRENAME		proc near		; ...
		mov	si, 81h
		mov	di, offset PARSE_RENAME
		xor	cx, cx
		xor	dx, dx
		call	Parse_With_Msg
		cmp	ax, 0
		jnz	short crename_parse_error
		push	si
		lds	si, ds:PARSE1_ADDR
		call	Move_To_SrcBuf
		pop	si
		xor	dx, dx
		call	Parse_With_Msg
		cmp	ax, 0
		jnz	short crename_parse_error
		push	si
		lds	si, ds:PARSE1_ADDR
		mov	al, ':'
		cmp	[si+1],	al
		jnz	short ren_no_drive
		mov	ds:msg_disp_class, 2 ; parse_msg_class
		mov	dx, offset extend_buf_ptr
		mov	ds:extend_buf_ptr, 0Ah ; BadParm_ptr
		pop	si

crename_parse_error:			; ...
		jmp	short errj
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

ren_no_drive:				; ...
		mov	di, 6Ch		; FCB+10h
		mov	ax, 2901h
		int	21h		; DOS -	PARSE FILENAME
					; DS:SI	-> string to parse
					; ES:DI	-> buffer to fill with unopened	FCB
					; AL = bit mask	to control parsing
		lodsb
		mov	ds:One_Char_Val, al
		pop	si
		mov	di, offset PARSE_RENAME
		call	parse_check_eol
		jnz	short crename_parse_error
		call	PathCrunch
		mov	dx, offset BADCPMES_PTR
		jz	short errj2
		jnb	short notest3
		mov	ax, ds:Msg_Numb
		cmp	ax, 0
		jnz	short extend_setup
		cmp	ds:DestIsDir, 0
		jz	short notest3
		jmp	badpath_err
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

notest3:				; ...
		mov	al, ds:One_Char_Val
		mov	dx, offset INORNOT_PTR
		call	pathchrcmp
		jz	short errj
		mov	ah, 17h
		mov	dx, 5Ch		; FCB
		int	21h		; DOS -	RENAME FILE via	FCB
					; DS:DX	-> FCB
					; FCB contains new name	starting at byte 17h.
		cmp	al, 0FFh
		jnz	short renameok
		call	get_ext_error_number
		push	ax
		mov	al, 0FFh

renameok:				; ...
