TYPEFIL		proc near		; ...
		mov	si, 81h
		mov	di, offset PARSE_MRDIR
		xor	cx, cx
		xor	dx, dx
		call	Parse_With_Msg
		cmp	ax, 0
		jnz	short typefil_parse_error
		push	si
		lds	si, ds:PARSE1_ADDR
		call	Move_To_SrcBuf
		pop	si
		mov	di, offset PARSE_MRDIR
		call	parse_check_eol
		jz	short gottarg

typefil_parse_error:			; ...
		jmp	cerror
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

