		pop	ds
		assume ds:TRANGROUP
		call	Set_Ext_Error_Msg
		mov	string_ptr_2, offset SrcBuf
		mov	extend_buf_sub,	1 ; one_subst
		jmp	cerror
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

typecont:				; ...
		mov	bx, ax
		mov	ax, 4400h
		int	21h		; DOS -	2+ - IOCTL - GET DEVICE	INFORMATION
					; BX = file or device handle
		test	dl, 80h
		jz	short not_device
		mov	TypeFilSiz+2, 0FFFFh ; -1
		jmp	short dotype
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

not_device:				; ...
		mov	ax, 4202h
		xor	dx, dx
		mov	cx, dx
		int	21h		; DOS -	2+ - MOVE FILE READ/WRITE POINTER (LSEEK)
					; AL = method: offset from end of file
		mov	TypeFilSiz, ax
		mov	TypeFilSiz+2, dx
		mov	ax, 4200h
		xor	dx, dx
		int	21h		; DOS -	2+ - MOVE FILE READ/WRITE POINTER (LSEEK)
					; AL = method: offset from beginning of	file

dotype:					; ...
		mov	zflag, 0
		mov	ds, TPA
		assume ds:nothing
		xor	dx, dx

typelp:					; ...
		cmp	cs:zflag, 0
		jz	short tf1
		retn
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

tf1:					; ...
		mov	cx, cs:BYTCNT
		cmp	cs:TypeFilSiz+2, 0FFFFh	; -1
		jz	short typ_read
		cmp	cs:TypeFilSiz+2, 0
		jz	short lt64k
		sub	cs:TypeFilSiz, cx
		sbb	cs:TypeFilSiz+2, 0
		jmp	short typ_read
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

lt64k:					; ...
		cmp	cx, cs:TypeFilSiz
		jbe	short gtbuf
		mov	cx, cs:TypeFilSiz
		jcxz	short typelp_ret
		mov	cs:TypeFilSiz, 0
		jmp	short typ_read
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

gtbuf:					; ...
		sub	cs:TypeFilSiz, cx

typ_read:				; ...
		mov	ah, 3Fh
		int	21h		; DOS -	2+ - READ FROM FILE WITH HANDLE
					; BX = file handle, CX = number	of bytes to read
					; DS:DX	-> buffer
		jnb	short tf2
		jmp	typerr
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

tf2:					; ...
		mov	cx, ax
		jcxz	short typelp_ret
		push	ds
		pop	es
		xor	di, di
		push	ax
		mov	al, 1Ah
		repne scasb
		pop	ax
		xchg	ax, cx
		cmp	ax, 0
		jnz	short foundz
		cmp	byte ptr [di-1], 1Ah
		jnz	short typecont2

foundz:					; ...
		sub	cx, ax
		dec	cx
		push	cs
		pop	es
		assume es:TRANGROUP
		not	es:zflag

typecont2:				; ...
		push	bx
		mov	bx, 1
		mov	ah, 40h
		int	21h		; DOS -	2+ - WRITE TO FILE WITH	HANDLE
					; BX = file handle, CX = number	of bytes to write, DS:DX -> buffer
		pop	bx
		jb	short Error_outputj
		cmp	ax, cx
		jnz	short tf3
		jmp	typelp
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

tf3:					; ...
		dec	cx
		cmp	ax, cx
		jnz	short Error_outputj

tf4:					; ...
		retn
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

Error_outputj:				; ...
		mov	bx, 1
		mov	ax, 4400h
		int	21h		; DOS -	2+ - IOCTL - GET DEVICE	INFORMATION
