VERSION		proc near		; ...
		mov	si, 81h
		mov	di, offset PARSE_VER
		xor	cx, cx
		call	Parse_With_Msg
		mov	bl, 1
		cmp	ax, 0		; RESULT_NO_ERROR
		jz	short verPrintVer
		dec	bl
		cmp	ax, 0FFFFh	; END_OF_LINE
		jz	short verPrintVer
		jmp	cerror
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

verPrintVer:				; ...
		push	bx
		call	CRLF2
		call	PRINT_VERSION
		call	CRLF2
		pop	bx
		or	bl, bl
		jz	short verDone
		mov	ax, 3306h
		int	21h		; DOS -	5+ Get TRUE Version Number (BL major, BH minor,	DL revision, DH	flags)
		mov	al, dl
		mov	bh, dh
		cmp	al, 25		; 'Z'-'A'
		jbe	short ver1
		mov	al, -23		; '*'-'A'

ver1:					; ...
		add	al, 'A'         ; 41h
		mov	ds:One_Char_Val, al
		mov	dx, offset dosrev_ptr
		call	std_printf
		mov	cl, 4
		shr	bh, cl
		jb	short verRom
		shr	bh, 1
		jb	short verHma
		mov	dx, offset DosLow_Ptr
		jmp	short verPrintLoc
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

verRom:					; ...
