		mov	ah, 30h
		int	21h		; DOS -	GET DOS	VERSION
					; Return: AL = major version number (00h for DOS 1.x)
		push	ax
		xor	ah, ah
		mov	ds:Major_Ver_Num, ax
		pop	ax
		xchg	ah, al
		xor	ah, ah
		mov	ds:Minor_Ver_Num, ax
		mov	dx, offset VerMes_Ptr
