PRINT_PROMPT	proc near		; ...
		push	ds
		push	cs
		pop	ds
		assume ds:TRANGROUP
		push	es
		call	find_prompt
		jb	short PP0
		cmp	byte ptr es:[di], 0
		jnz	short PP1

PP0:					; ...
		call	PRINT_DRIVE
		mov	al, '>'
		call	PRINT_CHAR
		jmp	short PP5
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

PP1:					; ...
		mov	al, es:[di]
		inc	di
		or	al, al
		jz	short PP5
		cmp	al, '$'
		jz	short PP2
		call	PRINT_CHAR
		jmp	short PP1
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

PP2:					; ...
		mov	al, es:[di]
		inc	di
		mov	bx, offset clsstring_2 ; PROMPT_TABLE-3	(db '[2J')
		or	al, al
		jz	short PP5

PP3:					; ...
		add	bx, 3
		call	UPCONV
		cmp	al, [bx]
		jz	short PP4
		cmp	byte ptr [bx], 0
		jnz	short PP3
		jmp	short PP1
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

PP4:					; ...
		push	es
		push	di
		push	cs
		pop	es
		assume es:TRANGROUP
		call	word ptr [bx+1]
		pop	di
		pop	es
		assume es:nothing
		jmp	short PP1
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

PP5:					; ...
		pop	es
		pop	ds
		assume ds:nothing
		retn
PRINT_PROMPT	endp

; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

PRINT_BACK:				; ...
