; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

notkanj2:				; ...
		call	UPCONV
		cmp	al, ';'         ; 3Bh ; semicolon
		jz	short path_hold
		call	DELIM
		jnz	short path_hold

scan_white:				; ...
		lodsb
		cmp	al, 0Dh
		jz	short path_eol
		cmp	al, 20h	; ' '
		jz	short scan_white
		cmp	al, 9
		jz	short scan_white
		mov	dx, offset extend_buf_ptr
		mov	extend_buf_ptr,	1 ; MoreArgs_Ptr
