		push	cs
		pop	ds
		jmp	std_printf
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

path1:					; ...
		push	es
		pop	ds
		sub	di, 5
		mov	si, di
		call	SCASB2
		cmp	cx, 0FFh	; 255
		jz	short path0
		push	cs
		pop	es
		mov	di, offset Arg_Buf
		mov	dx, 100h	; 256
		sub	dx, cx
		xchg	dx, cx
		rep movsb
		mov	dx, offset arg_buf_ptr
		push	cs
		pop	ds
		jmp	std_printf
print_path	endp


; ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ S U B	R O U T	I N E ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ


CLS		proc near		; ...
		mov	ah, 1Ah
		mov	al, 0
		int	2Fh		; - Multiplex -	DOS 4+ ANSI.SYS	internal - INSTALLATION	CHECK
					; Return: AL = FFh if installed
		cmp	al, 0FFh	; ANSI_installed
		jz	short ansicls
		mov	ax, 440Ch
		mov	bx, 1
		mov	ch, 3
		mov	cl, 7Fh
		mov	dx, offset Display_Ioctl
		int	21h		; DOS -	2+ - IOCTL -
		jb	short no_variable
		mov	ax, LinPerPag
		mov	dh, al
		mov	ax, display_width
		mov	dl, al
		jmp	short regcls
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

no_variable:				; ...
		mov	bx, 1
		mov	ax, 4400h
		int	21h		; DOS -	2+ - IOCTL - GET DEVICE	INFORMATION
					; BX = file or device handle
		test	dl, 80h
		jz	short ansicls
		test	dl, 10h
		jnz	short cls_normal

ansicls:				; ...
		call	ansi_cls
		jmp	short cls_ret
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

cls_normal:				; ...
		mov	ah, 0Fh
		int	10h		; - VIDEO - GET	CURRENT	VIDEO MODE
					; Return: AH = number of columns on screen
					; AL = current video mode
					; BH = current active display page
		cmp	al, 3
		jbe	short DoAlpha
		cmp	al, 7
		jz	short DoAlpha
		mov	ah, 0
		int	10h		; - VIDEO - SET	VIDEO MODE
					; AL = mode
		jmp	short cls_ret
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

DoAlpha:				; ...
		push	ds
		mov	ax, 40h		; ROMBIOS_DATA
		mov	ds, ax
		assume ds:nothing
		mov	dx, ds:4Ah	; [CRT_Cols]
		mov	dh, ds:84h	; [CRT_Rows]
		pop	ds
		assume ds:nothing
		or	dh, dh
		jnz	short regcls
		mov	dh, 25

regcls:					; ...
		inc	dh
		call	reg_cls

cls_ret:				; ...
		retn
CLS		endp


; ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ S U B	R O U T	I N E ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ


reg_cls		proc near		; ...
		dec	dh
		dec	dl
		push	dx
		mov	ah, 0Bh
		xor	bx, bx
		int	10h		; - VIDEO - SET	COLOR PALETTE
					; BH = 00h, BL = border	color
					; BH = 01h, BL = palette (0-3)
		pop	dx
		xor	ax, ax
		mov	cx, ax
		mov	ah, 6
		mov	bh, 7
		xor	bl, bl
		int	10h		; - VIDEO - SCROLL PAGE	UP
					; AL = number of lines to scroll window	(0 = blank whole window)
					; BH = attributes to be	used on	blanked	lines
					; CH,CL	= row,column of	upper left corner of window to scroll
					; DH,DL	= row,column of	lower right corner of window
		mov	ah, 0Fh
		int	10h		; - VIDEO - GET	CURRENT	VIDEO MODE
					; Return: AH = number of columns on screen
					; AL = current video mode
					; BH = current active display page
		mov	ah, 2
		xor	dx, dx
		int	10h		; - VIDEO - SET	CURSOR POSITION
					; DH,DL	= row, column (0,0 = upper left)
					; BH = page number
		retn
reg_cls		endp


; ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ S U B	R O U T	I N E ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ


ansi_cls	proc near		; ...
		mov	si, offset CLSSTRING
		lodsb
		mov	cl, al
		xor	ch, ch
		mov	ah, 6		; Raw_CON_IO

clrloop:				; ...
		lodsb
		mov	dl, al
		int	21h		; DOS -	DIRECT CONSOLE I/O CHARACTER OUTPUT
					; DL = character <> FFh
					;  Return: ZF set = no character
					;   ZF clear = character recieved, AL =	character
		loop	clrloop
		retn
ansi_cls	endp


; ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ S U B	R O U T	I N E ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ


