nocolon:				; ...
		mov	dx, offset SrcBuf
		mov	ax, 3D02h
		int	21h		; DOS -	2+ - OPEN DISK FILE WITH HANDLE
					; DS:DX	-> ASCIZ filename
					; AL = access mode
					; 2 - read & write
		jb	short isbaddev
		mov	bx, ax
		mov	ax, 4400h
		int	21h		; DOS -	2+ - IOCTL - GET DEVICE	INFORMATION
					; BX = file or device handle
		test	dl, 80h
		jnz	short devisok

closedev:				; ...
		mov	ah, 3Eh
		int	21h		; DOS -	2+ - CLOSE A FILE WITH HANDLE
					; BX = file handle

isbaddev:				; ...
		mov	dx, offset BADDEV_PTR
		call	std_printf
		jmp	short resret
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

devisok:				; ...
		push	dx
		mov	ax, ds:acrlf_ptr
		mov	dh, 0FFh	; -1 ; util_msg_class
		push	bx
		call	TSYSGETMSG
		mov	dx, si
		mov	ax, 4000h
		mov	cx, 2
		int	21h		; DOS -	2+ - WRITE TO FILE WITH	HANDLE
					; BX = file handle, CX = number	of bytes to write, DS:DX -> buffer
		pop	bx
		pop	dx
		jb	short closedev
		xor	dh, dh
		or	dl, 3
		mov	ax, 4401h
		int	21h		; DOS -	2+ - IOCTL - SET DEVICE	INFORMATION
					; BX = device handle, DH = 0
					; DL = device information to set (bits 0-7 from	function 0)
		push	bx
		mov	cx, 3
		xor	bx, bx

iclloop:				; ...
		mov	ah, 3Eh
		int	21h		; DOS -	2+ - CLOSE A FILE WITH HANDLE
					; BX = file handle
		inc	bx
		loop	iclloop
		pop	bx
		mov	ah, 45h
		int	21h		; DOS -	2+ - CREATE DUPLICATE HANDLE (DUP)
					; BX = file handle to duplicate
		mov	ah, 45h
		int	21h		; DOS -	2+ - CREATE DUPLICATE HANDLE (DUP)
					; BX = file handle to duplicate
		mov	ah, 45h
		int	21h		; DOS -	2+ - CREATE DUPLICATE HANDLE (DUP)
					; BX = file handle to duplicate
		mov	ah, 3Eh
		int	21h		; DOS -	2+ - CLOSE A FILE WITH HANDLE
					; BX = file handle

resret:					; ...
		mov	ds, ds:RESSEG
		push	ds
		mov	ax, ds:18h	; [PDB.JFN_TABLE]
