; ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ S U B	R O U T	I N E ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ


PARSELINE	proc near		; ...
		push	ax
		push	bx
		push	cx
		push	dx
		push	di
		push	si
		pushf
		mov	ds:cpyflag, 0
