		jcxz	short _copydone
		rep movsb

_copydone:				; ...
		jmp	path_success
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

path_noinit:				; ...
		test	ds:ARGV0_ARG_FLAGS, 4 ;	[ARG+ARGV_ELE.argflags],path_sep
		jnz	short path_failure
		mov	bh, ';'
		mov	dx, ds:ARGV0_ARGSTARTEL	; [ARG+ARGV_ELE.argstartel]
		sub	dx, ds:ARG_ARGV	; [ARG+ARGV_ELE.argpointer]
		add	dx, offset EXECPATH

path_loop:				; ...
		call	path_crunch
		mov	bp, ax
		lahf
		or	bp, bp
		jnz	short path_found
		sahf
		jnb	short path_loop

path_failure:				; ...
		xor	ax, ax
		jmp	path_exit
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

path_found:				; ...
		mov	di, offset EXECPATH
		mov	cx, ds:pathinfo+4
		mov	si, ds:pathinfo+2
		push	es
		push	ds:pathinfo	; [pathinfo+0]
		pop	es
		cmp	byte ptr es:[si+2], 2Eh	; '.'
		jnz	short path_cpy
		cmp	byte ptr es:[si+1], 3Ah	; ':'
		jnz	short path_cpy
		push	cx
		mov	al, es:[si]
		mov	[di], al
		mov	al, es:[si+1]
		mov	[di+1],	al
		mov	al, ds:psep_char
		mov	[di+2],	al
		push	si
		mov	dl, es:[si]
		or	dl, 20h
		sub	dl, 60h		; 'a'-1
		mov	si, di
		add	si, 3
		mov	ax, 4700h
		int	21h		; DOS -	2+ - GET CURRENT DIRECTORY
					; DL = drive (0=default, 1=A, etc.)
					; DS:SI	points to 64-byte buffer area
		call	dstrlen
		add	si, cx
		dec	si
		mov	di, si
		pop	si
		add	si, 3
		pop	cx

path_cpy:				; ...
		pop	es
		sub	cx, si
		push	ds
		push	ds:pathinfo	; [pathinfo+0]
		pop	ds
		cld

kloop:					; ...
		lodsb
		stosb
		call	testkanj
		jz	short notkanj1
		dec	cx
		jcxz	short popdone
		movsb
		dec	cx
		cmp	cx, 1
		ja	short kloop

popdone:				; ...
		pop	ds
		mov	al, ds:psep_char
		jmp	short path_store
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

notkanj1:				; ...
		loop	kloop
		pop	ds
		dec	di
		mov	al, ds:psep_char
		cmp	al, [di-1]
		jz	short path_success

path_store:				; ...
		stosb

path_success:				; ...
		mov	si, offset search_best_buf
		xor	cx, cx

path_succ_loop:				; ...
		lodsb
		stosb
		or	al, al
		jnz	short path_succ_loop
		mov	ax, bp

path_exit:				; ...
		popf
		pop	bp
		pop	di
		pop	si
		pop	dx
		pop	cx
		pop	bx
		retn
path_search	endp


; ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ S U B	R O U T	I N E ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ


STORE_PCHAR	proc near		; ...
		push	ax
		mov	al, 2Fh	; '/'
		call	pathchrcmp
		jz	short STORE_SLASH
		mov	al, 5Ch	; '\'
		mov	ds:psep_char, al
		pop	ax
		retn
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

STORE_SLASH:				; ...
		mov	ds:psep_char, al
		pop	ax
		retn
STORE_PCHAR	endp


; ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ S U B	R O U T	I N E ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ


path_crunch	proc near		; ...
		push	bx
		push	cx
		push	dx
		push	di
		push	si
		pushf
		call	STORE_PCHAR
		mov	di, offset TPBUF
		mov	si, ds:pathinfo+4
		mov	ds:pathinfo+2, si
		push	ds
		push	ds:pathinfo	; [pathinfo+0]
		pop	ds
		xor	cl, cl

path_cr_copy:				; ...
		lodsb
		or	al, al
		jz	short path_seg
		cmp	al, bh
		jz	short path_seg
		call	testkanj
		jz	short _notkanj2
		stosb
		movsb
		mov	cl, 1
		jmp	short path_cr_copy
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

_notkanj2:				; ...
		xor	cl, cl
		stosb
		jmp	short path_cr_copy
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

path_seg:				; ...
		pop	ds
		mov	ds:pathinfo+4, si
		mov	bl, al
		xor	ax, ax
		cmp	di, offset TPBUF
		jz	short path_cr_leave

path_cr_look:
		mov	al, ds:psep_char
		or	cl, cl
		jnz	short path_cr_store
		cmp	al, [di-1]
		jz	short path_cr_l1

path_cr_store:				; ...
		stosb

path_cr_l1:				; ...
		mov	si, dx

path_cr_l2:				; ...
		lodsb
		stosb
		or	al, al
		jnz	short path_cr_l2
		mov	dx, offset TPBUF
		mov	ds:search_error, offset	BADPMES_PTR
		call	PSEARCH

path_cr_leave:				; ...
		or	bl, bl
		jz	short path_cr_empty
		popf
		clc
		jmp	short path_cr_exit
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

path_cr_empty:				; ...
		popf
		stc

path_cr_exit:				; ...
		pop	si
		pop	di
		pop	dx
		pop	cx
		pop	bx
		retn
path_crunch	endp


; ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ S U B	R O U T	I N E ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ


PSEARCH		proc near		; ...
		push	cx
		push	dx
		push	di
		push	si
		pushf
		push	dx
		mov	di, dx
		mov	si, offset search_curdir_buf
		xor	dx, dx
		cmp	byte ptr [di+1], 3Ah ; ':'
		jnz	short SEARCH_DIR_CHECK
		mov	dl, [di]
		and	dl, 0DFh	; ~20h	; uppercase
		sub	dl, 40h	; '@'   ; convert to drive number

SEARCH_DIR_CHECK:			; ...
		mov	ax, 4700h
		int	21h		; DOS -	2+ - GET CURRENT DIRECTORY
					; DL = drive (0=default, 1=A, etc.)
					; DS:SI	points to 64-byte buffer area
		pop	dx
		jb	short SEARCH_INVALID_DRIVE
		mov	cx, 13h		; search_attr
		mov	ax, 4E00h
		int	21h		; DOS -	2+ - FIND FIRST	ASCIZ (FINDFIRST)
					; CX = search attributes
					; DS:DX	-> ASCIZ filespec
					; (drive, path,	and wildcards allowed)
		jb	short SEARCH_NO_FILE
		mov	ds:search_best,	0 ; SEARCH_FILE_NOT_FOUND ; 0
		mov	ds:search_best_buf, 0 ;	ANULL ;	0

SEARCH_LOOP:				; ...
		call	SEARCH_FTYPE
		cmp	al, ds:search_best
		jle	short SEARCH_NEXT
		mov	ds:search_best,	al
		mov	si, (offset FBUF+1Eh) ;	FBUF+FIND_BUF.PNAME
		mov	di, offset search_best_buf
		mov	cx, 13		; FNAME_MAX_LEN
		cld
		rep movsb
		cmp	al, 8		; SEARCH_COM
		jz	short SEARCH_DONE

SEARCH_NEXT:				; ...
		mov	cx, 13h		; SEARCH_ATTR
		mov	ax, 4F00h
		int	21h		; DOS -	2+ - FIND NEXT ASCIZ (FINDNEXT)
					; [DTA]	= data block from
					; last AH = 4Eh/4Fh call
		jnb	short SEARCH_LOOP

SEARCH_DONE:				; ...
		mov	al, ds:search_best
		cmp	ds:ext_entered,	1
		jz	short SEARCH_EXIT
		mov	al, ds:ext_entered
		mov	ds:search_best,	al
		jmp	short SEARCH_EXIT
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

SEARCH_INVALID_DRIVE:			; ...
		mov	dx, ds:search_error
		call	std_printf

SEARCH_NO_FILE:				; ...
		mov	ax, 0

SEARCH_EXIT:				; ...
		popf
		pop	si
		pop	di
		pop	dx
		pop	cx
		retn
PSEARCH		endp


; ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ S U B	R O U T	I N E ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ


SEARCH_FTYPE	proc near		; ...
		push	di
		push	si
		mov	ax, 0
		mov	di, (offset FBUF+1Eh) ;	FBUF+FIND_BUF.PNAME
		mov	cx, 13		; FNAME_MAX_LEN
		cld
		repne scasb
		jnz	short FTYPE_EXIT
		sub	di, 5
		mov	si, offset comext ; ".COM"
		mov	ax, di
		cmpsw
		jnz	short FTYPE_EXE
		cmpsw
		jnz	short FTYPE_EXE
		mov	ax, 8		; SEARCH_COM
		jmp	short FTYPE_EXIT
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

FTYPE_EXE:				; ...
		mov	di, ax
		mov	si, offset exeext ; ".EXE"
		cmpsw
		jnz	short FTYPE_BAT
		cmpsw
		jnz	short FTYPE_BAT
		mov	ax, 4		; SEARCH_EXE
		jmp	short FTYPE_EXIT
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

FTYPE_BAT:				; ...
		mov	di, ax
		mov	si, offset batext ; ".BAT"
		cmpsw
		jnz	short FTYPE_FAIL
		cmpsw
		jnz	short FTYPE_FAIL
		mov	ax, 2		; SEARCH_BAT
		jmp	short FTYPE_EXIT
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

FTYPE_FAIL:				; ...
		mov	ax, 0

FTYPE_EXIT:				; ...
		cmp	ds:ext_entered,	1
		jz	short FTYPE_DONE
		cmp	ax, 0		; ANULL
		jz	short FTYPE_DONE
		mov	ds:ext_entered,	al
		mov	ax, 8		; SEARCH_COM

FTYPE_DONE:				; ...
		pop	si
		pop	di
		retn
SEARCH_FTYPE	endp


; ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ S U B	R O U T	I N E ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ


STRIP		proc near		; ...
		push	ax
		push	bx
		push	cx
		push	dx
		push	di
		push	si
		pushf
		mov	ds:ext_entered,	1
		mov	dx, ds:ARG_ARGV	; [ARG+ARGV_ELE.argpointer]
		mov	di, ds:ARGV0_ARGSTARTEL	; [ARG+ARGV_ELE.argstartel]
		cmp	byte ptr [di], 0
		jz	short STRIP_ERROR
		mov	cx, dx
		add	cx, ds:ARGV0_ARGLEN ; [ARG+ARGV_ELE.arglen]
		sub	cx, di
		inc	cx
		mov	al, 2Eh	; '.'
		cld
		repne scasb
		jcxz	short PROCESS_EXT
		mov	ds:ext_entered,	0
		mov	al, 0
		repne scasb

PROCESS_EXT:				; ...
		mov	cx, di
		sub	cx, dx
		sub	bx, 4
		cmp	cx, bx
		jg	short STRIP_ERROR
		mov	di, si
		mov	si, dx
		cld
		rep movsb
		cmp	ds:ext_entered,	1
		jnz	short SKIP_WILDS
		dec	di
		stosb
		mov	al, 3Fh	; '?'
		stosb
		stosb
		stosb
		mov	al, 0
		stosb

SKIP_WILDS:				; ...
		popf
		clc
		jmp	short STRIP_EXIT
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

STRIP_ERROR:				; ...
		popf
		stc

STRIP_EXIT:				; ...
		pop	si
		pop	di
		pop	dx
		pop	cx
		pop	bx
		pop	ax
		retn
STRIP		endp


; ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ S U B	R O U T	I N E ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ


SAVE_ARGS	proc near		; ...
		push	bx
		push	cx
		push	dx
		push	di
		push	si
		push	bp
		pushf
		add	bx, 1363	; ARG_UNIT.SIZE+15
		mov	cl, 4
		shr	bx, cl
		mov	ax, 4800h
		int	21h		; DOS -	2+ - ALLOCATE MEMORY
					; BX = number of 16-byte paragraphs desired
		jb	short SAVE_ERROR
		mov	bp, ax
		push	es
		mov	es, ax
		assume es:nothing
		mov	cx, 1348	; ARG_UNIT.SIZE
		xor	di, di
		mov	si, offset ARG_ARGV ; offset ARG
		rep movsb
		mov	cx, ds:ARG_ARGVCNT ; [ARG+ARG_UNIT.argvcnt]
		xor	ax, ax
		mov	si, offset ARG_ARGV ; offset ARG

SAVE_PTR_LOOP:				; ...
		dec	cx
		jl	short SAVE_DONE
		mov	bx, cx
		call	argv_calc
		mov	dx, ds:ARG_ARGV[bx] ; [ARG+ARGV_ELE.argpointer+bx]
		sub	dx, si
		mov	es:[bx], dx
		mov	dx, ds:ARGV0_ARGSTARTEL[bx] ; [ARG+ARGV_ELE.argstartel+bx]
		sub	dx, si
		mov	es:[bx+3], dx	; [es:bx+ARGV_ELE.argstartel]
		mov	dx, ds:ARGV0_OCOMPTR[bx] ; [ARG+ARGV_ELE.arg_ocomptr+bx]
		sub	dx, si
		mov	es:[bx+9], dx	; [es:bx+ARGV_ELE.arg_ocomptr]
		jmp	short SAVE_PTR_LOOP
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

SAVE_DONE:				; ...
		pop	es
		assume es:nothing
		mov	ax, bp
		jmp	short SAVE_OK
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

SAVE_ERROR:				; ...
