; ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ S U B	R O U T	I N E ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ


SETASC		proc near		; ...
		and	al, 0Ch		; SWITCHA+SWITCHB
		jp	short LOADSW
		push	ax
		and	al, 8
		mov	ds:BINARY, al	; SWITCHB
		pop	ax
		and	al, 4		; SWITCHA
		mov	ds:ASCII, al
		or	ds:INEXACT, al

LOADSW:					; ...
		mov	al, ds:ASCII
		or	al, al
		retn
SETASC		endp


; ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ S U B	R O U T	I N E ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ


BUILDDEST	proc near		; ...
		cmp	ds:DestIsDir, 0FFh ; -1
		jnz	short KNOWABOUTDEST
		mov	di, offset USERDIR1
		mov	bp, offset DestIsDir ; offset DestVars
		call	BUILDPATH
		call	RestUDir1

KNOWABOUTDEST:				; ...
		xor	al, al
		xchg	al, ds:FIRSTDEST
		or	al, al
		jnz	short FIRSTDST
		jmp	NOFIRSTDEST
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

FIRSTDST:				; ...
		mov	si, ds:DestTail
		mov	di, offset DESTDIR ; offset DestFcb
		mov	ax, 2900h
		int	21h		; DOS -	PARSE FILENAME
					; DS:SI	-> string to parse
					; ES:DI	-> buffer to fill with unopened	FCB
					; AL = bit mask	to control parsing
		cmp	byte ptr [si], 0
		jz	short GOODPARSE
		mov	dx, offset FULLDIR_PTR
		jmp	COPYERR
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

GOODPARSE:				; ...
		mov	ax, word ptr ds:DestBuf
		cmp	ah, ':'
		jz	short DRVSPEC4
		mov	al, 40h	; '@'

DRVSPEC4:				; ...
		mov	cl, ds:ASCII
		or	al, 20h
		sub	al, 60h
		mov	ds:DESTDIR, al	; [DestFcb]
		mov	al, ds:DestInfo
		mov	ah, ds:SrcInfo
		and	ax, 202h
		or	al, al
		jz	short NOTMELCOPY
		cmp	al, ah
		jnz	short NOTMELCOPY
		cmp	ds:PLUS, 0
		jz	short NOTMELCOPY
		inc	ds:MELCOPY
		xor	al, al
		jmp	short SETCONC
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

NOTMELCOPY:				; ...
		xor	al, 2
		and	al, ah
		shr	al, 1

SETCONC:				; ...
		or	al, ds:PLUS
		mov	ds:PARM1, al	; [Concat]
		shl	al, 1
		shl	al, 1
		mov	ds:INEXACT, al
		cmp	ds:BINARY, 0
		jnz	short NOFIRSTDEST
		or	ds:ASCII, al
		or	cl, cl
		jnz	short NOFIRSTDEST
		or	al, al
		jz	short NOFIRSTDEST
		mov	cx, ds:NXTADD
		jcxz	short NOFIRSTDEST
		mov	al, 1Ah
		push	es
		xor	di, di
		mov	es, ds:TPA
		repne scasb
		pop	es
		jnz	short NOFIRSTDEST
		dec	di
		mov	ds:NXTADD, di

NOFIRSTDEST:				; ...
		mov	bx, (offset BWDBUF+1) ;	DIRBUF+1
		cmp	ds:PARM1, 0	; [Concat]
		jz	short GOTCHRSRC
		mov	bx, (offset SDIRBUF+1)

GOTCHRSRC:				; ...
		mov	si, (offset DESTDIR+1)
		mov	di, ds:DestTail
BUILDDEST	endp


; ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ S U B	R O U T	I N E ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ


BUILDNAME	proc near		; ...
		mov	cx, 8

BUILDMAIN:				; ...
		lodsb
		cmp	al, '?'
		jnz	short NOTAMBIG
		mov	al, [bx]

NOTAMBIG:				; ...
		cmp	al, 20h	; ' '
		jz	short NOSTORE
		stosb

NOSTORE:				; ...
		inc	bx
		loop	BUILDMAIN
		mov	cl, 3
		mov	al, 20h	; ' '
		cmp	[si], al
		jz	short ENDDEST
		mov	al, '.'         ; 2Eh ; dot_chr
		stosb

BUILDEXT:				; ...
		lodsb
		cmp	al, '?'         ; 3Fh
		jnz	short NOTAMBIGE
		mov	al, [bx]

NOTAMBIGE:				; ...
		cmp	al, 20h	; ' '
		jz	short NOSTOREE
		stosb

NOSTOREE:				; ...
		inc	bx
		loop	BUILDEXT

ENDDEST:				; ...
		xor	al, al
		stosb
		retn
BUILDNAME	endp


; ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ S U B	R O U T	I N E ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ


BUILDPATH	proc near		; ...

; FUNCTION CHUNK AT 2078 SIZE 00000011 BYTES

		test	byte ptr [bp+4], 2 ; [bp+VARSTRUC.INFO]
		jnz	short NOTPFILE
		mov	dx, bp
		add	dx, 5		; add dx,VARSTRUC.BUF
		push	di
		mov	ax, 6C00h
		mov	bx, 40h		; deny_none or read_open_mode
		xor	cx, cx
		mov	si, dx
		mov	dx, 101h	; read_open_flag
		int	21h		; DOS -	4.0 - EXTENDED OPEN/CREATE
					; BL = open mode as in AL for normal open (INT 21h/AH=3Dh)
					; BH = flags, CX = create attribute, DL	= action if file exists/does not exists
					; DH = 00h (reserved), DS:SI ->	ASCIZ file name
		pop	di
		jnb	short PURE_FILE
		call	get_ext_error_number
		cmp	ax, 2		; ERROR_FILE_NOT_FOUND
		jz	short NOTPFILE
		cmp	ax, 3		; ERROR_PATH_NOT_FOUND
		jz	short NOTPFILE
		cmp	ax, 5		; ERROR_ACCESS_DENIED
		jz	short NOTPFILE
		jmp	extend_setup
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

PURE_FILE:				; ...
		mov	bx, ax
		mov	ax, 4400h
		int	21h		; DOS -	2+ - IOCTL - GET DEVICE	INFORMATION
					; BX = file or device handle
		mov	ah, 3Eh
		int	21h		; DOS -	2+ - CLOSE A FILE WITH HANDLE
					; BX = file handle
		test	dl, 80h
		jnz	short ISADEV
		test	byte ptr [bp+4], 4 ; [bp+VARSTRUC.INFO]
		jz	short ISADEV

NOTPFILE:				; ...
		mov	dx, [bp+5]	; [bp+VARSTRUC.BUF]
		cmp	dl, 0
		jz	short SET_DRIVE_SPEC
		cmp	dh, 3Ah	; ':'
		jz	short DRVSPEC5

SET_DRIVE_SPEC:				; ...
		mov	dl, 40h	; '@'

DRVSPEC5:				; ...
		or	dl, 20h
		sub	dl, 60h		; A = 1
		call	SAVUDIR1
		jnb	short CURDIR_OK
		call	get_ext_error_number
		jmp	extend_setup
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

CURDIR_OK:				; ...
		mov	dx, bp
		add	dx, 5		; add dx,VARSTRUC.BUF
		mov	bh, [bp+4]	; [bp+VARSTRUC.INFO]
		and	bh, 6
		cmp	bh, 6
		jnz	short CHECKAMB
		mov	si, [bp+2]	; [bp+VARSTRUC.TTAIL]
		mov	bl, 3Ah	; ':'
		cmp	[si-2],	bl
		jnz	short KNOWNOTSPEC
		mov	byte ptr [bp+0], 2 ; [bp+VARSTRUC.ISDIR]
		jmp	short DOPCDJ
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

KNOWNOTSPEC:				; ...
		mov	byte ptr [bp+0], 1
		dec	si

DOPCDJ:					; ...
		jmp	DOPCD
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

CHECKAMB:				; ...
		cmp	bh, 2
		jnz	short CHECKCD

ISADEV:					; ...
		mov	byte ptr [bp+0], 0
		retn
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

CHECKCD:				; ...
		call	SetRest1
		mov	ah, 3Bh
		int	21h		; DOS -	2+ - CHANGE THE	CURRENT	DIRECTORY (CHDIR)
					; DS:DX	-> ASCIZ directory name	(may include drive)
		jb	short NOTPDIR
		mov	di, dx
		xor	ax, ax
		mov	cx, ax
		dec	cx

Kloop:					; ...
		mov	al, es:[di]
		inc	di
		or	al, al
		jz	short DONE
		xor	ah, ah
		call	testkanj
		jz	short Kloop
		inc	di
		inc	ah
		jmp	short Kloop
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

DONE:					; ...
		dec	di
		mov	al, ds:DIRCHAR
		mov	byte ptr [bp+0], 2 ; [bp+VARSTRUC.ISDIR]
		or	ah, ah
		jnz	short _STORE_PCHAR
		cmp	al, [di-1]
		jz	short GOTSRCSLSH

_STORE_PCHAR:				; ...
		stosb
		mov	byte ptr [bp+0], 1

GOTSRCSLSH:				; ...
		or	byte ptr [bp+4], 6 ; [bp+VARSTRUC.INFO]
		call	SETSTARS

NOTPDIR_RETN:				; ...
		retn
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

NOTPDIR:				; ...
		call	get_ext_error_number
		cmp	ax, 3		; ERROR_PATH_NOT_FOUND
		jz	short NOTPDIR_TRY
		cmp	ax, 5		; ERROR_ACCESS_DENIED
		jnz	short EXTEND_SETUPJ

NOTPDIR_TRY:				; ...
		mov	byte ptr [bp+0], 0 ; [bp+VARSTRUC.ISDIR]
		mov	bh, [bp+4]	; [bp+VARSTRUC.INFO]
		test	bh, 4
		jz	short NOTPDIR_RETN
		mov	byte ptr [bp+0], 2
		mov	si, [bp+2]	; [bp+VARSTRUC.TTAIL]
		cmp	byte ptr [si], 0
		jz	short BADCDERRJ2
		mov	bl, 2Eh	; '.'
		cmp	[si], bl
		jz	short BADCDERRJ2
		mov	bl, 3Ah	; ':'
		cmp	[si-2],	bl
		jz	short DOPCD
		mov	byte ptr [bp+0], 1
		dec	si

DOPCD:					; ...
		xor	bl, bl
		xchg	bl, [si]
		call	SetRest1
		cmp	dx, si
		jnb	short LookBack
		push	si
		push	cx
		mov	cx, si
		mov	si, dx

Kloop2:					; ...
		lodsb
		call	testkanj
		jz	short NotKanj4
		lodsb
		cmp	si, cx
		jb	short Kloop2
		pop	cx
		pop	si
		jmp	short DoCdr
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

NotKanj4:				; ...
		cmp	si, cx
		jb	short Kloop2
		pop	cx
		pop	si

LookBack:				; ...
		cmp	bl, [si-1]
		jz	short BADCDERRJ2

DoCdr:					; ...
		mov	ah, 3Bh
		int	21h		; DOS -	2+ - CHANGE THE	CURRENT	DIRECTORY (CHDIR)
					; DS:DX	-> ASCIZ directory name	(may include drive)
		xchg	bl, [si]
		jnb	short NOTPDIR_RETN
		call	get_ext_error_number

EXTEND_SETUPJ:				; ...
		jmp	extend_setup
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

BADCDERRJ2:				; ...
		jmp	badpath_err
BUILDPATH	endp


; ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ S U B	R O U T	I N E ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ


SETSTARS	proc near		; ...
		mov	[bp+2],	di	; [bp+VARSTRUC.TTAIL]
		add	byte ptr [bp+1], 12 ; [bp+VARSTRUC.SIZ]
		mov	ax, '.?'        ;  "?." ; dot_qmark ; 2E3Fh
		mov	cx, 8
		rep stosb
		xchg	al, ah
		stosb
		xchg	al, ah
		mov	cl, 3
		rep stosb
		xor	al, al
		stosb
		retn
SETSTARS	endp


; ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ S U B	R O U T	I N E ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ


COMPNAME	proc near		; ...
		mov	si, offset DestBuf
		mov	di, offset TRGXNAME
		mov	ah, 60h		; xNameTrans
		int	21h		; DOS -	RESOLVE	PATH STRING TO CANONICAL PATH STRING
					; DS:SI	-> ASCIZ relative path string or directory name
					; ES:DI	-> 128-byte buffer for ASCIZ canonical fully qualified name
		mov	si, offset SRCXNAME
		mov	di, offset TRGXNAME
		call	STRCOMP
		retn
COMPNAME	endp


; ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ S U B	R O U T	I N E ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ


cparse		proc near		; ...
		xor	ax, ax
		mov	ds:STARTEL, di
		mov	ds:ELPOS, al
		mov	ds:SKPDEL, al
		mov	bh, al
		pushf
		push	di
		xor	cx, cx
		mov	ds:comma, cl

moredelim:				; ...
		lodsb
		call	DELIM
		jnz	short SCANCDONE
		cmp	al, 20h	; ' '
		jz	short moredelim
		cmp	al, 9
		jz	short moredelim
		xchg	al, ds:SKPDEL
		or	al, al
		jz	short moredelim
		test	bh, 80h
		jz	short no_comma
		mov	ds:comma, 1

no_comma:				; ...
		jmp	x_done
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

SCANCDONE:				; ...
		cmp	al, bl
		jnz	short nospec
		or	bh, 80h
		jmp	short moredelim
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

nospec:					; ...
		cmp	al, 0Dh
		jnz	short ncperror
		jmp	cperror
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

ncperror:				; ...
		cmp	al, ds:SWITCHAR
		jnz	short na_switch
		jmp	a_switch
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

na_switch:				; ...
		mov	dl, ':'
		cmp	[si], dl
		jnz	short anum_chard
		cmp	ds:cpyflag, 1
		jnz	short cpcont2
		call	UPCONV

cpcont2:				; ...
		call	move_char
		lodsb
		call	move_char
		mov	ds:STARTEL, di
		mov	ds:ELCNT, 0
		jmp	anum_test
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

anum_chard:				; ...
		mov	ds:STARTEL, di
		mov	ds:ELCNT, 0
		cmp	ds:cpyflag, 1
		jnz	short anum_char
		call	pathchrcmp
		jnz	short anum_char
		push	ax
		mov	al, ds:CURDRV
		add	al, 41h	; 'A'
		call	move_char
		mov	al, 3Ah	; ':'
		call	move_char
		pop	ax
		mov	ds:STARTEL, di
		mov	ds:ELCNT, 0

anum_char:				; ...
		call	testkanj
		jz	short NOTKANJ
		call	move_char
		lodsb
		jmp	short notspecial
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

NOTKANJ:				; ...
		cmp	ds:cpyflag, 1
		jnz	short TESTDOT
		call	UPCONV

TESTDOT:				; ...
		cmp	al, 2Eh	; '.'   ; dot_chr
		jnz	short testquest
		inc	ds:ELPOS
		mov	ds:ELCNT, 0FFh

testquest:				; ...
		cmp	al, 3Fh	; '?'
		jnz	short testsplat
		or	bh, 2

testsplat:				; ...
		cmp	al, 2Ah	; '*'   ; star
		jnz	short testpath
		or	bh, 2
		cmp	ds:expand_star,	0
		jnz	short expand_filename
		jmp	short testpath
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

BADPERR2J:				; ...
		jmp	short BADPERR2
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

expand_filename:			; ...
		mov	ah, 7
		cmp	ds:ELPOS, 0
		jz	short gotelcnt
		mov	ah, 2

gotelcnt:				; ...
		mov	al, 3Fh	; '?'
		sub	ah, ds:ELCNT
		jb	short BADPERR2J
		xchg	ah, cl
		jcxz	short testpathx

qmove:					; ...
		xchg	ah, cl
		call	move_char
		xchg	ah, cl
		loop	qmove

testpathx:				; ...
		xchg	ah, cl

testpath:				; ...
		call	pathchrcmp
		jnz	short notspecial
		or	bh, 4
		cmp	ds:expand_star,	0
		jz	short no_err_check
		test	bh, 2
		jnz	short BADPERR

no_err_check:				; ...
		mov	ds:STARTEL, di
		inc	ds:STARTEL
		mov	ds:ELCNT, 0FFh
		mov	ds:ELPOS, 0

notspecial:				; ...
		call	move_char

anum_test:				; ...
		lodsb

cpcont3:
		call	DELIM
		jz	short x_done
		cmp	al, 0Dh
		jz	short x_done
		cmp	al, ds:SWITCHAR
		jz	short x_done
		cmp	al, bl
		jz	short x_done
		cmp	al, 3Ah	; ':'
		jz	short FOO15
		jmp	anum_char
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

FOO15:					; ...
		cmp	ds:cpyflag, 2
		jnz	short cpcont4
		call	move_char
		jmp	short anum_test
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

cpcont4:				; ...
		inc	si
		jmp	short x_done
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ
		jmp	anum_char
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

BADPERR2:				; ...
		mov	dx, offset BADCPMES_PTR
