		test	byte ptr es:[bx+2], 2 ;	[es:bx+$P_CONTROL_BLK.$P_Function_Flag],$P_CAP_Char
		jz	short $P_RLT_CAP01
		mov	al, 2		; $P_DOSTBL_Char

P_RLT_CAP02:				; ...
		call	$P_Do_CAPS_String

$P_RLT_CAP01:				; ...
		pop	ax
		test	byte ptr es:[bx+2], 10h	; [es:bx+$P_CONTROL_BLK.$P_Function_Flag],$P_Rm_Colon
		jz	short $P_RLT_Exit
		call	$P_Remove_Colon

$P_RLT_Exit:				; ...
		pop	di
		retn
$P_Fill_Result	endp


; ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ S U B	R O U T	I N E ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ


$P_Check_Match_Flags proc near		; ...
		mov	cs:$P_err_flag,	0 ; $P_NULL
		push	ax
		mov	ax, es:[bx]	; [es:bx+$P_CONTROL_BLK.$P_Match_Flag]
		or	ax, ax
		jnz	short $P_Mat
		push	ax
		push	bx
		push	dx
		push	di
		mov	cs:$P_RC, 9	; $P_Syntax
		mov	ah, 0FFh	; $P_No_Tag
		mov	al, 3		; $P_String
		call	$P_Fill_Result
		pop	di
		pop	dx
		pop	bx
		pop	ax
		jmp	short $P_Bridge
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

$P_Mat:					; ...
		test	ax, 1000h	; $P_Date_S
		jz	short $P_Match02
		mov	cs:$P_RC, 0	; $P_No_Error
		call	$P_Date_Format
		cmp	cs:$P_RC, 9	; $P_Syntax
		jnz	short $P_Bridge

$P_Match02:				; ...
		test	ax, 800h
		jz	short $P_Match03
		mov	cs:$P_RC, 0	; $P_No_Error
		call	$P_Time_Format
		cmp	cs:$P_RC, 9	; $P_Syntax
		jnz	short $P_Bridge
		jmp	short $P_Match03
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

$P_Bridge:				; ...
		jmp	short $P_Match_Exit
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ
		nop

$P_Match03:				; ...
		test	ax, 8000h	; $P_Num_Val
		jz	short $P_Match04
		mov	cs:$P_RC, 0	; $P_No_Error
		call	$P_Value
		cmp	cs:$P_RC, 9	; $P_Syntax
		jnz	short $P_Match_Exit

$P_Match04:				; ...
		test	ax, 4000h	; $P_SNum_Val
		jz	short $P_Match05
		mov	cs:$P_RC, 0	; $P_No_Error
		call	$P_SValue
		cmp	cs:$P_RC, 9	; $P_Syntax
		jnz	short $P_Match_Exit

$P_Match05:				; ...
		test	ax, 100h	; $P_Drv_Only
		jz	short $P_Match06
		mov	cs:$P_RC, 0	; $P_No_Error
		call	$P_File_Format
		call	$P_Drive_Format
		cmp	cs:$P_RC, 9	; $P_Syntax
		jnz	short $P_Match_Exit

$P_Match06:				; ...
		test	ax, 200h	; $P_File_Spc
		jz	short $P_Match07
		mov	cs:$P_RC, 0
		call	$P_File_Format
		cmp	cs:$P_RC, 9
		jnz	short $P_Match_Exit

$P_Match07:				; ...
		test	ax, 2000h	; $P_Simple_S
		jz	short $P_Match_Exit
		mov	cs:$P_RC, 0
		call	$P_Simple_String

$P_Match_Exit:				; ...
		cmp	cs:$P_err_flag,	1 ; $P_error_filespec
		jnz	short $P_Match2_Exit
		cmp	cs:$P_RC, 0	; $P_No_Error
		jnz	short $P_Match2_Exit
		mov	cs:$P_RC, 9	; $P_Syntax

$P_Match2_Exit:				; ...
		pop	ax
		retn
$P_Check_Match_Flags endp


; ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ S U B	R O U T	I N E ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ


$P_Remove_Colon	proc near		; ...
		push	ax
		push	si

$P_RCOL_Loop:				; ...
		mov	al, cs:[si]
		or	al, al
		jz	short $P_RCOL_Exit
		cmp	al, ':'         ; $P_Colon
		jnz	short $P_RCOL00
		cmp	byte ptr cs:[si+1], 0 ;	$P_NULL
		jnz	short $P_RCOL00
		mov	byte ptr cs:[si], 0
		jmp	short $P_RCOL_Exit
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

$P_RCOL00:				; ...
		call	$P_Chk_DBCS
		jnb	short $P_RCOL01
		inc	si

$P_RCOL01:				; ...
		inc	si
		jmp	short $P_RCOL_Loop
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

$P_RCOL_Exit:				; ...
		pop	si
		pop	ax
		retn
$P_Remove_Colon	endp


; ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ S U B	R O U T	I N E ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ


$P_Do_CAPS_String proc near		; ...
		push	si
		push	dx
		mov	dl, al

$P_DCS_Loop:				; ...
		mov	al, cs:[si]
		call	$P_Chk_DBCS
		jb	short $P_DCS0
		or	al, al
		jz	short $P_DCS_Exit
		call	$P_Do_CAPS_Char
		mov	cs:[si], al
		jmp	short $P_DCS01
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

$P_DCS0:				; ...
		inc	si

$P_DCS01:				; ...
		inc	si
		jmp	short $P_DCS_Loop
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

$P_DCS_Exit:				; ...
		pop	dx
		pop	si
		retn
$P_Do_CAPS_String endp


; ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ S U B	R O U T	I N E ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ


$P_Do_CAPS_Char	proc near		; ...
		cmp	al, 80h		; $P_ASCII80
		jnb	short $P_DCC_Go
		cmp	al, 61h	; 'a'
		jb	short $P_CAPS_Ret
		cmp	al, 7Ah	; 'z'
		ja	short $P_CAPS_Ret
		and	al, 0DFh	; $P_Make_Upper
		jmp	short $P_CAPS_Ret
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

$P_DCC_Go:				; ...
		push	bx
		push	es
		push	di
		lea	di, $P_File_CAP_Ptr
		cmp	dl, 4		; $P_DOSTBL_File
		jz	short $P_DCC00
		lea	di, $P_Char_CAP_Ptr

$P_DCC00:				; ...
		cmp	cs:[di], dl
		jz	short $P_DCC01
		push	ax
		push	cx
		push	dx
		push	cs
		pop	es
		assume es:TRANGROUP
		mov	ah, 65h		; $P_DOS_Get_TBL
		mov	al, dl
		mov	bx, 0FFFFh	; -1 ; get active CON
		mov	cx, 5		; buffer length
		mov	dx, 0FFFFh	; -1 ; get for default code page
		int	21h		; DOS -	4.x internal - COUNTRY-DEPENDENT FILENAME CAPITALIZATION
					; AL = function	-
		pop	dx
		pop	cx
		pop	ax

$P_DCC01:				; ...
		mov	bx, cs:[di+1]	; [cs:di+$P_DOS_TBL.$P_DOS_TBL_Off]
		mov	es, word ptr cs:[di+3] ; [cs:di+$P_DOS_TBL.$P_DOS_TBL_Seg]
		assume es:nothing
		inc	bx
		inc	bx
		sub	al, 80h
		xlat	byte ptr es:[bx] ; $P_ASCII80
		pop	di
		pop	es
		pop	bx

$P_CAPS_Ret:				; ...
		retn
$P_Do_CAPS_Char	endp


; ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ S U B	R O U T	I N E ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ


$P_SValue	proc near		; ...
		push	ax
		or	cs:$P_Flags2, 80h ; $P_Signed
		and	cs:$P_Flags2, 0FDh ; 0FFh-$P_Neg
		mov	al, cs:[si]
		cmp	al, 2Bh	; '+'   ; $P_Plus
		jz	short $P_SVal00
		cmp	al, 2Dh	; '-'   ; $P_Minus
		jnz	short $P_Sval01
		or	cs:$P_Flags2, 2	; $P_Neg

$P_SVal00:				; ...
		inc	si

$P_Sval01:				; ...
		call	$P_Value
		pop	ax
		retn
$P_SValue	endp


; ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ S U B	R O U T	I N E ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ


$P_Value	proc near		; ...
		push	ax
		push	cx
		push	dx
		push	si
		xor	cx, cx
		xor	dx, dx
		push	bx

$P_Value_Loop:				; ...
		mov	al, cs:[si]
		or	al, al
		jz	short $P_Value00
		call	$P_0099
		jb	short $P_Value_Err0
		xor	ah, ah
		mov	bp, ax
		shl	dx, 1
		rcl	cx, 1
		call	$P_Check_OVF
		jb	short $P_Value_Err0
		mov	bx, dx
		mov	ax, cx
		shl	dx, 1
		rcl	cx, 1
		call	$P_Check_OVF
		jb	short $P_Value_Err0
		shl	dx, 1
		rcl	cx, 1
		call	$P_Check_OVF
		jb	short $P_Value_Err0
		add	dx, bx
		adc	cx, ax
		call	$P_Check_OVF
		jb	short $P_Value_Err0
		add	dx, bp
		adc	cx, 0
		call	$P_Check_OVF
		jb	short $P_Value_Err0
		inc	si
		jmp	short $P_Value_Loop
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

$P_Value_Err0:				; ...
		pop	bx
		jmp	$P_Value_Err
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

$P_Value00:				; ...
		pop	bx
		test	cs:$P_Flags2, 2	; $P_Neg
		jz	short $P_Value01
		not	cx
		not	dx
		add	dx, 1
		adc	cx, 0

$P_Value01:				; ...
		mov	si, es:[bx+6]	; [es:bx+$P_CONTROL_BLK.$P_Value_List]
		mov	al, es:[si]
		cmp	al, 0		; $P_nval_None
					;
		jnz	short $P_Value02
		mov	al, 1
		mov	ah, 0FFh
		jmp	short $P_Value_Exit
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ
		nop

$P_Value02:				; ...
		inc	si
		mov	al, es:[si]
		cmp	al, 0
		jz	short $P_Value_Err ; $P_Value03
		inc	si

$P_Val02_Loop:				; ...
		test	cs:$P_Flags2, 80h ; $P_Signed
		jnz	short $P_Val02_Sign
		cmp	cx, es:[si+3]	; [es:si+$P_VAL_LIST.$P_Val_XH]
		jb	short $P_Val02_Next
		ja	short $P_Val_In
		cmp	dx, es:[si+1]	; [es:si+$P_VAL_LIST.$P_Val_XL]
		jb	short $P_Val02_Next

$P_Val_In:				; ...
		cmp	cx, es:[si+7]	; [es:si+$P_VAL_LIST.$P_Val_YH]
		ja	short $P_Val02_Next
		jb	short $P_Val_Found
		cmp	dx, es:[si+5]	; [es:si+$P_VAL_LIST.$P_Val_YL]
		ja	short $P_Val02_Next
		jmp	short $P_Val_Found
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

$P_Val02_Sign:				; ...
		cmp	cx, es:[si+3]
		jl	short $P_Val02_Next
		jg	short $P_SVal_In
		cmp	dx, es:[si+1]
		jl	short $P_Val02_Next

$P_SVal_In:				; ...
		cmp	cx, es:[si+7]
		jg	short $P_Val02_Next
		jl	short $P_Val_Found
		cmp	dx, es:[si+5]
		jg	short $P_Val02_Next
		jmp	short $P_Val_Found
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

$P_Val02_Next:				; ...
		add	si, 9		; $P_Len_Range
		dec	al
		jnz	short $P_Val02_Loop
		mov	cs:$P_RC, 6	; $P_Out_Of_Range
		mov	al, 1		; $P_Number
		mov	ah, 0FFh	; $P_No_Tag
		jmp	short $P_Value_Exit
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

$P_Val_Found:				; ...
		mov	al, 1
		mov	ah, es:[si]
		jmp	short $P_Value_Exit
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

$P_Value_Err:				; ...
		mov	cs:$P_RC, 9	; $P_Syntax
		mov	al, 3		; $P_String
		mov	ah, 0FFh	; $P_No_Tag

$P_Value_Exit:				; ...
		call	$P_Fill_Result
		pop	si
		pop	dx
		pop	cx
		pop	ax
		retn
$P_Value	endp


; ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ S U B	R O U T	I N E ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ


$P_Check_OVF	proc near		; ...
		pushf
		test	cs:$P_Flags2, 2	; $P_Neg
		jnz	short $P_COVF
		popf
		retn
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

$P_COVF:				; ...
		popf
		jo	short $P_COVF00
		clc
		retn
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

$P_COVF00:				; ...
		stc
		retn
$P_Check_OVF	endp


; ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ S U B	R O U T	I N E ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ


$P_0099		proc near		; ...
		cmp	al, 30h	; '0'
		jb	short $P_0099Err
		cmp	al, 39h	; '9'
		ja	short $P_0099Err
		sub	al, 30h	; '0'
		clc
		retn
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

$P_0099Err:				; ...
		stc
		retn
$P_0099		endp


; ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ S U B	R O U T	I N E ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ


$P_Simple_String proc near		; ...
		push	ax
		push	bx
		push	dx
		push	di
		mov	di, es:[bx+6]	; [es:bx+$P_CONTROL_BLK.$P_Value_List]
		mov	al, es:[di]
		or	al, al
		jnz	short $P_Sim00
		mov	ah, 0FFh
		jmp	short $P_Sim_Exit
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

$P_Sim00:				; ...
		cmp	al, 3		; $P_nval_String
		jnz	short $P_Sim01
		inc	di
		mov	al, es:[di]
		mov	ah, 9		; $P_Len_Range
		mul	ah
		inc	ax
		add	di, ax
		mov	al, es:[di]
		mov	ah, 5		; $P_Len_Value
		mul	ah
		inc	ax
		add	di, ax
		mov	al, es:[di]
		inc	di
		inc	di

$P_Sim_Loop:				; ...
		mov	bp, es:[di]
		call	$P_String_Comp
		jnb	short $P_Sim_Found
		add	di, 3		; $P_Len_String
		dec	al
		jnz	short $P_Sim_Loop
		mov	cs:$P_RC, 8	; $P_Not_In_Str
		mov	ah, 0FFh	; $P_No_Tag
		jmp	short $P_Sim_Exit
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

$P_Sim_Found:				; ...
		mov	ah, es:[di-1]
		mov	al, 2		; $P_List_Idx
		mov	dx, es:[di]
		jmp	short $P_Sim_Exit0
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

$P_Sim01:				; ...
		mov	cs:$P_RC, 9	; $P_Syntax
		mov	ah, 0FFh	; $P_No_Tag

$P_Sim_Exit:				; ...
		mov	al, 3		; $P_String

$P_Sim_Exit0:				; ...
		call	$P_Fill_Result
		pop	di
		pop	dx
		pop	bx
		pop	ax
		retn
$P_Simple_String endp


; ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ S U B	R O U T	I N E ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ


$P_String_Comp	proc near		; ...
		push	ax
		push	bp
		push	dx
		push	si
		mov	dl, 2		; $P_DOSTBL_Char

$P_SCOM_Loop:				; ...
		mov	al, cs:[si]	; 1st string
		call	$P_Chk_DBCS
		jb	short $P_SCOM00
		call	$P_Do_CAPS_Char
		test	cs:$P_Flags2, 8	; $P_Key_Cmp
		jz	short $P_SCOM04
		cmp	al, 3Dh	; '='   ; $P_Keyword
		jnz	short $P_SCOM03
		cmp	byte ptr es:[bp+1], 0
		jnz	short $P_SCOM_Differ
		jmp	short $P_SCOM05
; ÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄÄ

