; ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ S U B	R O U T	I N E ÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛÛ


$P_Chk_EOL	proc near		; ...
		push	bx
		push	cx
		cmp	al, 0Dh		; $P_CR
		jz	short $P_Chk_EOL_Exit
		cmp	al, 0		; $P_NULL
		jz	short $P_Chk_EOL_Exit
