		jb	short $P_Chk_EOL_Exit
		xor	bx, bx
		mov	bl, es:[di+3]	; [es:di+$P_PARMS_BLK.$P_Len_Extra_Delim]
		add	bx, 4		; $P_Len_PARMS
		cmp	byte ptr es:[bx+di], 0 ; $P_I_Use_Default
