startmsg_ok:				; ...
		pop	ds
		assume ds:nothing
		mov	cx, 5
		mov	si, offset RomVectors

next_int:				; ...
		lods	byte ptr cs:[si]
		cbw
		shl	ax, 1
		shl	ax, 1
		mov	di, ax
		xchg	si, di
		lodsw
		stosw
		lodsw
		stosw
		xchg	si, di
		loop	next_int
		pop	di
