print_init	proc near		; ...
		cbw
		mov	dx, ax		; AL = device number
		mov	ah, 1
		int	17h		; PRINTER - INITIALIZE
					; DX = printer port (0-3)
					; Return: AH = status
		retn
print_init	endp


