aux_init	proc near		; ...
		cbw
		mov	dx, ax
		mov	al, 0A3h	; RSINIT ; 2400,n,1,8 (msequ.inc)
		mov	ah, 0
		int	14h		; SERIAL I/O - INITIALIZE USART
					; AL = initializing parameters,	DX = port number (0-3)
					; Return: AH = RS-232 status code bits,	AL = modem status bits
		retn
aux_init	endp


